float map(vec2 p, inout int index) {
    vec2 mouse = MS.xy;
    float k = 1.0;
    if (MS.z > 0.0) {
        p -= mouse;
        k = dot(p,p);
        p /= k;
        p += mouse;
    }
    const float strong_factor = 4.;
#ifdef DrawVerticalSections
    vec3 q = vec3(p.x, TT*0.1, p.y + 1.);
#else
    vec3 q = vec3(p, mod(TT*0.2 - 0.01, SECTION_HEIGHT - 0.02) + 0.01);
#endif
    return k*DE(q, index) * strong_factor;

}

vec3 hsv2rgb(vec3 c) {
  const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 post_process(vec3 col, vec2 uv) {
  col = pow(clamp(col, 0., 1.), vec3(1.0/2.2)); 
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *= 0.5 + 0.5*pow(19.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.7);
  return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    config;
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = 2. * uv - 1.;
    p.x *= iResolution.x / iResolution.y;
    p *= ZOOM;
    float aa  = 2.0 / clamp(iResolution.y, 400., 800.);

    int index, cid;

    float d = map(p, index);
    cid = index;


    float b = -0.4;
    float t = 10.0;
    const float lh = 2.;
    const vec3 lp = vec3(2.5, 2.5, lh);

    vec3 ro = vec3(0, 0, t);
    vec3 pp = vec3(p, 0);

    vec3 rd = normalize(pp - ro);

    vec3 ld = normalize(lp - pp);

    float bt = -(t-b)/rd.z;

    vec3  bp   = ro + bt*rd;
    vec3  srd = normalize(lp - bp);
    float bl = L2(lp - bp);

    float st = (0.0-b)/srd.z;
    vec3  sp = bp + srd*st;

    float bd = map(bp.xy, index);
    float sd = map(sp.xy, index);

    vec3 col = vec3(0);
    const float ss = 15.0;
    col       += vec3(1.)  * (1.0 - exp(-ss*(max(sd, 0.0)))) / bl;
    float l   = length(p);
#ifdef DrawLimitSet
    float hue = fract(0.75*l-0.3*TT) + .45;
#else
    float hue = fract(0.15*l-0.3*TT) + .5;
#endif
    float sat = .9*tanh(4.*l);
    vec3 hsv  = vec3(hue, sat, 1.0);
    vec3 bcol = hsv2rgb(hsv);

    col       *= (1.0-clamp(tanh(0.75*l), 0., .1)) * 1.3;

#ifdef DrawLimitSet
    col       = mix(col, vec3(1), smoothstep(-aa, aa, -d));
#else
    col       = mix(col, bcol, 1. - step(-d, aa));
#endif
    col       += 0.5*sqrt(bcol.zxy)*(exp(-(10.0+100.0*tanh(l))*max(d, 0.0)));

    col = post_process(col, uv);
    fragColor = vec4(col,1.0);
}

#define MAX_ITER         30
#define PI               3.141592653
#define L2(x)            dot(x, x)
#define ZOOM             4.
#define inf              -1.

// comment out this to show the circles
#define DrawLimitSet


// uncomment this to show the vertice sections
//#define DrawVerticalSections

float dihedral(float x) {
    return x == inf ? 1. : cos(PI / x);
}

vec3 dihedral(vec3 v) {
    return vec3(dihedral(v.x), dihedral(v.y), dihedral(v.z));
}

float SECTION_HEIGHT;

// For spheres n is the center, r is the radius
// For planes n is the normal vector, r is the distance between the plane and the origin
// if invert is true then the inside/outside of the sphere is interchanged (not used in this program)
// if hasRealBall is true then this virtual ball has a real ball correspondes to it
struct Ball {
    bool isplane;
    vec3 n;
    float r;
    bool invert;
    bool isRealBall;
};

// coclusters are mirror balls, they corresponde to root vectors (space-like)
Ball[5] coclusters;
// clusters are real balls, they corresponde to space-like weight vectors
Ball[5] clusters;


// create a ball. by default it's the z=0 plane with normal points to the negative half
Ball defaultBall() {
    return Ball(false, vec3(0, 0, -1), 0., false, false);
}

// Distance from a point to a ball
float sdistanceToBall(vec3 p, Ball B) {
    if (B.isplane) {
        float k = dot(vec4(p, 1), vec4(B.n, B.r));
        return k;
    }
    else
        return length(p - B.n) - B.r;
}

Ball from_plane(vec3 n, float d) {
    return Ball(true, n, d, false, false);
}

Ball from_sphere(vec3 cen, float r) {
    return Ball(false, cen, r, false, false);
}

void invertBall(inout Ball B) {
    B.invert = !B.invert;
}

// try to reflect a point p to the positive half space bounded by a ball
// if we are already in the positive half space, do nothing and return true,
// else reflect about the ball and return false
// if B is a sphere we try to reflect p into the interior of B
bool try_reflect(inout vec3 p, Ball B, inout float scale) {
    if (B.isplane) {
        float k = dot(vec4(p, 1), vec4(B.n, B.r));
        if (k >= 0.)
            return true;
        p -= 2. * k  * B.n;
        return false;
    }
    else {
        vec3 cen = B.n;
        float r = B.r;
        vec3 q = p - cen;
        float d2 = dot(q, q);
        float k = (r * r) / d2;
        if ( (k < 1.0 && B.invert) || (k > 1. && !B.invert) )
            return true;
        p = k * q + cen;
        scale *= k;
        return false;
    }
}

// return distance to the scene, and get the index of the real ball hitted
float DE(vec3 p, inout int index) {
    float scale = 3.;
    for (int i = 0; i < MAX_ITER; i++) {
        bool cond = true;
        for (int k = 0; k < 5; k++) {
            cond = try_reflect(p, coclusters[k], scale) && cond;
        }

        if (cond)
            break;
    }
    index = 0;
#ifdef DrawLimitSet
    float d = abs(p.z);
    for (int j = 1; j < 5; j++) {
        if (clusters[j].isRealBall) {
            float d1 = abs(sdistanceToBall(p, clusters[j]));
            if (d1 < d) { d = d1; index = j; }
        }
    }
#else
    float d = p.z;
    for (int j = 1; j < 5; j++) {
        if (clusters[j].isRealBall) {
            float d1 = sdistanceToBall(p, clusters[j]);
            if (d1 < d) { d = d1; index = j; }
        }
    }

#endif
    return d / scale;
}

// solve M*v = b, where v = (x, y, r) is the xy-coordinate of the center of the ball,
// r is the radius of the ball.
Ball solveBall(mat3 M, vec3 b) {
    vec3 p = b * inverse(M);
    return from_sphere(vec3(p.xy, 0.), p.z);
}

// Solve a ball with its center has xy-coordinates P, and is orthogonal to other two Balls B0, B1.
// Here B0 may be either a sphere or a plane, B1 is always a sphere. 
Ball solveBall(vec2 P, Ball B0, Ball B1) {
    if (B0.isplane) {
        float z = B0.r;
        vec3 cen = vec3(P, z);
        float R = sqrt(L2(cen - B1.n) - B1.r*B1.r);
        return from_sphere(cen, R);
    }
    else {
        float r1 = B1.r;
        float r0 = B0.r;
        float z0 = B0.n.z;
        float k0 = L2(P - B0.n.xy);
        float k1 = L2(P - B1.n.xy);
        float z = (r1*r1 - r0*r0 + z0*z0 + k0 - k1) / (2.*z0);
        float R = sqrt(k1 + z*z - r1*r1);
        return from_sphere(vec3(P, z), R);
    }

}

// triangleType: must be one of 236, 244 or 333
// dihedralAngles0: labels of the edges between v0 and (v2, v3, v4)
// dihedralAngles1: labels of the edges between v1 and (v2, v3, v4)
// dihedralAngles0_1: label of the edge between (v0, v1)
// isRealBall: specify which of (v1, v2, v3, v4) has real balls
void init(int triangleType,
          vec3 dihedralAngles0,
          vec3 dihedralAngles1,
          float dihedralAngle0_1,
          bvec4 isRealBall) {
    const float s2 = sqrt(2.);
    const float s3 = sqrt(3.);
    mat3 M0, M1;
    vec3 b;
    Ball B0, B1, B2, B3, B4;
    vec3 t0 = dihedral(dihedralAngles0);
    vec3 t1 = dihedral(dihedralAngles1);
    float t01 = dihedral(dihedralAngle0_1);
    // A, B, C are the vertices of the triangle formed by mirror plane v2, v3, v4 and z=0 plane
    vec2 A, B, C;

    // the 236 case
    if (triangleType == 236) {
        A = vec2(0, 0), B = vec2(0, s3), C = vec2(1, 0);
        B2 = from_plane(vec3(1, 0, 0), 0.);
        B3 = from_plane(vec3(-s3/2., -0.5, 0), s3/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(1, 0, -t1.x), vec3(s3/2., 0.5, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(1, 0, -t0.x), vec3(s3/2., 0.5, t0.y), vec3(0, 1, -t0.z));
        b = vec3(0, s3/2., 0);
    }

    // the 244 case
    else if (triangleType == 244) {
        A = vec2(0, 0), B = vec2(0, 1), C = vec2(1, 0);
        B2 = from_plane(vec3(1, 0, 0), 0.);
        B3 = from_plane(vec3(-s2/2., -s2/2., 0), s2/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(1, 0, -t1.x), vec3(1./s2, 1./s2, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(1, 0, -t0.x), vec3(1./s2, 1./s2, t0.y), vec3(0, 1, -t0.z));
        b = vec3(0, s2/2., 0);
    }

    // the 333 case
    else {
        A = vec2(-1, 0), B = vec2(0, s3), C = vec2(1, 0);
        B2 = from_plane(vec3(s3/2., -.5, 0), s3/2.);
        B3 = from_plane(vec3(-s3/2., -.5, 0), s3/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(-s3/2., 0.5, t1.x), vec3(s3/2., .5, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(-s3/2., 0.5, t0.x), vec3(s3/2., .5, t0.y), vec3(0, 1, -t0.z));
        b = vec3(s3, s3, 0)/2.;
    }

    // now we solve the virtual ball B1, this can't be a plane
    B1 = solveBall(M1, b);
    invertBall(B1);

    // now we solve the virtual ball B0, this can be either a plane or a sphere
    // this depends on if all entries in dihedralAngles0 are all 2
    if (dot(dihedralAngles0, vec3(1)) == 6.) {
        B0 = from_plane(vec3(0, 0, -1), B1.r*t01);
    }
    else {
        B0 = solveBall(M0, b);
        float r1 = B1.r, r0 = B0.r;
        B0.n.z = sqrt(r0*r0 + r1*r1 + 2.*r0*r1*t01 - L2(B1.n.xy - B0.n.xy));
        invertBall(B0);
    }
    coclusters = Ball[5] (B0, B1, B2, B3, B4);

    SECTION_HEIGHT = B0.isplane ? 2.*B0.r : B0.n.z;

    //now we process the real balls
    for (int k = 0; k < 5; k++)
        clusters[k] = defaultBall();

    if (isRealBall.x) {
        clusters[1] = from_plane(vec3(0, 0, -1.), B0.n.z);
        clusters[1].isRealBall = true;
    }
    if (isRealBall.y) {
        clusters[2] = solveBall(C, B0, B1);
        clusters[2].isRealBall = true;
    }
    if (isRealBall.z) {
        clusters[3] = solveBall(A, B0, B1);
        clusters[3].isRealBall = true;
    }

    if (isRealBall.w) {
        clusters[4] = solveBall(B, B0, B1);
        clusters[4].isRealBall = true;
    }
}

/*
    A mimic of shane's work at https://www.shadertoy.com/view/WlBczG
    But using a more general approach.

    Show the basic procedure to draw a 2d hyperbolic Poincare tiling.

    Also can draw paracompact and noncompact tilings (with ideal and hyperideal
   vertices)

*/
// show the three reflection mirrors
//#define debug_mirrors

// comment out this to see the initial pattern
#define enableMouseInversion

// change to Klein model
//#define KleinModel

// set inf = 1.0 for paracompact tiling
// and inf > 1.0 for noncompact tiling
#define inf 1.0

#define PI 3.14159265
#define TAU 6.28318531
#define MAX_ITER 30

// the smaller this value, the larger the black area
#define BlackRegionSize 0.065

// the first entry in PQR must be finite, other two entries can be either finite
// or infinite for example (3, inf 4), (3, 4, inf), etc
const vec3 PQR = vec3(3, 3, 7);

// reflection mirrors, two lines and a circle
vec2 A, B; // normal vectors of the two lines
vec3 C;    // (center, radius)

// two vertices of the fundamental triangle, the 3rd one is the origin
vec2 v0, m0;

// count the total number of reflections
float count;

// compute cos(PI / x) using Vinberg's convention for inf.
float dihedral(float x) { return x == inf ? inf : cos(PI / x); }

mat2 rot2(in float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

void init() {

    float cAB = dihedral(PQR.x);
    float sAB = sqrt(1. - cAB * cAB);

    A = vec2(1, 0);
    B = vec2(-cAB, sAB);

    float cAC = dihedral(PQR.y);
    float cBC = dihedral(PQR.z);

    float k1 = cAC;
    float k2 = (cBC + cAB * cAC) / sAB;
    float r = 1. / sqrt(k1 * k1 + k2 * k2 - 1.);

    C = vec3(k1, k2, 1.) * r;

    if (r * r >= C.x * C.x)
        v0 = vec2(0., C.y - sqrt(r * r - C.x * C.x));
    else
        v0 = vec2(0, 1);

    vec2 n = vec2(-B.y, B.x);
    float b = dot(C.xy, n);
    float c = dot(C.xy, C.xy) - r * r;
    float k = -1.0;

    if (b * b >= c)
        k = b + sqrt(b * b - c);

    m0 = k * n;
}

bool try_reflect(inout vec2 p, vec2 mirror, inout float count) {
    float k = dot(p, mirror);
    if (k >= 0.)
        return true;
    p -= 2. * k * mirror;
    count += 1.;
    return false;
}

bool try_reflect(inout vec2 p, vec3 sphere, inout float count) {
    vec2 cen = sphere.xy;
    float r = sphere.z;
    float d = length(p - cen) - r;
    if (d >= 0.)
        return true;
    p -= cen;
    p *= r * r / dot(p, p);
    p += cen;
    count += 1.;
    return false;
}

void fold(inout vec2 p, inout float count) {
    count = 0.;
    for (int k = 0; k < MAX_ITER; k++) {
        try_reflect(p, A, count);
        try_reflect(p, B, count);
        try_reflect(p, C, count);
    }
}

float sBox(in vec2 p, in vec2 b) {
    vec2 d = abs(p) - b;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

float lBox(vec2 p, vec2 a, vec2 b, float ew) {
    float ang = atan(b.y - a.y, b.x - a.x);
    p = rot2(ang) * (p - mix(a, b, .5));

    vec2 l = vec2(length(b - a), ew);
    return sBox(p, (l + ew) / 2.);
}

vec2 mouseInversion(vec2 p) {
    vec2 m = vec2((2. * iMouse.xy - iResolution.xy) / iResolution.y);
    if (length(m) < 1e-3)
        m += 1e-3;
    if (abs(m.x) > .98 * .7071 || abs(m.y) > .98 * .7071)
        m *= .98;

    float k = 1. / dot(m, m);
    vec2 invCtr = k * m;
    float t = (k - 1.) / dot(p - invCtr, p - invCtr);
    p = t * p + (1. - t) * invCtr;
    p.x = -p.x;
    return p;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    uv *= 1.05;
    vec2 p = uv;

#ifdef enableMouseInversion
    p = mouseInversion(p);
    p = rot2(iTime / 16.) * p;
#endif

    init();

    if (length(p) > 1.)
        p /= dot(p, p);

#ifdef KleinModel
    p = p / (1. + sqrt(1. - dot(p, p)));
#endif

    fold(p, count);

    float lw = 0.002;

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    ln = min(ln, lBox(p, vec2(0), v0, lw));
    ln = min(ln, lBox(p, vec2(0), m0, lw));
    ln = min(ln, length(p - C.xy) - C.z - lw);

    ln2 = min(ln2, lBox(p, vec2(0), m0, lw));
    pnt = min(pnt, length(p - v0));
    pnt = min(pnt, length(p - m0));

    float ssf = (2. - smoothstep(0., .25, abs(length(uv) - 1.) - .25));
    float sf = 2. / iResolution.y * ssf;

    vec3 oCol = .55 + .45 * cos(count * TAU / 8. + vec3(0, 1, 2));
    float pat = smoothstep(0., .25, abs(fract(ln2 * 50. - .2) - .5) * 2. - .2);

    float sh = clamp(.65 + ln / v0.y * 4., 0., 1.);

    vec3 col = min(oCol * (pat * .2 + .9) * sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize)));

    pnt -= .032;
    pnt = min(pnt, length(p) - .032);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));
    col = mix(col, vec3(1, .8, .3), 1. - smoothstep(0., sf, pnt + .02));

    vec3 bg = vec3(1, .2, .4);
    bg *= .7 *
          (mix(col, vec3(1) * dot(col, vec3(.299, .587, .114)), .5) * .5 + .5);
    pat = smoothstep(0., .25,
                     abs(fract((uv.x - uv.y) * 43. - .25) - .5) * 2. - .5);
    bg *= max(1. - length(uv) * .5, 0.) * (pat * .2 + .9);

    float cir = length(uv);
    col = mix(col, vec3(0),
              (1. - smoothstep(0., sf * 10., abs(cir - 1.) - .05)) * .7);
    col =
        mix(col, vec3(0), (1. - smoothstep(0., sf * 2., abs(cir - 1.) - .05)));
    col =
        mix(col, vec3(.9) + bg, (1. - smoothstep(0., sf, abs(cir - 1.) - .03)));
    col = mix(col, col * max(1. - length(uv) * .5, 0.),
              (1. - smoothstep(0., sf, -cir + 1.05)));
    col = mix(col, bg, (1. - smoothstep(0., sf, -cir + 1.05)));

    col = mix(col, vec3(0),
              (1. - smoothstep(0., sf, abs(cir - 1.035) - .03)) * .8);
    col = mix(col, 1. - exp(-col), .35);

#ifdef debug_mirrors
    col = mix(col, vec3(1, 0, 0),
              1. - smoothstep(0., 0.01, abs(dot(uv, A)) - 0.01));
    col = mix(col, vec3(1, 0, 0),
              1. - smoothstep(0., 0.01, abs(dot(uv, B)) - 0.01));
    col = mix(col, vec3(1, 0, 0),
              1. - smoothstep(0., 0.02, abs(length(uv - C.xy) - C.z)) - 0.01);
#endif

    fragColor = vec4(sqrt(max(col, 0.)), 1.);
}

/*
=============================================

Limit set of rank 4 hyperbolic Coxeter groups

                                by Zhao Liang
=============================================

This program shows the limit sets of rank 4 hyperbolic Coxeter groups.

Some math stuff:

Let G be a hyperbolic Coxeter group, and let x be a point located
within the hyperbolic unit ball. The orbit S_x = { gx | g \in G }
has accumulation points (under the Euclidean metric) only on the
boundary of the space. These accumulation points are called the limit
set of the group. This set is independent of the choice of x, and it
is the smallest closed subset of the boundary that remains invariant
under the action of the group.

The Coxeter-Dynkin diagram of a rank 4 Coxeter group of string type
has the form

   A --- B --- C --- D
      p     q     r

Here A, B, D can be chosen as ususal Euclidean planes,
C is a sphere orthongonal to the unit ball. This is taken from
@mla's notation, and as far as I know this is also used by
users on fractalforums. (fragmentarium)

In this animation these points are colored in "brass metal".

==========
!important
==========

The limit set is a closed set with no interior points, to show them
we have to use an approximate procedure: we simply try to reflect a
point p on the boundary back to the fundamental domain up to a maximum
steps, once failed then we think p belongs to the limit set.

**So the number MAX_REFLECTIONS is an important parameter**,
if it's set too high then little limit set will be shown, or if it's
not high enough then the boundary of the set will look too coarse,
so beware of this.

As always, you can do whatever you want to this work.

Update: thanks @mla for helping fix some bugs!

You can try more patterns like
(3, 7, 3), (4, 6, 3), (4, 4, 5), (5, 4, 4), (7, 3, 4), ..., etc.
5, 4, 4) is my favorite!
Set PQR below to see the result.
For large PQRs the limit set will become too small to be visible,
you need to adjust MAX_REFLECTIONS and tweak with the function
chooseColor to get appealling results.
*/

#define inf -1.

// Try this one!
// const vec3 PQR = vec3(6, 3, inf);

// and this one!
// const vec3 PQR = vec3(4, 4, 5);

const vec3 PQR = vec3(3, 3, 7);

// --------------------------
// some global settings

#define MAX_TRACE_STEPS 100
#define MIN_TRACE_DIST 0.1
#define MAX_TRACE_DIST 100.0
#define PRECISION 0.0001
#define AA 2
#define MAX_REFLECTIONS 500
#define PI 3.141592653

// another pattern
//#define CHECKER1  vec3(0.196078, 0.33, 0.82)
//#define CHECKER2  vec3(0.75, 0.35, 0.196078)

/*
#define CHECKER1  vec3(0.82, 0.196078, 0.33)
#define CHECKER2  vec3(0.196078, 0.35, 0.92)
#define MATERIAL  vec3(0.71, 0.65, 0.26)
#define FUNDCOL   vec3(0., 0.82, .33)
*/

// Shane's color scheme
#define CHECKER1 vec3(0., 0., 0.05)
#define CHECKER2 vec3(0.2)
#define MATERIAL vec3(10, 0.3, 0.2)
#define FUNDCOL vec3(.3, 1, 8)

// used to highlight the limit set
#define LighteningFactor 8.
// --------------------------

vec3 A, B, D;
vec4 C;
float orb;

float dihedral(float x) { return x == inf ? 1. : cos(PI / x); }

// minimal distance to the four mirrors
float distABCD(vec3 p) {
    float dA = abs(dot(p, A));
    float dB = abs(dot(p, B));
    float dD = abs(dot(p, D));
    float dC = abs(length(p - C.xyz) - C.w);
    return min(dA, min(dB, min(dC, dD)));
}

// try to reflect across a plane with normal n and update the counter
bool try_reflect(inout vec3 p, vec3 n, inout int count) {
    float k = dot(p, n);
    // if we are already inside, do nothing and return true
    if (k >= 0.0)
        return true;

    p -= 2.0 * k * n;
    count += 1;
    return false;
}

// similar with above, instead this is a sphere inversion
bool try_reflect(inout vec3 p, vec4 sphere, inout int count) {
    vec3 cen = sphere.xyz;
    float r = sphere.w;
    vec3 q = p - cen;
    float d2 = dot(q, q);
    if (d2 == 0.0)
        return true;
    float k = (r * r) / d2;
    if (k < 1.0)
        return true;
    p = k * q + cen;
    count += 1;
    orb *= k;
    return false;
}

// sdf of the unit sphere at origin
float sdSphere(vec3 p, float radius) { return length(p) - 1.0; }

// sdf of the plane y=-1
float sdPlane(vec3 p, float offset) { return p.y + 1.0; }

// inverse stereo-graphic projection, from a point on plane y=-1 to
// the unit ball centered at the origin
vec3 planeToSphere(vec2 p) {
    float pp = dot(p, p);
    return vec3(2.0 * p, pp - 1.0).xzy / (1.0 + pp);
}

// iteratively reflect a point on the unit sphere into the fundamental cell
// and update the counter along the way
bool iterateSpherePoint(inout vec3 p, inout int count) {
    bool inA, inB, inC, inD;
    for (int iter = 0; iter < MAX_REFLECTIONS; iter++) {
        inA = try_reflect(p, A, count);
        inB = try_reflect(p, B, count);
        inC = try_reflect(p, C, count);
        inD = try_reflect(p, D, count);
        p = normalize(p); // avoid floating error accumulation
        if (inA && inB && inC && inD)
            return true;
    }
    return false;
}

// colors for fundamental domain, checker pattern and limit set.
vec3 chooseColor(bool found, int count) {
    vec3 col;
    if (found) {
        if (count == 0)
            return FUNDCOL;
        else if (count >= 300)
            col = MATERIAL;
        else
            col = (count % 2 == 0) ? CHECKER1 : CHECKER2;

    } else
        col = MATERIAL;

    float t = float(count) / float(MAX_REFLECTIONS);
    col = mix(MATERIAL * LighteningFactor, col,
              1. - t * smoothstep(0., 1., log(orb) / 32.));
    return col;
}

// 2d rotation
vec2 rot2d(vec2 p, float a) { return p * cos(a) + vec2(-p.y, p.x) * sin(a); }

vec2 map(vec3 p) {
    float d1 = sdSphere(p, 1.0);
    float d2 = sdPlane(p, -1.0);
    float id = (d1 < d2) ? 0. : 1.;
    return vec2(min(d1, d2), id);
}

// standard scene normal
vec3 getNormal(vec3 p) {
    const vec2 e = vec2(0.001, 0.);
    return normalize(vec3(map(p + e.xyy).x - map(p - e.xyy).x,
                          map(p + e.yxy).x - map(p - e.yxy).x,
                          map(p + e.yyx).x - map(p - e.yyx).x));
}

// get the signed distance to an object and object id
vec2 raymarch(in vec3 ro, in vec3 rd) {
    float t = MIN_TRACE_DIST;
    vec2 h;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        h = map(ro + t * rd);
        if (h.x < PRECISION * t)
            return vec2(t, h.y);

        if (t > MAX_TRACE_DIST)
            break;

        t += h.x;
    }
    return vec2(-1.0);
}

float calcOcclusion(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = map(p + h * n).x;
        occ += (h - d) * sca;
        sca *= 0.75;
    }
    return clamp(1.0 - occ, 0.0, 1.0);
}

float softShadow(vec3 ro, vec3 rd, float tmin, float tmax, float k) {
    float res = 1.0;
    float t = tmin;
    for (int i = 0; i < 12; i++) {
        float h = map(ro + rd * t).x;
        res = min(res, k * h / t);
        t += clamp(h, 0.01, 0.2);
        if (h < 0.0001 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}

vec3 getColor(vec3 ro, vec3 rd, vec3 pos, vec3 nor, vec3 lp, vec3 basecol) {
    vec3 col = vec3(0.0);
    vec3 ld = lp - pos;
    float lDist = max(length(ld), .001);
    ld /= lDist;
    float ao = calcOcclusion(pos, nor);
    float sh = softShadow(pos + 0.001 * nor, ld, 0.02, lDist, 32.);
    float diff = clamp(dot(nor, ld), 0., 1.);
    float atten = 2. / (1. + lDist * lDist * .01);

    float spec = pow(max(dot(reflect(-ld, nor), -rd), 0.0), 32.);
    float fres = clamp(1.0 + dot(rd, nor), 0.0, 1.0);

    col += basecol * diff;
    col += basecol * vec3(1., 0.8, 0.3) * spec * 20.;
    col += basecol * vec3(0.8) * pow(fres, 5.0) * 2.;
    col *= ao * atten * sh;
    col += basecol * clamp(0.8 + 0.2 * nor.y, 0., 1.) * 0.5;
    return col;
}

mat3 sphMat(float theta, float phi) {
    float cx = cos(theta);
    float cy = cos(phi);
    float sx = sin(theta);
    float sy = sin(phi);
    return mat3(cy, -sy * -sx, -sy * cx, 0, cx, sx, sy, cy * -sx, cy * cx);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 finalcol = vec3(0.);
    int count = 0;
    vec2 m = vec2(0.0, 1.0) + iMouse.xy / iResolution.xy;
    float rx = m.y * PI;
    float ry = -m.x * 2. * PI;
    mat3 mouRot = sphMat(rx, ry);

    // ---------------------------------
    // initialize the mirrors

    float P = PQR.x, Q = PQR.y, R = PQR.z;
    float cp = dihedral(P), sp = sqrt(1. - cp * cp);
    float cq = dihedral(Q);
    float cr = dihedral(R);
    A = vec3(0, 0, 1);
    B = vec3(0, sp, -cp);
    D = vec3(1, 0, 0);

    float r = 1.0 / cr;
    float k = r * cq / sp;
    vec3 cen = vec3(1, k, 0);
    C = vec4(cen, r) / sqrt(dot(cen, cen) - r * r);

    // -------------------------------------
    // view setttings

    vec3 camera = vec3(3., 3.2, -3.);
    vec3 lp = vec3(0.5, 3.0, -0.8); // light position
    camera.xz = rot2d(camera.xz, iTime * 0.3);
    vec3 lookat = vec3(0., -0.5, 0.);
    vec3 up = vec3(0., 1., 0.);
    vec3 forward = normalize(lookat - camera);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));

    // -------------------------------------
    // antialiasing loop

    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * fragCoord + o - iResolution.xy) / iResolution.y;
            vec3 rd = normalize(uv.x * right + uv.y * up + 3.0 * forward);
            orb = 1.0;
            // ---------------------------------
            // hit the scene and get distance, object id

            vec2 res = raymarch(camera, rd);
            float t = res.x;
            float id = res.y;
            vec3 pos = camera + t * rd;

            bool found;
            float edist;
            vec3 col;
            // the sphere is hit
            if (id == 0.) {
                vec3 nor = pos;
                vec3 q = pos * mouRot;
                found = iterateSpherePoint(q, count);
                edist = distABCD(q);
                vec3 basecol = chooseColor(found, count);

                col = getColor(camera, rd, pos, nor, lp, basecol);
            }
            // the plane is hit
            else if (id == 1.) {
                vec3 nor = vec3(0., 1., 0.);
                vec3 q = planeToSphere(pos.xz);
                q = q * mouRot;
                found = iterateSpherePoint(q, count);
                edist = distABCD(q);
                vec3 basecol = chooseColor(found, count);
                col = getColor(camera, rd, pos, nor, lp, basecol) * .9;
            }
            // draw the arcs
            col =
                mix(col, vec3(0.), (1.0 - smoothstep(0., 0.005, edist)) * 0.85);
            col = mix(col, vec3(0.), 1.0 - exp(-0.01 * t * t));
            finalcol += col;
        }
    }
    finalcol /= (float(AA) * float(AA));

    // ------------------------------------
    // a little post-processing

    finalcol = mix(finalcol, 1. - exp(-finalcol), .35);
    fragColor = vec4(sqrt(max(finalcol, 0.0)), 1.0);
}

/*


	Motion Tweening
	---------------

	Creating a looping animation with magnetized blocks utilizing basic motion 
    tweening techniques. The animation sequence itself is a rough recreation 
    of a sequence I've seen around in GIF form on the internet. I'm not sure 
    where the original came from, but if I were to take a guess, I'd say it was 
    created by Andreas Wannerstedt, who produces a lot of mesmerizing looping 
    animations... but I wouldn't quote me on it.

    I've been away for a while, so wanted to start with something relatively
	simple. Motion tweening requires a bit of effort, but is relatively easy
	repetitive work. Demosceners do stuff like this all the time, but for those 
	not familar with the process, you just choose a total looping time 
    (tm = mod(iTime, totalTime)), then partition it into individual time segments 
    using a case statement, or some if-elseif statements. The segment intervals 
    themselves are filled with interpolated motion, distortion, morphing, etc.

    As you can see, none of the individual movements are particularly complex;
    rotations, pivots, translations, etc -- A lot of it was made up on the fly,
	so I'd imagine there'd be more efficient ways to achieve the same. One thing
	to note is that the colored cube moves in conjunction with the larger one, 
    which might throw some people off, but that's just a simple case of moving 
    the chrome looking box, setting the cube coordinates to the chrome box 
	coordinate system (p = pPrevious), then peforming more simple operations.

    I seem to say this a lot, but apologies in advance for the extended compile
	time. This should run pretty quickly, but the lengthy decision-making logic
	inside the raymarching loop taxes the compiler, which is amplified with the 
	reflection pass. By the way, you could simplify the objects and use IQ's 
    raytraced rounded-box intersection formulas to make this way, way more
	efficient. However, keeping track of the rotations for normal calculations, 
    and so forth, would get pretty tiresome... It's the kind of thing I'll leave 
    for Dr2 to do. :)



    Other examples:


    // This is one of the most clever and innovative examples on here.
    [SH18] Human Document - reinder
    https://www.shadertoy.com/view/XtcyW4

*/

#define FAR 30.

vec4 vObjID;
int objID;


// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: http://http.developer.nvidia.com/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){    

    // Ryan Geiss effectively multiplies the first line by 7. It took me a while to realize that 
    // it's largely redundant, due to the division process that follows. I'd never noticed on 
    // account of the fact that I'm not in the habit of questioning stuff written by Ryan Geiss. :)
    n = max(n*n - .2, .001); // max(abs(n), 0.001), etc.
    n /= dot(n, vec3(1)); 
    //n /= length(n); 

    // Texure samples. One for each plane.
    vec3 tx = texture(tex, p.yz).xyz;
    vec3 ty = texture(tex, p.zx).xyz;
    vec3 tz = texture(tex, p.xy).xyz;

    // Multiply each texture plane by its normal dominance factor.... or however you wish
    // to describe it. For instance, if the normal faces up or down, the "ty" texture sample,
    // represnting the XZ plane, will be used, which makes sense.

    // Textures are stored in sRGB (I think), so you have to convert them to linear space 
    // (squaring is a rough approximation) prior to working with them... or something like that. :)
    // Once the final color value is gamma corrected, you should see correct looking colors.
    return mat3(tx*tx, ty*ty, tz*tz)*n; // Equivalent to: tx*tx*n.x + ty*ty*n.y + tz*tz*n.z;

}

// Time variables.
float tm, t, t2;

// Animation segment ID.
int segID = 0;




// Distance function: This one is pretty simple.
float map(vec3 p){


    // Floor.
    float fl = p.y;  //-sBoxS(p - vec3(0, 3, 0), vec3(6, 3, 6),.04);//min(p.y, -p.y + 3.8);




    const vec3 bDim = vec3(.25, .5, .25);
    const vec3 bDim2 = vec3(.25, .25, .25);
    vec3 svDim = bDim;

    vec3 q = p, q2 = p;


    // The rendering portion is the easy bit; Just some standard distance
    // field operations with IQ's box formula.


    // The chrome box.
    float obj = sdSponge(q);

    // The colored cube.
    float obj2 = sierpinski(q); 



    // Store the floor, chrome box and gold cube positions for sorting
    // and surface identification outside the loop.
    vObjID = vec4(fl, obj, obj2, 0);


    // Return the minimum object.
    return min(min(fl, obj), obj2);
}

// Standard raymarching routine.
float trace(vec3 ro, vec3 rd){

    float t = 0., d;

    for (int i = min(0, iFrame); i<80; i++){

        d = map(ro + rd*t);

        // Using the hacky "abs," trick, for more accuracy. 
        if(abs(d)<.001 || t>FAR) break;        

        t += d;  // Using more accuracy, in the first pass.
    }

    return t;
}

// Second pass, which is the first, and only, reflected bounce. 
// Virtually the same as above, but with fewer iterations and less 
// accuracy.
//
// The reason for a second, virtually identical equation is that 
// raymarching is usually a pretty expensive exercise, so since the 
// reflected ray doesn't require as much detail, you can relax things 
// a bit - in the hope of speeding things up a little.
float traceRef(vec3 ro, vec3 rd){

    float t = 0., d;

    for (int i = min(0, iFrame); i<48; i++){

        d = map(ro + rd*t);

        if(abs(d)<.001 || t>FAR) break;

        t += d;
    }

    return t;
}


// Cheap shadows are hard. In fact, I'd almost say, shadowing repeat objects - in a setting like this - with limited 
// iterations is impossible... However, I'd be very grateful if someone could prove me wrong. :)
float softShadow(vec3 ro, vec3 lp, float k){

    // More would be nicer. More is always nicer, but not really affordable... Not on my slow test machine, anyway.
    const int maxIterationsShad = 24; 

    vec3 rd = lp - ro; // Unnormalized direction ray.

    float shade = 1.;
    float dist = .002;    
    float end = max(length(rd), .001);
    float stepDist = end/float(maxIterationsShad);

    rd /= end;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, the lowest 
    // number to give a decent shadow is the best one to choose. 
    for (int i = min(0, iFrame); i<maxIterationsShad; i++){

        float h = map(ro + rd*dist);
        //shade = min(shade, k*h/dist);
        shade = min(shade, smoothstep(0., 1., k*h/dist)); // Subtle difference. Thanks to IQ for this tidbit.
        // So many options here, and none are perfect: dist += min(h, .2), dist += clamp(h, .01, .2), 
        // clamp(h, .02, stepDist*2.), etc.
        dist += clamp(h, .01, .25);

        // Early exits from accumulative distance function calls tend to be a good thing.
        if (h<0. || dist>end) break; 
        //if (h<.001 || dist > end) break; // If you're prepared to put up with more artifacts.
    }

    // I've added 0.5 to the final shade value, which lightens the shadow a bit. It's a preference thing. 
    // Really dark shadows look too brutal to me.
    return max(shade, 0.); 
}


// I keep a collection of occlusion routines... OK, that sounded really nerdy. :)
// Anyway, I like this one. I'm assuming it's based on IQ's original.
float calcAO(in vec3 p, in vec3 n){

	float sca = 1.5, occ = 0.;
    for( int i = 0; i<5; i++ ){

        float hr = float(i + 1)*.25/5.;        
        float d = map(p + n*hr);
        occ += (hr - d)*sca;
        sca *= .7;
    }

    return clamp(1. - occ, 0., 1.);  

}


// Standard normal function. It's not as fast as the tetrahedral calculation, but more symmetrical.
vec3 getNormal(in vec3 p) {
	const vec2 e = vec2(.001, 0);
	return normalize(vec3(map(p + e.xyy) - map(p - e.xyy), map(p + e.yxy) - map(p - e.yxy),	map(p + e.yyx) - map(p - e.yyx)));
}


// The floor, chrome cube, and colored cube materials. These ones are pretty
// basic, but you could put more effort in.
//
vec3 getObjectColor(vec3 p, vec3 r, vec3 n){


    vec3 col;

    if(objID == 0) {

        // The floor.
        vec3 tx = texture(iChannel0, p.xz/4.).xyz; tx *= tx;
        col = tx*vec3(1, .7, .5)*.5;

    }
    else if(objID == 2) {

        // The chrome colored box. It's something I tend to get, but tinging
        // things blue can help bring about a metallic color... kind of. :)
        col = vec3(.65, .85, 1);
    }
    else {

        // The cube. 
        col = vec3(2, 2, .45); // Redish gold.
        //col = vec3(.7, 1.2, .3); // Green.
        //col = vec3(2, .35, .85); // Pink.
        //col = vec3(.3, 1, 2.5); // Blue.
        //col = vec3(.3); // Grey
    }


    // Adding some fake cube mapping information. The science is terrible, but it 
    // adds a bit of shine. :)
    vec3 cTx = tex3D(iChannel0, reflect(r, n)/1.5, n);
    cTx *= vec3(1, .8, .6);

    // Add a dose of fake reflection to the box and cube, and just a bit to the floor.
    if(objID>0) col *= cTx*2.;
    else col += cTx*.1;

    return col;

}

// Using the hit point, unit direction ray, etc, to color the 
// scene. Diffuse, specular, falloff, etc. It's all pretty 
// standard stuff.
vec3 doColor(in vec3 sp, in vec3 rd, in vec3 sn, in vec3 lp, float t){


    // Initiate the scene color to zero.
    vec3 sceneCol = vec3(0);

    if(t<FAR){

        vec3 ld = lp - sp; // Light direction vector.
        float lDist = max(length(ld), .0001); // Light to surface distance.
        ld /= lDist; // Normalizing the light vector.

        float ao = calcAO(sp, sn);

        // Attenuating the light, based on distance.
        float atten = 1./(1. + lDist*.2 + lDist*lDist*.05);

        // Standard diffuse term.
        float diff = max(dot(sn, ld), 0.);
        // Standard specualr term.
        float spec = pow(max( dot( reflect(-ld, sn), -rd ), 0.), 8.);

        // Ramp up the diffuse value. Sometimes, it can help things look metallic.
        diff = pow(diff, 4.)*2.;

        // Coloring the object. You could set it to a single color, to
        // make things simpler, if you wanted.
        vec3 objCol = getObjectColor(sp, rd, sn);


        // Combining the above terms to produce the final scene color.
        sceneCol = objCol*((diff + ao*.2) + vec3(1, .97, .92)*spec*4.);

        // Apply the attenuation and ambient occlusion.
        sceneCol *= atten*ao;

    }


    // Fog factor -- based on the distance from the camera.
    float fogF = smoothstep(0., .9, t/FAR);
    //
    // Applying the background fog. Just black, in this case, but you could
    // render sky, etc, as well.
    sceneCol = mix(sceneCol, vec3(0), fogF); 


    // Return the color. Performed once every pass... of which there are
    // only two, in this particular instance.
    return sceneCol;

}


vec3 getRd(vec2 u, vec3 ro){

    // Camera Setup.     
    vec3 lk = vec3(0, .5, 0);  // "Look At" position.


    // Using the above to produce the unit ray-direction vector.
    float FOV = 3.14159265/3.; // FOV - Field of view.
    vec3 fw = normalize(lk - ro);
    vec3 rt = normalize(vec3(fw.z, 0, -fw.x )); 
    vec3 up = cross(fw, rt);

    // rd - Ray direction.
    vec3 rd = normalize(fw + (u.x*rt + u.y*up)*FOV);
    // Warping the ray to give that curved lens effect.
    //rd = normalize(vec3(rd.xy, rd.z*(1. - length(rd.xy)*.25)));

    return rd;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    // Aspect correct screen coordinates.
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;


    // Slight barrel distortion.
    float r = dot(uv, uv);
    uv *= 1. + .2*(r*r + r);

    float cTm = iTime/2.;
    vec3 ro = vec3(sin(cTm)*2.65, cos(tm)*sin(cTm)*.25 + 2.25, cos(cTm)*2.65);//vec3(0, 0, 0);
    //vec3 ro = vec3(sin(cTm)*.75, cos(tm)*sin(cTm)*.25 + 2.25, -2.65);//vec3(0, 0, 0);


    vec3 rd = getRd(uv, ro);

    // Ray origin. Doubling as the surface position, in this particular example.
    // I hope that doesn't confuse anyone.

    vec3 lp = vec3(1, 3, -1); // Light position. Set in the vicinity the ray origin.


    // FIRST PASS.

    // Raymarch.
    float t = trace(ro, rd);

    // Obtain the current object ID.
    objID = vObjID.x < vObjID.y && vObjID.x < vObjID.z? 0 : vObjID.y < vObjID.z? 1 : 2;

    // Advancing the ray origin, "ro," to the new hit point.
    ro += rd*t;

    // Retrieving the normal at the hit point.
    vec3 sn = getNormal(ro);

    // Retrieving the color at the hit point, which is now "ro." I agree, reusing 
    // the ray origin to describe the surface hit point is kind of confusing. The reason 
    // we do it is because the reflective ray will begin from the hit point in the 
    // direction of the reflected ray. Thus the new ray origin will be the hit point. 
    // See "traceRef" below.
    vec3 sceneColor = doColor(ro, rd, sn, lp, t);

    // Checking to see if the surface is in shadow. Ideally, you'd also check to
    // see if the reflected surface is in shadow. However, shadows are expensive, so
    // it's only performed on the first pass. If you pause and check the reflections,
    // you'll see that they're not shadowed. OMG! Better call the shadow police. :)
    float sh = softShadow(ro +  sn*.0015, lp, 12.);
    sh = min(sh + .3, 1.);


    // SECOND PASS - REFLECTED RAY

    // Standard reflected ray, which is just a reflection of the unit
    // direction ray off of the intersected surface. You use the normal
    // at the surface point to do that. Hopefully, it's common sense.
    rd = reflect(rd, sn);


    // The reflected pass begins where the first ray ended, which is the suface
    // hit point, or in a few cases, beyond the far plane. By the way, for the sake
    // of simplicity, we'll perform a reflective pass for non hit points too. Kind
    // of wasteful, but not really noticeable. The direction of the new ray will
    // obviously be in the direction of the reflected ray. See just above.
    //
    // To anyone who's new to this, don't forgot to nudge the ray off of the 
    // initial surface point. Otherwise, you'll intersect with the surface
    // you've just hit. After years of doing this, I still forget on occasion.
    t = traceRef(ro +  sn*.003, rd);


    // Obtain the current object ID.
    objID = vObjID.x < vObjID.y && vObjID.x < vObjID.z? 0 : vObjID.y < vObjID.z? 1 : 2;

    // Advancing the ray origin, "ro," to the new reflected hit point.
    ro += rd*t;

    // Retrieving the normal at the reflected hit point.
    sn = getNormal(ro);

    // Coloring the reflected hit point, then adding a portion of it to the final scene color.
    // How much you add, and how you apply it is up to you, but I'm simply adding 35 percent.
    //sceneColor += doColor(ro, rd, sn, lp, t)*.5;
    // Other combinations... depending what you're trying to achieve.
    vec3 rCol = doColor(ro, rd, sn, lp, t);
    sceneColor = sceneColor + rCol*.75;


    // APPLYING SHADOWS
    //
    // Multiply the shadow from the first pass by the final scene color. Ideally, you'd check to
    // see if the reflected point was in shadow, and incorporate that too, but we're cheating to
    // save cycles and skipping it. It's not really noticeable anyway. By the way, ambient
    // occlusion would make it a little nicer, but we're saving cycles and keeping things simple.
    sceneColor *= sh;


    // Extra coloring.
    //sceneColor *= vec3(1.1, 1, .9);

    // Clamping the scene color, performing some rough gamma correction (the "sqrt" bit), then 
    // presenting it to the screen.
	fragColor = vec4(sqrt(clamp(sceneColor, 0., 1.)), 1);
}

// Easing functions are their own topic, but a lot of it is straight forward.
// Usage is easy. Normalize the time between zero and one, then choose the 
// one you're after.

const float PI = 3.14159265358979;

// Robert Penner's easing functions in GLSL.
// Available as a module for glslify. http://stack.gl/glsl-easings/

float easeInOutCubic(float t){

    return t<0.5 ? 4.*t*t*t : (t - 1.)*(2.*t - 2.)*(2.*t - 2.) + 1.;
}

float easeInOutQuint(float t){

    return t<.5 ? 16.*t*t*t*t*t : 1. +16.*(--t)*t*t*t*t;
}

float easeOutQuad(float t) {
    return -1. * t * (t - 2.);
}

float easeInQuad(float t) {
    return t * t;
}


float bounceOut(float t) {

  const float a = 4.0 / 11.0;
  const float b = 8.0 / 11.0;
  const float c = 9.0 / 10.0;

  const float ca = 4356.0 / 361.0;
  const float cb = 35442.0 / 1805.0;
  const float cc = 16061.0 / 1805.0;

  float t2 = t * t;

  return t < a
    ? 7.5625 * t2
    : t < b
      ? 9.075 * t2 - 9.9 * t + 3.4
      : t < c
        ? ca * t2 - cb * t + cc
        : 10.8 * t * t - 20.52 * t + 10.72;
}

float bounceInOut(float t) {
  return t < 0.5
    ? 0.5 * (1.0 - bounceOut(1.0 - t * 2.0))
    : 0.5 * bounceOut(t * 2.0 - 1.0) + 0.5;
}

float bounceIn(float t) {
  return 1.0 - bounceOut(1.0 - t);
}


float elasticOut(float t) {
  return sin(-13.0 * (t + 1.0) * PI/2.) * pow(2.0, -10.0 * t) + 1.0;
}

float circularInOut(float t) {
  return t < 0.5
    ? 0.5 * (1.0 - sqrt(1.0 - 4.0 * t * t))
    : 0.5 * (sqrt((3.0 - 2.0 * t) * (2.0 * t - 1.0)) + 1.0);
}

float exponentialOut(float t) {
  return t == 1.0 ? t : 1.0 - pow(2.0, -10.0 * t);
}

float exponentialIn(float t) {
  return t == 0.0 ? t : pow(2.0, 10.0 * (t - 1.0));
}

float exponentialInOut(float t) {
  return t == 0.0 || t == 1.0
    ? t
    : t < 0.5
      ? +0.5 * pow(2.0, (20.0 * t) - 10.0)
      : -0.5 * pow(2.0, 10.0 - (t * 20.0)) + 1.0;
}


// IQ's unsigned box formula.
float sBox(in vec3 p, in vec3 b, in float sf){

  return length(max(abs(p) - b + sf, 0.)) - sf;
}

// IQ's unsigned rectangle formula.
float sBox(in vec2 p, in vec2 b, in float sf){

  return length(max(abs(p) - b + sf, 0.)) - sf;
}




float sdSponge(vec3 z) {
    const float s = 1.8;
    z *= s;
    z.y -= 1.;
    const int SPONGE_ITERATIONS = 5;
    for(int i = 0; i < SPONGE_ITERATIONS; i++) {
        z = abs(z);
        z.xy = (z.x < z.y) ? z.yx : z.xy;
        z.xz = (z.x < z.z) ? z.zx : z.xz;
        z.zy = (z.y < z.z) ? z.yz : z.zy;
        z = z * 3.0  - 2.0 ;
        z.z += (z.z < -1.0) ? 2.0 : 0.0;
    }
    //distance to cube
    z = abs(z) - vec3(1);
    float dis = min(max(z.x, max(z.y, z.z)), 0.0) + length(max(z, 0.0));
    //scale cube size to iterations
    dis =  dis * pow(3.0, -float(SPONGE_ITERATIONS)) ;
    return dis / s;

}



float sierpinski( vec3 z )
{
    z *= 1.5;
    z -= vec3(2, 1, 2);
   const float Phi = 1.61803398875;
const vec3 n1   = normalize(vec3(-Phi   , Phi-1.0, 1.0    ));
const vec3 n2   = normalize(vec3(1.0    , -Phi   , Phi+1.0));
const vec3 o    = normalize(vec3(-Phi, -1.   , 0      ));

    float s = 1.;
    float orbit = 1e20;

    z = abs(z);      
    z -= 2.*max(0.,dot(z,n2))*n2;
    z.z = abs(z.z); 
    z -= 2.*max(0.,dot(z,n2))*n2;
    const int ITERATIONS = 10;
    const float SCALE = 2.;
    for(int n=0;n < ITERATIONS;n++) {
		orbit = min(orbit,dot(z,z));

        z = abs(z);             
        z -= 2.*max(0.,dot(z,n1))*n1; 

		z *= SCALE;
        s *= SCALE;

        z += o;
    }

    //dis & descale

    return (length(z)-1.) / s / 1.5; 


}

/*
    A mimic of shane's work at https://www.shadertoy.com/view/WlBczG.

    Show the basic procedure to draw a 2d hyperbolic Poincare tiling.

*/

// comment out this to see the initial pattern
//#define enableMouseInversion



#define PI		  3.14159265
#define TAU	      6.28318531
#define inf       -1.

int MAX_ITER = 0;


// the smaller this value, the larger the black area
#define BlackRegionSize   0.065

// the first entry in PQR must be finite, other two entries can be either finite or infinite
const vec3 PQR = vec3(3, 3, 7);


// reflection mirrors
vec2 A, B;
vec3 C;

// two vertices of the fundamental triangle, the 3rd one is the origin
vec2 v0, m0;

// count the number of reflections across each mirror
float count;


// compute cos(PI / x), for x = infiniy this is cos(0) = 1.0
float dihedral(float x) { return x == inf ? 1. : cos(PI / x); }

mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


void init() {

    float cAB = dihedral(PQR.x);
    float sAB = sqrt(1. - cAB*cAB);

	A = vec2(1, 0);
    B = vec2(-cAB, sAB);

    float cAC = dihedral(PQR.y);
    float cBC = dihedral(PQR.z);

    float k1 = cAC;
    float k2 = (cBC + cAB*cAC) / sAB;
    float r = 1. / sqrt(k1*k1 + k2*k2 - 1.);

    C = vec3(k1, k2, 1.)*r;

    v0 = vec2(0., C.y - sqrt(r*r - C.x*C.x));
    vec2 n = vec2(-B.y, B.x);
    float b = dot(C.xy, n);
    float c = dot(C.xy, C.xy) - r*r;
    float k = b + sqrt(b*b-c);
    m0 = k*n;
}


bool try_reflect(inout vec2 p, vec2 mirror, inout float count) {
    float k = dot(p, mirror);
    if (k >= 0.)
        return true;
    p -= 2. * k  * mirror;
    count += 1.;
    return false;
}


bool try_reflect(inout vec2 p, vec3 sphere, inout float count) {
    vec2 cen = sphere.xy;
    float r = sphere.z;
    float d = length(p - cen) - r;
    if (d >= 0.)
        return true;
    p -= cen;
    p *= r * r/ dot(p, p);
    p += cen;
    count += 1.;
    return false;
}


bool fold(inout vec2 p, inout float count) {
    count = 0.;
    for (int k = 0; k < MAX_ITER; k++) {
        bool cond = true;
        cond = cond && try_reflect(p, A, count);
        cond = cond && try_reflect(p, B, count);
        cond = cond && try_reflect(p, C, count);
        if (cond)
            return true;
    }
    return false;
}

float sBox(in vec2 p, in vec2 b) {
    vec2 d = abs(p) - b;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}


float lBox(vec2 p, vec2 a, vec2 b, float ew) {
    float ang = atan(b.y - a.y, b.x - a.x);
    p = rot2(ang)*(p - mix(a, b, .5));

    vec2 l = vec2(length(b - a), ew);
    return sBox(p, (l + ew)/2.) ;
}

float darc(vec2 p, float k) {


    vec2 q = p;
    float tmp = 0.;
    bool isout = try_reflect(q, C, tmp);
    float dp = length(p - C.xy) - C.z;
    float d = length(q - C.xy) - C.z;
    float d1 = length(q - v0);
    float d2 = length(q - m0);
     if (dot(p, A) < 0.)
        return d1;
    if (dot(p, B) < 0.)
        return d2;

    if (!isout && dot(p, A) < 0.02 && d > 0.02)
        return d1;
    if (!isout && dot(p, B) < 0.05 && d > 0.02)
        return d2;


    return d;
}


vec2 mouseInversion(vec2 p) {
    vec2 m = vec2((2.*iMouse.xy - iResolution.xy)/iResolution.y);
    if(length(m) < 1e-3) m += 1e-3; 
    if(abs(m.x)>.98*.7071 || abs(m.y)>.98*.7071) m *= .98;

    float k = 1./dot(m, m);
    vec2 invCtr = k*m;
    float t = (k - 1.)/dot(p -invCtr, p - invCtr);
    p = t*p + (1. - t)*invCtr;
    p.x = -p.x;
    return p;    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    uv *= 1.05;
    vec2 p = uv;

#ifdef enableMouseInversion
    p = mouseInversion(p);
    p = rot2(iTime/16.)*p;
#endif

    init();

    if(length(p)> 1.)
        p /= dot(p, p);

    MAX_ITER = int(mod(iTime, 20.0));

    bool found = fold(p, count);

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    float tm = -0.005;
    ln = min(ln, lBox(p, vec2(0), v0, .007));
    ln = min(ln, lBox(p, vec2(0), m0, .007));
    ln = min(ln, darc(p, tm) + tm);


    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    pnt = min(pnt, length(p - v0)); 
    pnt = min(pnt, length(p - m0)); 

    float ssf = (2. - smoothstep(0., .25, abs(length(uv) - 1.) - .25));
    float sf = 2./iResolution.y*ssf;

    vec3 oCol = .55 + .45*cos(count*TAU / 8. + vec3(0, 1, 2));
    float pat = smoothstep(0., .25, abs(fract(ln2*50. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);

    vec3 col = min(oCol*(pat*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize)));

    pnt -= .032;
    pnt = min(pnt, length(p) - .032);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));
    col = mix(col, vec3(1, .8, .3), 1. - smoothstep(0., sf, pnt + .02));

    vec3 bg = vec3(1, .2, .4);
    bg *= .7*(mix(col, vec3(1)*dot(col, vec3(.299, .587, .114)), .5)*.5 + .5);
    pat = smoothstep(0., .25, abs(fract((uv.x - uv.y)*43. - .25) - .5)*2. -.5);
    bg *= max(1. - length(uv)*.5, 0.)*(pat*.2 + .9);

    float cir = length(uv);
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*10., abs(cir - 1.) - .05))*.7);
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*2., abs(cir - 1.) - .05)));
    col = mix(col, vec3(.9) + bg, (1. - smoothstep(0., sf, abs(cir - 1.) - .03)));
    col = mix(col, col*max(1. - length(uv)*.5, 0.), (1. - smoothstep(0., sf, -cir + 1.05)));
    col = mix(col, bg, (1. - smoothstep(0., sf, -cir + 1.05)));

    col = mix(col, vec3(0), (1. - smoothstep(0., sf, abs(cir - 1.035) - .03))*.8);
    col = mix(col, 1. - exp(-col), .35);

    //debug mirrors
/*
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.01, abs(dot(uv, A)) - 0.01));
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.01, abs(dot(uv, B)) - 0.01));
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.02, abs(length(uv-C.xy) - C.z))-0.01);
*/
    fragColor = vec4(sqrt(max(col, 0.)), 1.);
}

vec3 get_floor_color(vec2 z) {
    // Modify P, Q, R to change the tiling
    const float P = 7.;
    const float Q = 2.;
    const float R = 3.;
    const float cp = cos(PI / P), sp = sin(PI / P);
    const vec2 mB = vec2(-cp, sp);
    const float k1 = cos(PI / Q);
    const float k2 = (cos(PI / R) + cp * k1) / sp;
    const float rad = 1. / sqrt(k1 * k1 + k2 * k2 - 1.);
    const vec2 cen = vec2(k1 * rad, k2 * rad);
    const vec2 v0 = vec2(0, cen.y - sqrt(rad * rad - cen.x * cen.x));
    const vec2 n_ = vec2(-mB.y, mB.x);
    const float b_ = dot(cen, n_);
    const float c_ = dot(cen, cen) - rad * rad;
    const float k_ = b_ + sqrt(b_ * b_ - c_);
    const vec2 m0 = k_ * n_;

    vec2 p = square_to_disc(z);
    vec2 invCtr = vec2(1);
    float t = 1. / dot(p -invCtr, p - invCtr);
    p = mix(invCtr, p, t);
    p.x = -p.x;
    p = rot2(iTime/12.)*p;

    if (length(p) > 1.)
        p /= dot(p, p);

    float count = 0.;
    for (int i = 0; i < MAX_TILING_REFLECTIONS; i++) {
        if (p.x < 0.) {
            p.x = -p.x;
            count += 1.;
        }
        float k = dot(p, mB);
        if (k < 0.) {
            p -= 2. * k * mB;
            count += 1.;
        }

        float d = length(p - cen) - rad;
        if (d < 0.) {
            p -= cen;
            p *= rad * rad / dot(p, p);
            p += cen;
            count += 1.;
        }
    }

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    ln = min(ln, lBox(p, vec2(0), v0, .007));    
    ln = min(ln, lBox(p, vec2(0), m0, .007));
    ln = min(ln, length(p - cen) - rad - 0.007);

    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    pnt = min(pnt, length(p - v0)); 
    pnt = min(pnt, length(p - m0)); 

    float sf = 2. / iResolution.y;
    float rnd = smoothstep(.8, .97, sin(iTime*.5 + count)*.5 + .5);

    vec3 oCol = .55 + .45*cos(count * PI / 4. + vec3(0, 1, 2));
    oCol = mix(oCol*.1, clamp(oCol*3.5, 0., 1.), rnd);

    float pat = smoothstep(0., .25, abs(fract(ln2*50. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);
    vec3 col = min(oCol*(pat*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize)));

    pnt -= .032;
    pnt = min(pnt, length(p) - .032);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));

    rnd = smoothstep(.8, .97, sin(iTime * 1.5)*.5 + .5);
    vec3 blink = vec3(1.3, 0.25, 0.35);
    blink = mix(blink * 0.5, blink * 2.2, rnd);
    col = mix(col, blink, 1. - smoothstep(0., sf, pnt + .02));
    return col;
}

vec3 get_ceil_color(vec2 p) {
    const float P = 3.;
    const float Q = 3.;
    const float R = 3.;
    const float cp = cos(PI / P), sp = sin(PI / P);
    const float cq = cos(PI / Q), cr = cos(PI / R);
    const vec2 mB = vec2(-cp, sp);
    const vec3 mC = vec3(-cr, -(cq + cr * cp) / sp, 1.);
    const vec2 v0 = vec2(0, -1./mC.y);
    const float k_ = mB.x / mB.y;
    const float x_ = 1. / (k_ * mC.y - mC.x);
    const float y_ = -k_ * x_;
    const vec2 m0 = vec2(x_, y_);

    p = rot2(iTime/12.)*p;

    float count = 0.;
    for (int i = 0; i < MAX_TILING_REFLECTIONS; i++) {
        if (p.x < 0.) {
            p.x = -p.x;
            count += 1.;
        }
        float k = dot(p, mB);
        if (k < 0.) {
            p -= 2. * k * mB;
            count += 1.;
        }

        k = dot(vec3(p, 1.), mC);
        if (k < 0.) {
            p -= 2. * k * mC.xy;
            count += 1.;
        }
    }

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    //ln = min(ln, lBox(p, vec2(0), v0, .05));    
    //ln = min(ln, lBox(p, vec2(0), m0, .05));
    ln = min(ln, lBox(p, v0, m0, .05));

    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    pnt = min(pnt, length(p - v0)); 
    pnt = min(pnt, length(p - m0)); 

    float sf = 2. / iResolution.y;
    float rnd = smoothstep(.8, .97, sin(iTime + count * PI)*.5 + .5);

    vec3 oCol = .55 + .45*cos(count * PI / 8. + vec3(0, 1, 2)).yzx;
    oCol = mix(oCol*.1, clamp(oCol*1.5, 0., 1.), rnd);

    float pat = smoothstep(0., .25, abs(fract(ln2*15. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);
    vec3 col = min(oCol*(pat*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize * 16.)));

    pnt -= .15;
    pnt = min(pnt, length(p) - .15);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));

    rnd = smoothstep(.4, .5, sin(iTime)*.5 + .5);
    vec3 blink = vec3(1.3, 0.25, 0.35);
    blink = mix(blink * 0.5, blink * 2.2, rnd);
    col = mix(col, blink, 1. - smoothstep(0., sf, pnt + .02));
    return col;
}

// distance to the four sides of mirrors 
float sdMirror(vec3 p) {
    vec2 d = RoomSize.xz - abs(p.xz);
    return min(d.x, d.y);
}

// the floor and the ceil
float sdFloor(vec3 p) {
    return RoomSize.y - abs(p.y);
}

// wood framework
float sdRoomFrame(vec3 p) {
    return sdBoxFrame(p, RoomSize, vec3(RoomFrameSize));
}

float sdMirrorFrame(vec3 p) {    
    float d1 = sdBoxFrame(p, RoomSize, WindowFrameSize.xyy);
    float d2 = sdBoxFrame(p, RoomSize, WindowFrameSize.yxy);
    float d3 = sdBoxFrame(p, RoomSize, WindowFrameSize.yyx);
    return min(d1, min(d2, d3));
}

// polyhedra with faces and edges
vec2 sdPolyhedra(vec3 p) {
    p *= RotObj;
    p = fold_spherical(p);
    p -= v0S;
    float d1 = sdPolyhedraFaces(p);
    float d2 = sdPolyhedraEdges(p);
    float d = min(d1, d2);
    float id = d1 < d2 ? 1. : 2.;
    return vec2(d, id);
}

float sdScene(vec3 p) {
    float d1 = sdMirror(p);
    float d2 = sdFloor(p);
    float d3 = sdRoomFrame(p);
    float d4 = sdMirrorFrame(p);
    float d5 = sdPolyhedra(p).x;
    return min(d1, min(d2, min(d3, min(d4, d5))));
}

vec3 trace(vec3 pos, vec3 rd) {
    float h = 1.0;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        if (h < PRECISION)
            break;
        h = sdScene(pos);
        pos += rd * min(h, MAX_MARCH_STEP);
    }
    return pos;
}

float soft_shadow(vec3 pos, vec3 lp) {
    const float softness = 16.;
    const float shadowStartDistance = .1;
    const int shadowLoopCount = 16;

    vec3 ld = lp - pos;
    float ldist = max(length(lp), 0.001);
    ld /= ldist;
    float epsilon = .001;
    float t = shadowStartDistance;
    float h = 1.;
    float shade = 1.0;
    for (int i=0; i < shadowLoopCount; i++ ) {
        if (h < epsilon || t > ldist)
            break;
        h = sdScene(pos + ld * t);
        shade = min(shade, smoothstep(0.0, 1.0, softness*h/t));
        t += h;
    }
    return clamp(shade + 0.25, 0., 1.);
}

vec3 get_normal(vec3 p) {
    const vec2 d = vec2(-1, 1) * .001;
    return normalize(
            sdScene(p + d.xxx)*d.xxx +
            sdScene(p + d.yyx)*d.yyx +
            sdScene(p + d.yxy)*d.yxy +
            sdScene(p + d.xyy)*d.xyy);
}

struct Light {
    vec3 pos;
    vec3 col;
};

Light lights[] = Light[] (
    Light(vec3(2.5, 2.2, 2.5), vec3(1) * 5.),
    Light(vec3(-1, -.5, -1), vec3(1)),
    Light(vec3(-2.5,-1.9,-2.5), vec3(0, .3, 1))
);

void ray_bounce(in vec3 p,
                in vec3 rd,
                out vec3 diffuse,
                out vec3 ref_dir,
                out vec3 bounceTint,
                in vec3 normal) {

    vec3 bCol = vec3(0);
    for( int i=0; i < lights.length(); i++ ) {
        vec3 ld = lights[i].pos - p;
        float ldist = max(length(ld), 0.001);
        ld /= ldist;
        float diff = max(0., dot(normal, ld));
        vec3 viewDir = -rd;
        vec3 halfDir = normalize(ld + viewDir);
        float spec = pow(max(dot(normal, halfDir), 0.0), 20.);
        float at = 1.5 / (1. + ldist * 0.3 + ldist * ldist * 0.1);
        float sh = soft_shadow(p, lights[i].pos);
        bCol += lights[i].col * sh * (.2 + diff + spec) * at;
    }
    // ambient light
    vec3 ao = vec3(.03,.05,.07);
    // sample SDF to approximate occlusion
    ao *= exp2(min(0., sdScene(p + normal * 0.3) / 0.3 - 1.));
    ao *= exp2(min(0., sdScene(p + normal * .15) / .15 - 1.));
    ao *= exp2(min(0., sdScene(p + normal * .07) / .07 - 1.));
    bCol += ao;

    // https://www.shadertoy.com/view/tlBGR1
    vec3 albedo = vec3(.3,.5,.6) * .4;
    vec4 specLevel = vec4(0.9, 0.9, 0.9, 1);

    float d1 = sdMirror(p);
    float d2 = sdFloor(p);
    float d3 = sdRoomFrame(p);
    float d4 = sdMirrorFrame(p);
    vec2 sdPoly = sdPolyhedra(p);
    float d5 = sdPoly.x, id = sdPoly.y;
    float d = min(d1, min(min(d2, d3), min(d4, d5)));

    if (d == d3) {  // room frame
        albedo = vec3(.25);
        specLevel = vec4(0.08);
    }
    else if (d == d4) {  // mirror border
        albedo = vec3(0.01);
        specLevel = vec4(0.2, 0.2, 0.2, 1.);
    }
    else if (d == d5) {  // polyhedra
        if (id == 2.) {  // polyhedra edges
            albedo = vec3(0.15, 0.3, 0.8);
            specLevel = vec4(vec3(0.05), 1.);
        }
        else {  // polyhedra faces
            specLevel = vec4(0.7);
            vec3 bump = texture(iChannel0, p.xz * 0.5).rgb - 0.5;
            normal = normalize(normal + bump * 0.01);
        }
    }
    else if (d == d2) {  // floor and ceil
        vec2 uv = p.xz;
        albedo = p.y < 0. ? get_floor_color(uv / (RoomSize.x - RoomFrameSize))
                          : get_ceil_color(uv * 2.);
        float ior = 1.33;
        float schlick = pow((ior - 1.) / (ior + 1.), 2.);
	    specLevel = vec4(vec3(schlick), 1);
    	specLevel *= mix(.2, .7, p.x);
    	normal = normalize(normal + (texture(iChannel0, vec2(p.xz) /2.).rgb - .5) * .005);
    }

    float fresnel = pow(dot(normal, rd) + 1., 5.);
    vec3 spec = mix(specLevel.xyz, specLevel.www, fresnel);

    bounceTint = spec;
    diffuse = albedo * (vec3(1) - spec) * bCol;
    ref_dir = reflect(rd, normal);
}

vec3 get_ray_color(vec3 ro, vec3 rd) {
    vec3 pos = ro;
    vec3 tint = vec3(1);

    vec3 col = vec3(0);
    for (int i = 0; i < MAX_RAY_BOUNCES; i++) {
        pos = trace(pos, rd);
        vec3 normal = get_normal(pos);
        vec3 bounceTint, diffuse;
        ray_bounce(pos, rd, diffuse, rd, bounceTint, normal);
        pos += normal * PRECISION * 2.;
        col += diffuse * tint;
        tint *= bounceTint;
        if (length(tint) < .01)
            break;
    }
    return col;
}

mat3 camera_matrix(vec3 eye, vec3 lookat, vec3 up) {
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    return mat3(right, up, -forward);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    init_spherical();

    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec2 movement = vec2(iTime * 0.2, sin(iTime * 0.2) * 0.5);
    vec3 eye = 2.5 * vec3(
        cos(movement.x) * cos(movement.y),
        0.5*sin(movement.y),
        sin(movement.x) * cos(movement.y));
    eye += vec3(1, 0.3, 1) * cos(vec3(0.75, 0.3, 0.2) * iTime);

    vec2 mouse = vec2(0);
    if (iMouse.x > 0.) {
        mouse = 2. * iMouse.xy / iResolution.y - 1.;
        eye = rot_y(mouse.x) * rot_x(mouse.y) * eye;
    }
    vec3 lookat = vec3(0);
    vec3 up = vec3(0, 1, 0);
    mat3 M = camera_matrix(eye, lookat, up);

    vec3 ray = M * normalize(vec3(uv, -2.));
    vec3 col = get_ray_color(eye, ray);
    fragColor = vec4(clamp(sqrt(col), 0., 1.), 1.);
}

#define PI  3.141592654
#define BlackRegionSize  0.03
#define L2(x)  dot(x, x)
#define RotObj  rot_x(iTime/2.)*rot_z(iTime/3.)
#define PolyhedraEdgeSize  0.02
#define PolyhedraFaceOffset  0.
#define RoomSize  vec3(5, 2.5, 5)
#define RoomFrameSize  0.5
#define WindowFrameSize vec2(RoomFrameSize*1.2, 0)
#define MAX_TILING_REFLECTIONS 12
#define MAX_TRACE_STEPS  128
#define MAX_RAY_BOUNCES  15
#define PRECISION        1e-3
#define MAX_MARCH_STEP   1.0

const vec3 pqr_wythoff = vec3(5, 2, 3);
const vec3 bary = vec3(1, 1, 1);

mat3 matS, triS;
vec3 v0S;

void init_spherical() {
    vec3 c = cos(PI / pqr_wythoff);
    float sp = sin(PI / pqr_wythoff.x);
    vec3 m1 = vec3(1, 0, 0);
    vec3 m2 = vec3(-c.x, sp, 0);
    float x3 = -c.z;
    float y3 = -(c.y + c.x*c.z)/sp;
    float z3 = sqrt(1.0 - x3*x3 - y3*y3);
    vec3 m3 = vec3(x3, y3, z3);
    matS = mat3(m1, m2, m3);
    triS[0] = normalize(cross(m2, m3));
    triS[1] = normalize(cross(m3, m1));
    triS[2] = normalize(cross(m1, m2));
    v0S = normalize(bary * inverse(matS));
}

vec3 fold_spherical(vec3 p) {
    for (int i = 0; i < 5; i++)
    for (int j = 0; j < 3; j++) {
        p -= 2. * min(dot(p, matS[j]), 0.) * matS[j];
    }
    return p;
}

vec3 tex3D(sampler2D tex, in vec3 p, in vec3 n ) {
    n = max(n * n, 0.001);
    n /= (n.x + n.y + n.z ); 
	return (
        texture(tex, p.yz) * n.x +
        texture(tex, p.zx) * n.y +
        texture(tex, p.xy) * n.z
    ).xyz;
}

mat2 rot2(in float a) {
    float c = cos(a), s = sin(a); return mat2(c, -s, s, c);
}

mat3 rot_x(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0,  0, 
                0, cx, sx,
                0, -sx, cx);
}

mat3 rot_y(in float t) {
    float cy = cos(t), sy = sin(t);
    return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);
}

mat3 rot_z(in float t) {
    float cz = cos(t), sz = sin(t);
    return mat3(cz, -sz, 0.,
                sz, cz, 0.,
                0., 0., 1.);
}

float sBox(in vec2 p, in vec2 b) {
    vec2 d = abs(p) - b;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

float lBox(vec2 p, vec2 a, vec2 b, float ew) {
    float ang = atan(b.y - a.y, b.x - a.x);
    p = rot2(ang)*(p - mix(a, b, .5));

    vec2 l = vec2(length(b - a), ew);
    return sBox(p, (l + ew)/2.) ;
}

// https://www.shadertoy.com/view/3ljcRh
// I changed e to vec3 so it works for non-uniform edge size
float sdBoxFrame(vec3 p, vec3 b, vec3 e) {
    p = abs(p) - b;
    vec3 q = abs(p + e) - e;
    return min(min(
        length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
        length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
        length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float sdPolyhedraEdges(vec3 p) {
    float d = 1e5;
    for (int i = 0; i < 3; i++) {
        d = min(d, L2(p - min(0., dot(p, matS[i])) * matS[i]));
    }
	return sqrt(d) - PolyhedraEdgeSize;
}

float sdPolyhedraFaces(vec3 p) {
   return max(dot(p, triS[0]), max(dot(p, triS[1]), dot(p, triS[2]))) - PolyhedraFaceOffset;
}

//complex multiplication
vec2 cMul(vec2 a, vec2 b) {
	return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

// complex inverse 
vec2 cDiv( vec2 a, vec2 b ) {
  float d = dot(b, b);
  return vec2(dot(a, b), a.y*b.x - a.x*b.y ) / d;
}

// complex inverse 
vec2 cInv(vec2 a) {
	return vec2(a.x, -a.y) / dot(a, a);
}

// code below taken from @mla's https://www.shadertoy.com/view/Mlsfzs
void sncndn(float u, float k2,
            out float sn, out float cn, out float dn) {
    float emc = 1.0 - k2;
    float a = 1.0, b, c;
    const int N = 4;
    float em[N], en[N];
    dn = 1.0;
    for (int i = 0; i < N; i++) {
        em[i] = a;
        emc = sqrt(emc);
        en[i] = emc;
        c = 0.5*(a + emc);
        emc *= a;
        a = c;
    }

    u = c*u;
    sn = sin(u);
    cn = cos(u);
    if (sn != 0.0) {
        a = cn / sn;
        c *= a;
        for(int i = N - 1; i >= 0; i--) {
            b = em[i];
            a *= c;
            c *= dn;
            dn = (en[i] + a) / (b + a);
            a = c/b;
        }
        a = 1.0 / sqrt(c*c + 1.0);
        if (sn < 0.0)
            sn = -a;
        else
            sn = a;
        cn = c * sn;
    }
}

vec2 cn(vec2 z, float k2) {
  float snu, cnu, dnu, snv, cnv, dnv;
  sncndn(z.x, k2, snu, cnu, dnu);
  sncndn(z.y, 1.0-k2, snv, cnv, dnv);
  return vec2(cnu * cnv, -snu*dnu*snv*dnv) / (1. - dnu*dnu*snv*snv);
}

vec2 square_to_disc(vec2 z) {     
    z = cDiv(z, vec2(1));
    z -= vec2(1, 0);
    z *= 1.854;
    z = cn(z, 0.5);
    z = cMul(z, vec2(0.70711));
    return z;
}

/*
This animation shows that Mobius transformations are rigid motions
of "admissible" spheres in the upper half-space.

A sphere is called admissible if its highest point is above the xy-plane.

The procedure involves four steps:

1. Map the complex plane (xy) to the sphere using inverse
   stereographic projection.

2. Translate and rotate the sphere, but keeping it admissible..

3. Map the sphere back to the complex plane using stereographic projection,
   with the north pole being the highest point of the sphere.

4. Combine steps 1-3 to get a Mobius transformation of the complex plane.
   All Mobius transformations can be obtained this way.


In this process,

1. moving the sphere in the xy-plane is a translation of the complex plane.

2. moving it along the z-axis is a scaling of the complex plane.

3. rotating it in the xy-plane is a rotation of the complex plane.

4. rotating 180 degree about the x-axis is the inverse map z -> 1/z.


For a given Mobius transformation M and a chosen initial position
of the sphere, the rigid motion that gives M is unique.
*/
#define AA   3
#define PI 3.141592654
#define TAU (2.0 * PI)
#define ROT(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define PCOS(x) (0.5 + 0.5 * cos(x))
#define DOT2(x) dot(x, x)
#define TIME iTime
#define Y vec3(0, 1, 0)
#define NPOLE(sph) (sph.xyz + sph.w * Y)
#define PLANE vec4(0, 1, 0, 0.)
#define aa (2.0 / iResolution.y)

const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
#define HSV2RGB(c)                                                             \
    (c.z * mix(hsv2rgb_K.xxx,                                                  \
               clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - \
                         hsv2rgb_K.xxx,                                        \
                     0.0, 1.0),                                                \
               c.y))

const float grid_max = 10000.0;
const float grid_size = 0.5;
const vec3 light_pos = vec3(3, 4, 3);
const vec3 light_dir = normalize(light_pos);
const vec3 sky0_color = HSV2RGB(vec3(0.0, 0.65, 0.95));
const vec3 sky1_color = HSV2RGB(vec3(0.6, 0.5, 0.5));
const vec3 grid_color = HSV2RGB(vec3(0.6, 0.6, 1.0));
const vec3 light_color = 12.0 * HSV2RGB(vec3(0.6, 0.5, 1.0));
const vec3 plane_color = HSV2RGB(vec3(0.7, 0.125, 1.0 / 32.0));

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

vec2 mod2(inout vec2 p, vec2 size) {
    vec2 id = floor(p / size);
    p = mod(p + size * 0.5, size) - size * 0.5;
    return id;
}

float rayPlane(vec3 ro, vec3 rd, vec4 p) {
    return -(dot(ro, p.xyz) + p.w) / dot(rd, p.xyz);
}

vec2 raySphere(vec3 ro, vec3 rd, vec4 sph) {
    vec3 oc = ro - sph.xyz;
    float b = dot(oc, rd);
    float c = dot(oc, oc) - sph.w * sph.w;
    float h = b * b - c;
    if (h < 0.0)
        return vec2(-1.0);
    h = sqrt(h);
    return vec2(-b - h, -b + h);
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(12.9898, 58.233))) * 13758.5453);
}

float softShadow(vec3 ro, vec3 rd, vec4 sph, float k) {
    vec3 oc = ro - sph.xyz;
    float r2 = sph.w * sph.w;
    float b = dot(oc, rd);
    float c = dot(oc, oc) - r2;
    float h = b * b - c;
    float d = -sph.w + sqrt(max(0.0, r2 - h));
    float t = -b - sqrt(max(0.0, h));
    return (t < 0.0) ? 1.0 : smoothstep(0.0, 1.0, k * d / t);
}

float bounce() {
    float t = fract(TIME) - 0.5;
    return 0.25 - t * t;
}

vec2 path(float t) {
    const float rad = 0.8;
    return rad * vec2(cos(t), sin(t));
}

void rot(inout vec3 sp) {
    sp.xy *= ROT(TIME * 0.3);
    sp.xz *= ROT(TIME * 0.3);
}

vec3 diffuse(vec3 pos, vec3 nor) {
    float ll = DOT2(light_pos - pos);
    vec3 ld = normalize(light_pos - pos);
    return light_color * max(dot(nor, ld), 0.0) / ll * 20.;
}

vec3 restrictColor(vec2 p, vec3 col) {
    float cond = step(abs(p.x), grid_max);
    cond *= step(abs(p.y), grid_max);
    return mix(plane_color, col, cond);
}

vec3 planeToSphere(vec3 p, vec4 sph) {
    vec3 N = NPOLE(sph);
    vec3 rd = normalize(N - p);
    float t = raySphere(p, rd, sph).x;
    return p + rd * t;
}

vec3 sphereToPlane(vec3 p, vec4 sph, vec4 plane) {
    vec3 N = NPOLE(sph);
    vec3 rd = normalize(p - N);
    float t = rayPlane(p, rd, plane);
    return p + rd * t - sph.xyz;
}

vec3 computeGridColor(vec3 pos, vec2 pp) {
    vec2 z = pp;
    mod2(z, vec2(grid_size));
    float d1 = smin(abs(z.x), abs(z.y), .05);
    float gm = PCOS(-TAU * TIME + 0.25 * TAU * length(pos.xz));

    z = pp;
    vec2 id = mod2(z, vec2(grid_size / 6.));
    float d2 = min(abs(z.x), abs(z.y)) - 0.0125;

    float n = hash21(id);
    float n2 = n * 0.3 + .7;
    if (n < 0.1 * sin(TIME) + 0.1)
        n = 1E6;
    else
        n = 0.0;

    vec3 gcol = vec3(0);
    gcol = mix(gcol, HSV2RGB(vec3(hash21(id), 1.0, n2)), vec3(n > 1.0));
    gcol -= 0.2 * vec3(2.0, 1.0, 1.0) * exp(-100.0 * max(d2 + 0.01, 0.0));
    gcol = mix(gcol, 6. * vec3(0.1, 0.09, 0.125),
               smoothstep(-aa, aa, -(d2 + 0.0075)));
    gcol += 0.5 * vec3(2.0, 1.0, 1.0) * exp(-900.0 * abs(d2 - 0.00125));

    vec3 col = clamp(gcol, -1.0, 1.0);

    gcol = mix(vec3(0.75), 2.0 * vec3(3.5, 2.0, 1.25), gm);
    gcol *= exp(-mix(400.0, 100.0, gm) * max(d1 - 0.0125, 0.0));
    gcol *= 0.3;
    col = mix(col, gcol, smoothstep(-aa, aa, -d1 + 0.0125));
    return col;
}

vec3 renderBackground(vec3 ro, vec3 rd, vec4 sph, float T) {
    vec3 sky = smoothstep(1.0, 0.0, rd.y) * sky1_color +
               smoothstep(0.5, 0.0, rd.y) * sky0_color;

    sky += pow(max(dot(rd, light_dir), 0.0), 800.0) * light_color;
    if (rd.y >= 0.0)
        return sky;

    float ht = 1.0 + 0.2 * smoothstep(-0.05, 0.1, bounce());
    float t = rayPlane(ro, rd, vec4(vec3(0.0, ht, 0.0), 0.5));
    vec3 pos = ro + t * rd;
    vec3 dif = diffuse(pos, Y);
    float sha = softShadow(pos, normalize(light_pos - pos), sph, 2.0);
    dif *= sha;

    vec3 sp = planeToSphere(pos, sph);
    sp -= sph.xyz;
    rot(sp);
    sp += sph.xyz;
    vec3 pp = sphereToPlane(sp, sph, PLANE);

    vec3 col = computeGridColor(pos, pp.xz);
    col = restrictColor(pp.xz, col);
    col += plane_color * dif;
    col /= (1.0 + 0.25 * DOT2(pos.xz));
    col = mix(sky, col, tanh(500.0 / (1.0 + DOT2(pos))));
    return col;
}

vec3 renderBall(vec3 ro, vec3 rd, vec4 sph, vec2 st, float T) {
    vec3 pos = ro + st.x * rd;
    vec3 sp = pos - sph.xyz;
    vec3 nor = normalize(sp);
    vec3 ref = reflect(rd, nor);
    vec3 dif = diffuse(pos, nor);
    rot(sp);
    pos = sp + sph.xyz;
    vec3 pp = sphereToPlane(pos, sph, vec4(0, 1, 0, 0.5));
    vec3 rcol = renderBackground(pos, ref, sph, T);

    vec2 z = pp.xz;
    mod2(z, vec2(grid_size));
    float d1 = smin(abs(z.x), abs(z.y), .05);
    float gm = PCOS(-TAU * T + 0.25 * TAU * length(pp));

    z = pp.xz;
    vec2 id = mod2(z, vec2(grid_size / 6.));
    float d2 = min(abs(z.x), abs(z.y)) - 0.0125;

    float n = hash21(id);
    float n2 = n * 0.3 + .7;
    if (n < 0.1 * sin(T) + 0.1)
        n = 1E6;
    else
        n = 0.0;

    vec3 gcol = vec3(0);
    gcol =
        mix(gcol, HSV2RGB(vec3(hash21(sin(id * TAU)), 1.0, n2)), vec3(n > 1.0));
    gcol -= 0.2 * vec3(2.0, 1.0, 1.0) * exp(-100.0 * max(d2 + 0.01, 0.0));
    gcol = mix(gcol, 6. * vec3(0.09, 0.1, 0.125),
               smoothstep(-aa, aa, -(d2 + 0.0075)));
    gcol += 1. * vec3(2.0, 1.0, 1.0) * exp(-900.0 * abs(d2 - 0.00125));
    vec3 col = clamp(gcol, -1., 1.);
    gcol = mix(vec3(0.75), 2.0 * vec3(3.5, 2.0, 1.25), gm);
    gcol *= exp(-mix(400.0, 100.0, gm) * max(d1 - 0.0125, 0.0));
    gcol *= 0.3;
    col = mix(col, gcol, smoothstep(-aa, aa, -d1 + 0.0125));
    col = restrictColor(pp.xz, col);
    col += rcol * 0.175;
    return col;
}

vec3 render(vec2 p) {
    vec2 ph = path(TIME * 0.5);
    vec3 ro = vec3(2.5, 1.0, 0.);
    ro.xz *= ROT(TIME / 3.);
    vec3 lookat = vec3(0., 0., 0.);
    vec3 forward = normalize(lookat - ro);
    vec3 right = normalize(cross(forward, Y));
    vec3 up = normalize(cross(right, forward));
    vec3 rd = normalize(p.x * right + p.y * up + 2.0 * forward);
    vec4 sph = vec4(vec3(0., bounce(), 0.), 0.5);
    sph.xz += ph;
    vec2 st = raySphere(ro, rd, sph);
    if (st.x >= 0.0)
        return renderBall(ro, rd, sph, st, TIME);
    else
        return renderBackground(ro, rd, sph, TIME);
}

vec3 postprocess(vec3 col, vec2 q) {
    col = pow(clamp(col, 0.0, 1.0), vec3(1.0 / 2.2));
    col = col * 0.6 + 0.4 * col * col * (3.0 - 2.0 * col);
    col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
    col *= 0.5 + 0.5 * pow(19.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.7);
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0);
    for (int i = 0; i < AA; i++) {
        for (int j = 0; j < AA; j++) {
            vec2 p = (2. * fragCoord + 2. * vec2(i, j) / float(AA) - iResolution.xy) / iResolution.y;
            col += render(p);
        }
    }
    col /= float(AA * AA);

    // float fi = smoothstep(0.0, 5.0, TIME);
    // col = mix(vec3(0.0), col, fi);

    col = postprocess(col, uv);
    fragColor = vec4(col, 1.0);
}

