void EuclideanFold(inout vec3 p) {
    if( euclideanTriangleType == 0)
        fold236(p.xy);
    else if ( euclideanTriangleType == 1)
        fold244(p.xy);
    else
        fold333(p.xy);
}

float map(inout vec3 p, inout float scale, inout vec4 orb) {
    float d = dist2balls(p, scale);
    // bool cond = true;
    // K: Very strange behaviour : doing cond && tru_reflect() cuts things out.
    // Remove all those tests and just see if the orbit stops. Now DE is "much" better.
    for (int i = 0; i < Iterations ; i++) {
        vec3 ap = p;
        EuclideanFold(p);
        for (int k = 0; k < 2; k++)
            try_reflect(p, coclusters[k], scale, orb);
        if (all(not(bvec3(p-ap))))
            break;
        d = min( d, dist2balls(p, scale));
    }
#if 0
    // Slow version. DEs to balls are each iterations
    return d;
#else
    // Fast version. distance to balls is done only once.
    return dist2balls(p, scale);
#endif
}

float DE(vec3 p) {
    float DEfactor=1.;
    //orbiTrap is not initialized by default. That's what was producing the strange haze and colors.
    orbitTrap = vec4(1);
    float d = map(p, DEfactor, orbitTrap);

    //Call basic shape and scale its DE
    return 0.3 * d ;// Fudging here because fudge factor desn't seem to affect shadows :-/
}

/*
Wythoff reflective polyhedra, code adapted from

https://www.shadertoy.com/view/tlX3WH

I basically rewrote the polyhera construction and raytracing part there,
and kept the rendering part untouched.

The code should look simpler now?

To play with:

1. Change pqr and truncation_type to see other polyhedron.
2. use 'INSIDE_VIEW' to view from the inside.
3. use 'DUAL' to view the dual polyhera.
pqr=(2, 3, 4) and truncation_type=(0, 0, 1) will give the
rhombic dodecahedron that tiles the whole inner space.
*/
#define PI 3.141592654
#define L2(x) dot(x, x)
#define EDGE_THICKNESS 0.05
#define MAX_TRACE_STEPS 128
#define MAX_RAY_BOUNCES 12
#define EPSILON 1e-4
#define FAR 20.
#define min3(x, y, z) (min(x, min(y, z)))
#define max3(x, y, z) (max(x, max(y, z)))

//#define INSIDE_VIEW

//#define DUAL

const vec3 pqr = vec3(2, 3, 5); // (2, 3, 3), (2, 3, 4)
const vec3 truncation_type = vec3(1, 1, 1);

const float size = 1.35; // polyhera size

mat3 M;  // normals of reflection mirrors
mat3 T;  // three vertices of the fundamental triangle
vec3 v0; // initial vertex

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(vec3(1, 0, 0), vec3(0, c, -s), vec3(0, s, c));
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(vec3(c, 0, s), vec3(0, 1, 0), vec3(-s, 0, c));
}

void init() {
    vec3 c = cos(PI / pqr);
    float sp = sin(PI / pqr.x);
    vec3 m1 = vec3(1, 0, 0);
    vec3 m2 = vec3(-c.x, sp, 0);
    float x3 = -c.z;
    float y3 = -(c.y + c.x * c.z) / sp;
    float z3 = sqrt(1.0 - x3 * x3 - y3 * y3);
    vec3 m3 = vec3(x3, y3, z3);
    M = mat3(m1, m2, m3);
    T[0] = normalize(cross(m2, m3));
    T[1] = normalize(cross(m3, m1));
    T[2] = normalize(cross(m1, m2));
    v0 = normalize(truncation_type * inverse(M)) * size;
#ifdef DUAL
#define proj(p, n) (length(p - dot(p, n) * n))
    float scale = min3(proj(v0, m1), proj(v0, m2), proj(v0, m3));
    v0 /= scale;
#endif
}

vec3 fold(vec3 p) {
    for (int i = 0; i < 5; i++)
        for (int j = 0; j < 3; j++) {
            p -= 2. * min(dot(p, M[j]), 0.) * M[j];
        }
    return p;
}

float dSegment(vec3 p, vec3 a, vec3 b) {
    vec3 pa = p - a;
    vec3 ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    float d = length(pa - ba * h);
    return d;
}

vec3 distEdges(vec3 p) {
    p = fold(p);
#ifdef DUAL
    vec3 ed = vec3(1e5);
    vec3 a = T[0] / dot(T[0], v0) * size;
    vec3 b = T[1] / dot(T[1], v0) * size;
    vec3 c = T[2] / dot(T[2], v0) * size;
    if (bool(truncation_type.x))
        ed.x = dSegment(p, b, c);
    if (bool(truncation_type.y))
        ed.y = dSegment(p, c, a);
    if (bool(truncation_type.z))
        ed.z = dSegment(p, a, b);
    return ed;
#else
    p -= v0;
    vec3 ed;
    for (int i = 0; i < 3; i++) {
        ed[i] = L2(p - min(0., dot(p, M[i])) * M[i]);
    }
    return sqrt(ed);
#endif
}

float map(vec3 p) {
    p = fold(p);
#ifdef DUAL
    return dot(p, v0) - size;
#else
    p -= v0;
    return max(dot(p, T[0]), max(dot(p, T[1]), dot(p, T[2])));
#endif
}

float trace(vec3 pos, vec3 rd, bool outside) {
    float t = 0.0;
    float sgn = outside ? 1.0 : -1.0;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        float d = map(pos + t * rd);
        if (abs(d) < EPSILON)
            return t;
        if (t > FAR)
            break;
        t += sgn * d * 0.9;
    }
    return FAR;
}

vec4 wallColor(vec3 dir, vec3 nor, vec3 eds) {
    float d = min3(eds.x, eds.y, eds.z);

    // Texturing of walls
    vec3 albedo = texture(iChannel1, vec2(eds.xy * 2.0)).rgb;
    albedo = pow(albedo, vec3(2.2)) * 0.5;

    // Simple diffuse lighting
    float lighting = 0.2 + max(dot(nor, vec3(0.8, 0.5, 0.0)), 0.0);

    if (dot(dir, nor) < 0.0) {
        // Outer walls, just add a black line to hide seams
        float f = clamp(d * 1000.0 - 3.0, 0.0, 1.0);
        albedo = mix(vec3(0.01), albedo, f);
        return vec4(albedo * lighting, f);
    } else {
        // Inner walls, add fancy lights
        float m = max3(eds.x, eds.y, eds.z);
        vec2 a = fract(vec2(d, m) * 40.6) - 0.5;
        float b = 1.0 - sqrt(dot(a, a));
        b = 0.2 / (dot(a, a) + 0.2);

        float lightShape = 1.0 - clamp(d * 100.0 - 2.0, 0.0, 1.0);
        lightShape *= b;

        vec3 emissive = vec3(3.5, 1.8, 1.0);
        return vec4(mix(albedo * lighting, emissive, lightShape), 0.0);
    }
    return vec4(1.0);
}

mat3 camera_matrix(vec3 eye, vec3 lookat, vec3 up) {
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    return mat3(right, up, -forward);
}

vec3 get_normal(in vec3 pos) {
    vec3 eps = vec3(0.001, 0.0, 0.0);
    return normalize(vec3(map(pos + eps.xyy) - map(pos - eps.xyy),
                          map(pos + eps.yxy) - map(pos - eps.yxy),
                          map(pos + eps.yyx) - map(pos - eps.yyx)));
}

vec3 background(vec3 dir) {
    vec3 col = texture(iChannel0, dir).rgb;
    col = pow(col, vec3(2.2));
    float origLuma = dot(col, vec3(0.2126, 0.7152, 0.0722)) * 0.7;
    return 2.5 * col / (1.0 - origLuma);
}

vec3 drawRay(vec3 pos, vec3 rd) {
    vec3 color = vec3(0.0);
#ifndef INSIDE_VIEW
    float t = trace(pos, rd, true);
    if (t == FAR) {
        return background(rd);
    }
    pos = pos + t * rd;
    vec3 nor = get_normal(pos);
    vec3 reflDir = reflect(rd, nor);
    vec3 bgColor = pow(background(reflDir), vec3(1.0));
    float fresnel = 0.04 + 0.96 * pow(1.0 - max(dot(rd, -nor), 0.0), 5.0);
    color += bgColor * fresnel;
    vec3 eds = distEdges(pos);
    float d = min3(eds.x, eds.y, eds.z);
    if (d < EDGE_THICKNESS) {
        vec4 wc = wallColor(rd, nor, eds);
        return color * wc.a + wc.rgb;
    }
#endif
    vec3 transmittance = vec3(1.0);
    for (int i = 0; i < MAX_RAY_BOUNCES; i++) {
        float t = trace(pos, rd, false);
        pos = pos + t * rd;
        vec3 eds = distEdges(pos);
        vec3 nor = get_normal(pos);
        float d = min3(eds.x, eds.y, eds.z);
        if (d < EDGE_THICKNESS) {
            return color + transmittance * wallColor(rd, nor, eds).rgb;
        }
        rd = reflect(rd, nor);
        pos += rd * 0.005;
        transmittance *= vec3(0.4, 0.7, 0.7);
    }

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    init();
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;
    vec2 move = vec2(iTime * 0.2, sin(iTime * 0.2) * 0.5);

#ifdef INSIDE_VIEW
    float radius = 0.1;
#else
    float radius = 2.0;
#endif
    vec3 eye = radius * vec3(cos(move.x) * cos(move.y), sin(move.y),
                             sin(move.x) * cos(move.y));

    vec2 mouse = vec2(0);
    if (iMouse.x > 0.)
        mouse = 2. * iMouse.xy / iResolution.y - 1.;
    eye = rotateY(mouse.x) * rotateX(-mouse.y) * eye;

    vec3 lookat = vec3(0);
    vec3 up = vec3(0, 1, 0);
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    vec3 ray = normalize(uv.x * right + uv.y * up + forward * 1.0);
    vec3 color = drawRay(eye, ray);
    color = color / (color * 0.5 + 0.5);
    color = pow(color, vec3(1.0 / 2.2));

    fragColor = vec4(color, 1.0);
}

/*
    A mimic of shane's work at https://www.shadertoy.com/view/WlBczG.

    Show the basic procedure to draw a 2d hyperbolic Poincare tiling.

    Also can draw paracompact and noncompact tilings (with ideal and hyperideal vertices)

*/
// show the three reflection mirrors
//#define debug_mirrors

// comment out this to see the initial pattern
//#define enableMouseInversion

// change to Klein model
//#define KleinModel

// set inf = 1.0 for paracompact tiling
// set inf > 1.0 for noncompact tiling
#define inf       1.15

#define PI		  3.14159265
#define TAU	      6.28318531
#define MAX_ITER  30


// the smaller this value, the larger the black area
#define BlackRegionSize   0.065

// the first entry in PQR must be finite, other two entries can be either finite or infinite
// for example (3, inf 4), (3, 4, inf), etc
const vec3 PQR = vec3(3, inf, 2);


// reflection mirrors
vec2 A, B;
vec3 C;

// two vertices of the fundamental triangle, the 3rd one is the origin
vec2 v0, m0;

// count the number of reflections across each mirror
float count;

// compute cos(PI / x), for x = infiniy this is inf using Vinberg's convention.
float dihedral(float x) { return x == inf ? inf : cos(PI / x); }

mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


void init() {

    float cAB = dihedral(PQR.x);
    float sAB = sqrt(1. - cAB*cAB);

	A = vec2(1, 0);
    B = vec2(-cAB, sAB);

    float cAC = dihedral(PQR.y);
    float cBC = dihedral(PQR.z);

    float k1 = cAC;
    float k2 = (cBC + cAB*cAC) / sAB;
    float r = 1. / sqrt(k1*k1 + k2*k2 - 1.);

    C = vec3(k1, k2, 1.)*r;

    if (r*r >= C.x*C.x)
        v0 = vec2(0., C.y - sqrt(r*r - C.x*C.x));
    else
        v0 = vec2(0, 1);

    vec2 n = vec2(-B.y, B.x);
    float b = dot(C.xy, n);
    float c = dot(C.xy, C.xy) - r*r;
    float k = -1.0;

    if (b*b >= c)
        k = b + sqrt(b*b-c);

    m0 = k*n;
}


bool try_reflect(inout vec2 p, vec2 mirror, inout float count) {
    float k = dot(p, mirror);
    if (k >= 0.)
        return true;
    p -= 2. * k  * mirror;
    count += 1.;
    return false;
}


bool try_reflect(inout vec2 p, vec3 sphere, inout float count) {
    vec2 cen = sphere.xy;
    float r = sphere.z;
    float d = length(p - cen) - r;
    if (d >= 0.)
        return true;
    p -= cen;
    p *= r * r/ dot(p, p);
    p += cen;
    count += 1.;
    return false;
}


void fold(inout vec2 p, inout float count) {
    count = 0.;
    for (int k = 0; k < MAX_ITER; k++) {
        try_reflect(p, A, count);
        //try_reflect(p, B, count);
        try_reflect(p, C, count);
    }
}


float sBox(in vec2 p, in vec2 b) {
    vec2 d = abs(p) - b;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}


float lBox(vec2 p, vec2 a, vec2 b, float ew) {
    float ang = atan(b.y - a.y, b.x - a.x);
    p = rot2(ang)*(p - mix(a, b, .5));

    vec2 l = vec2(length(b - a), ew);
    return sBox(p, (l + ew)/2.) ;
}


vec2 mouseInversion(vec2 p) {
    vec2 m = vec2((2.*iMouse.xy - iResolution.xy)/iResolution.y);
    if(length(m) < 1e-3) m += 1e-3; 
    if(abs(m.x)>.98*.7071 || abs(m.y)>.98*.7071) m *= .98;

    float k = 1./dot(m, m);
    vec2 invCtr = k*m;
    float t = (k - 1.)/dot(p -invCtr, p - invCtr);
    p = t*p + (1. - t)*invCtr;
    p.x = -p.x;
    return p;    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    uv *= 1.05;
    vec2 p = uv;

#ifdef enableMouseInversion
    p = mouseInversion(p);
    p = rot2(iTime/16.)*p;
#endif

    init();   

    if(length(p)> 1.)
        p /= dot(p, p);

#ifdef KleinModel
    p = p / (1. + sqrt(1. - dot(p, p)));
#endif

    fold(p, count);

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    ln = min(ln, lBox(p, -v0, v0, .007));    
    //ln = min(ln, lBox(p, vec2(0), m0, .007));
    ln = min(ln, length(p-C.xy) - C.z - 0.007);

    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    //pnt = min(pnt, length(p - v0)); 
    //pnt = min(pnt, length(p - m0)); 

    float ssf = (2. - smoothstep(0., .25, abs(length(uv) - 1.) - .25));
    float sf = 2./iResolution.y*ssf;

    vec3 oCol = .55 + .45*cos(count*TAU / 8. + vec3(0, 1, 2));
    //float pat = smoothstep(0., .25, abs(fract(ln2*50. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);

    vec3 col = min(oCol*(0.*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    //col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize)));
    vec3 bg = vec3(1, .2, .4);
    bg *= .7*(mix(col, vec3(1)*dot(col, vec3(.299, .587, .114)), .5)*.5 + .5);
    //pat = smoothstep(0., .25, abs(fract((uv.x - uv.y)*43. - .25) - .5)*2. -.5);
    bg *= max(1. - length(uv)*.5, 0.)*(0. + .9);

    float cir = length(uv);
    //col = mix(col, vec3(0), (1. - smoothstep(0., sf*10., abs(cir - 1.) - .05))*.7);
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*2., abs(cir - 1.) - .01)));
    //col = mix(col, vec3(.9) + bg, (1. - smoothstep(0., sf, abs(cir - 1.) - .03)));
    col = mix(col, col*max(1. - length(uv)*.5, 0.), (1. - smoothstep(0., sf, -cir + 1.05)));
    col = mix(col, bg, (1. - smoothstep(0., sf, -cir + 1.01)));

    //col = mix(col, vec3(0), (1. - smoothstep(0., sf, abs(cir - 1.035) - .03))*.8);
    col = mix(col, 1. - exp(-col), .35);

#ifdef debug_mirrors
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.01, abs(dot(uv, A)) - 0.01));
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.01, abs(dot(uv, B)) - 0.01));
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.02, abs(length(uv-C.xy) - C.z))-0.01);
#endif

    fragColor = vec4(sqrt(max(col, 0.)), 1.);
}

