     vec4(0.210, 0.105, 0.025, 0.060); // pulsating solitons


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iMouse.x < -5.0) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
        return;
    }

    vec2 uv = fragCoord / iResolution.xy;

    vec2 pixelSize = 1.0 / iResolution.xy;
    vec2 cen = tx(uv);
    vec2 rig = tx(uv + vec2( pixelSize.x,          0.0));
    vec2 top = tx(uv + vec2(         0.0,  pixelSize.y));
    vec2 lef = tx(uv + vec2(-pixelSize.x,          0.0));
    vec2 bot = tx(uv + vec2(         0.0, -pixelSize.y));

    float Du   = params.x;
    float Dv   = params.y;
    float feed = params.z;
    float kill = params.w;

    vec2 lapl = rig + top + lef + bot - 4.0 * cen;
    float du = Du * lapl.x - cen.x * cen.y * cen.y + feed * (1.0 - cen.x);
    float dv = Dv * lapl.y + cen.x * cen.y * cen.y - (feed + kill) * cen.y;
    vec2 newValue = cen + 0.6 * vec2(du, dv);

    if(iMouse.x > 0.0)
    {
        vec2 diff = uv * iResolution.xy - iMouse.xy;
        float dist = dot(diff, diff);
        if(dist < 1.0)
            newValue.y = 0.9;
    }

    fragColor = vec4(newValue, 0.0, 1.0);
}

/*
Reproduce an image by Fricke and Klein over a century ago.

It's Fig 156 in the book

    "Vorlesungen uber die Theorie der automorphen Funktionen,
     Teubner Leipzig, Germany, 1897."

The coloring and circle parameters are taken from

    https://images-archive.math.cnrs.fr/Un-ensemble-limite.html?lang=fr

Controls:

1. Use mouse to pan
2. Press W/S to zoom in/out
*/

#define PI 3.141592653589793
#define MAX_ITER 1000
#define CURVE_THICKNESS 0.006
#define AA 3

float zoom;

// Pixels near the center of a circle will be inverted to
// very far away and have too large scales. This causes such
// pixels to be incorrectly rendered.
// For such pixels, scale will be roughly the square of |z|,
// where z is the final location in the fundamental domain.
// so add a squared term to fix this.
bool Equal(vec2 z, vec2 a, float scale) {
    float k = 1. + length(z);
    return k * length(z - a) / (scale * zoom) < CURVE_THICKNESS;
}

const vec2 z2 = vec2(0.068, -0.272);
const vec2 z3 = vec2(0.426, -0.672);
const vec2 z4 = vec2(0.816, 0.362);
const vec2 z5 = vec2(-1.154, 0.854);
const float r1 = 0.4;

const float d23 = length(z3 - z2);
const float d34 = length(z3 - z4);
const float d24 = length(z4 - z2);
const float d45 = length(z4 - z5);

const float r2 = (d23 + d24 - d34) / 2.0;
const float r3 = (d23 + d34 - d24) / 2.0;
const float r4 = (d24 + d34 - d23) / 2.0;
const float r5 = d45 - r4;

const float a = r5 + r1;
const float b = r2 + r1;
const float c = length(z2 - z5);

vec2 z1;
vec2 u24, u23, u34, u12, u15, u45;
vec2 zr1;
float rr1;

vec2 circles[5];
float radii[5];

bool region1(vec2 z) { return length(z - zr1) < rr1; }

bool region2(vec2 z) {
    return cross2d(z, u12, u24) > 0. && cross2d(z, u24, u45) > 0. &&
           cross2d(z, u45, u15) > 0. && cross2d(z, u15, u12) > 0.;
}

vec3 getColor(vec2 z) {
    vec2 w = z;
    int count = 0;
    vec3 color = RED;
    float scale = 1.0;

    for (int i = 0; i < MAX_ITER; i++) {
        for (int j = 0; j < 6; j++) {
            vec2 p = vec2[6](u12, u15, u23, u24, u34, u45)[j];
            if (Equal(z, p, scale)) {
                return BLACK;
            }
        }

        bool found = true;
        for (int j = 0; j < 5; j++) {
            if (length(z - circles[j]) < radii[j]) {
                z = invert(z, circles[j], radii[j], scale);
                count++;
                found = false;
            }
        }

        if (found) {
            if (region1(z)) {
                color = (count % 2 == 0) ? BROWN : YELLOW;
            } else if (region2(z)) {
                color = (count % 2 == 0) ? GREEN : LTGREEN;
            } else {
                color = (count % 2 == 0) ? VIOLET : LTVIOLET;
            }
            break;
        }
    }

    float d = 1e5;
    for (int j = 0; j < 5; j++) {
        float d1 = abs(length(w - circles[j]) - radii[j]) - 0.002;
        d = min(d, d1);
    }
    d = smoothstep(0., 0.003, d);
    color = mix(vec3(1.0), color, d);
    return color;
}

void init() {
    z1 = findThirdVertex(z2, z5, a, b, c);
    u24 = getTouchPoint(z2, r2, z4);
    u23 = getTouchPoint(z2, r2, z3);
    u34 = getTouchPoint(z3, r3, z4);
    u12 = getTouchPoint(z1, r1, z2);
    u15 = getTouchPoint(z1, r1, z5);
    u45 = getTouchPoint(z4, r4, z5);
    zr1 = circumcenter(u23, u24, u34);
    rr1 = length(u24 - zr1);

    circles[0] = z1;
    circles[1] = z2;
    circles[2] = z3;
    circles[3] = z4;
    circles[4] = z5;
    radii[0] = r1;
    radii[1] = r2;
    radii[2] = r3;
    radii[3] = r4;
    radii[4] = r5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    init();
    vec4 pan_vals = textureLod(iChannel0, vec2(0.0, 0.0), 0.0);
    zoom = textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;
    vec3 col = vec3(0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * (fragCoord + o) - iResolution.xy -
                       pan_vals.xy * pow(zoom, -1.0)) /
                      iResolution.y;
            uv *= zoom * 0.8;
            col += getColor(uv);
        }
    }
    col /= (float(AA) * float(AA));
    fragColor = vec4(col, 1.0);
}

#define L2(x) dot(x, x)

const vec3 BLACK = vec3(0.0);
const vec3 RED = vec3(1.0, 0.0, 0.0);
const vec3 VIOLET = vec3(0.39, 0.16, 0.71);
const vec3 GREEN = vec3(0.0, 0.5, 0.0);
