const vec3 BROWN = vec3(0.75, 0.5, 0.0);
const vec3 LTGREEN = vec3(0.36, 0.88, 0.25);
const vec3 LTVIOLET = vec3(0.78, 0.39, 1.0);

vec2 findThirdVertex(vec2 z, vec2 w, float a, float b, float c) {
    vec2 d = z - w;
    d /= length(d);
    float k = (a * a - b * b + c * c) / (2.0 * c);
    float h = sqrt(a * a - k * k);
    return w + vec2(d.x * k + d.y * h, d.y * k - d.x * h);
}

vec2 circumcenter(vec2 A, vec2 B, vec2 C) {
    float dx = B.x - A.x;
    float dy = B.y - A.y;
    float ex = C.x - A.x;
    float ey = C.y - A.y;
    float bl = dx * dx + dy * dy;
    float cl = ex * ex + ey * ey;
    float d = 0.5 / (dx * ey - dy * ex);
    float x = A.x + (ey * bl - dy * cl) * d;
    float y = A.y + (dx * cl - ex * bl) * d;
    return vec2(x, y);
}

vec2 getTouchPoint(vec2 z1, float r1, vec2 z2) {
    vec2 d = z2 - z1;
    d /= length(d);
    return d * r1 + z1;
}

float cross2d(vec2 A, vec2 B, vec2 C) {
    vec2 AB = B - A;
    vec2 AC = C - A;
    return AB.x * AC.y - AB.y * AC.x;
}

vec2 invert(vec2 z, vec2 cen, float r, inout float scale) {
    float k = (r * r) / dot(z - cen, z - cen);
    scale *= k;
    return cen + k * (z - cen);
}

