/*
Fractal Dust

Fig 4.2 in the book Indra's Pearls, Chapter 4, "The Schottky Dance".

A Schottky group generated by two Mobius transformations that pairing
four disjoint circles. The limit set is a Cantor set, it looks like a
necklace that has been cut into countless pieces.

Controls:

1. Use mouse to pan
2. Press W/S to zoom in/out
*/

#define AA 3
const float PI = 3.141592654;
const int maxIter = 20;
const float ra = 0.55;
const float rA = 0.55;
const float rb = 0.45;
const float rB = 0.45;

const float thetaA = -PI / 2.;
const float thetaB = PI / 2.;

// colors for disks of different levels
#define N 7
const vec3 colors[N] =
    vec3[N](vec3(0.95, 0.2, 0.2), vec3(.9, .9, 0.3), vec3(.1, .6, .3),
            vec3(0.2, 0.3, 1.), vec3(0.5, .6, 1.), vec3(1, 0.7, 0.75),
            vec3(0.2, 1., 0.95));

vec2 za, zb, zA, zB;

float zoom;

vec2 rot2d(vec2 p, float a) { return p * cos(a) + vec2(-p.y, p.x) * sin(a); }

vec2 conj(vec2 z) { return vec2(z.x, -z.y); }

vec2 a(vec2 z, inout float scale) {
    z -= zA;
    float k = (ra * rA) / dot(z, z);
    scale *= k;
    return za + k * rot2d(conj(z), thetaA);
}

vec2 A(vec2 z, inout float scale) {
    float k = (ra * rA) / dot(z - za, z - za);
    scale *= k;
    return zA + k * rot2d(conj(z - za), thetaA);
}
vec2 b(vec2 z, inout float scale) {
    float k = (rb * rB) / dot(z - zB, z - zB);
    scale *= k;
    return zb + k * rot2d(conj(z - zB), thetaB);
}
vec2 B(vec2 z, inout float scale) {
    float k = (rb * rB) / dot(z - zb, z - zb);
    scale *= k;
    return zB + k * rot2d(conj(z - zb), thetaB);
}

vec3 getColor(vec2 z) {
    float count = 0.;
    float lig = 0.;
    float scale = 1.;
    for (int i = 0; i < maxIter; i++) {
        if (length(z - za) < ra) {
            lig = length(z - za) / ra;
            z = A(z, scale);
            count += 1.0;
        } else if (length(z - zA) < rA) {
            lig = length(z - zA) / rA;
            z = a(z, scale);
            count += 1.0;
        } else if (length(z - zb) < rb) {
            lig = length(z - zb) / rb;
            z = B(z, scale);
            count += 1.0;
        } else if (length(z - zB) < rB) {
            lig = length(z - zB) / rB;
            z = b(z, scale);
            count += 1.0;
        } else {
            break;
        }
    }

    float d = length(z - za) - ra;
    d = min(d, length(z - zb) - rb);
    d = min(d, length(z - zA) - rA);
    d = min(d, length(z - zB) - rB);
    float k = 1. / (10. + length(z));
    d /= k * scale;
    vec3 basecol = vec3(1);
    if (count > 0.) {
        basecol = colors[int(count - 1.) % N];
        basecol =
            vec3(pow(basecol.x, lig), pow(basecol.y, lig), pow(basecol.z, lig));
    }
    float thickness = iResolution.y > 400. ? 2.5 : 4.;
    thickness *= zoom / 200.;
    vec3 col =
        mix(vec3(0), basecol, smoothstep(0., thickness, abs(d) - thickness));
    return col;
}

void init() {
    float t = 3. * iTime;
    const float k = 0.05;
    za = vec2(ra) * (1. + k + k * cos(t));
    zA = vec2(-rA) * (1. + k + k * cos(t));
    zb = vec2(rb, -rb) * (1. + k + k * cos(t));
    zB = vec2(-rB, rB) * (1. + k + k * cos(t));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    init();
    vec4 pan_vals = textureLod(iChannel0, vec2(0.0, 0.0), 0.0);
    zoom = textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;

    vec3 col = vec3(0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (fragCoord + o - .5 * iResolution.xy -
                       pan_vals.xy * pow(zoom, -1.0)) /
                      iResolution.y;
            uv *= 2.0;
            uv *= zoom;
            col += getColor(uv);
        }
    }
    col /= (float(AA) * float(AA));
    fragColor = vec4(col, 1.0);
}

