#define AA 3

#define FOV_DIST         3.0
#define FOLDING_NUMBER   8
#define MAX_TRACE_STEPS  200
#define MIN_TRACE_DIST   0.01
#define MAX_TRACE_DIST   10.0
#define PRECISION        1e-4
#define PI               3.14159265358979323
#define T                (iTime * 0.005)


const vec4 param_min = vec4(-1.4661, -1.1076, -1.1844, 1.8886);
const vec4 param_max = vec4(3.7324, 1.1972, 1.1204, 2.4136);


// view to world transformation
mat3 viewMatrix(vec3 camera, vec3 lookat, vec3 up) {
    vec3 f = normalize(lookat - camera);
    vec3 r = normalize(cross(f, up));
    vec3 u = normalize(cross(r, f));
    return mat3(r, u, -f);
}

// 2D rotatation
void R(inout vec2 p, float a) {
    p = cos(a) * p + sin(a) * vec2(p.y, -p.x);
}

float sdSponge(vec3 z) {
    z *= 5.0;
    //folding
    for(int i = 0; i < 4; i++) {
        z = abs(z);
        z.xy = (z.x < z.y) ? z.yx : z.xy;
        z.xz = (z.x < z.z) ? z.zx : z.xz;
        z.zy = (z.y < z.z) ? z.yz : z.zy;
        z = z * 3.0 - 2.0;
        z.z += (z.z < -1.0) ? 2.0 : 0.0;
    }
    //distance to cube
    z = abs(z) - vec3(1.0);
    float dis = min(max(z.x, max(z.y, z.z)), 0.0) + length(max(z, 0.0));
    //scale cube size to iterations
    return dis * 0.2 * pow(3.0, -3.0);
}

vec2 DE(vec3 p) {
    float k, r2;
    float scale = 1.0;
    float orb = 1e4;
    for (int i = 0; i < FOLDING_NUMBER; i++) {
        p = 2.0 * clamp(p, param_min.xyz, param_max.xyz) - p;
        r2 = dot(p, p);
        k = max(param_min.w / r2, 1.0);
        p *= k;
        scale *= k;
        orb = min(orb, r2);
    }
    p /= scale;
    p *= param_max.w * 10.0;
    return vec2(0.1 * sdSponge(p) / (param_max.w * 10.0),
                sqrt(orb));
}

vec3 calcNormal(vec3 p) {
    const vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
			  DE(p + e.xyy).x - DE(p - e.xyy).x,
			  DE(p + e.yxy).x - DE(p - e.yxy).x,
			  DE(p + e.yyx).x - DE(p - e.yyx).x));
}

vec2 trace(vec3 ro, vec3 rd) {
    float t = MIN_TRACE_DIST;
    vec2 h;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        h = DE(ro + rd * t);
        if (h.x < PRECISION * t || t > MAX_TRACE_DIST)
            return vec2(t, h.y);
        t += h.x;
    }
    return vec2(-1.0, 0.0);
}

float softShadow(vec3 ro, vec3 rd, float tmin, float tmax, float k) {
    float res = 1.0;
    float t = tmin;
    for (int i = 0; i < 30; i++) {
        float h = DE(ro + rd * t).x;
        res = min(res, k * h / t);
        t += clamp(h, 0.01, 0.1);
        if (h < 0.001 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}

float calcAO(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = DE(p + h * n).x;
        occ += (h - d) * sca;
        sca *= 0.95;
    }
    return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

// https://www.shadertoy.com/view/4dSfRc
vec3 render(vec3 ro, vec3 rd, vec3 lig, vec2 uv) {
    vec3 background = vec3(0.08, 0.16, 0.32);
    vec3 col = background;
    vec2 res = trace(ro, rd);
    float t = res.x;
    if (t >= 0.0) {
        col = vec3(0.94, 0.93, 0.90); // silver taken from POV-Ray
        vec3 pos = ro + rd * t;
        vec3 nor = calcNormal(pos);
        vec3 ref = reflect(rd, nor);
        float occ = calcAO(pos, nor);
        float amb = 0.2;
        float dif = clamp(dot(nor, lig), 0.0, 1.0);
        float bac = clamp(dot(nor, normalize(vec3(-lig.x, 0.0, -lig.z))), 0.0, 1.0 ) * clamp(1.0 - pos.y, 0.0, 1.0);
        float dom = smoothstep(-0.1, 0.1, ref.y);
        float fre = pow(clamp(1.0 + dot(nor, rd), 0.0, 1.0), 2.0);
        float spe = pow(clamp(dot(ref, lig), 0.0, 1.0), 16.0);
        dif *= softShadow(pos, lig, 0.02, 5.0, 16.0);
        dom *= softShadow(pos, ref, 0.02, 5.0, 16.0);

        vec3 lin = vec3(0.05);
        lin += 1.5 * dif * texture(iChannel0, uv).rgb;
        lin += 4.0 * spe * vec3(1.0, 0.9, 0.7) * dif;
        lin += 0.3 * amb * vec3(0.4, 0.6, 1.0) * occ;
        lin += 0.2 * bac * vec3(0.25) * occ;
        lin += 0.5 * dom * vec3(0.4, 0.6, 1.0) * occ;
        lin += 0.25 * fre * vec3(1.0) * occ;

        col *= lin;
        float atten = 1.0 / (1.0 + t * t * 0.3);
        col *= atten;
        col = mix(col, background, smoothstep(0.0, 0.95, t / MAX_TRACE_DIST));
    }
    return sqrt(col);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 tot = vec3(0.0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            // map uv to (-1, 1) and adjust aspect ratio
            vec2 offset = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (fragCoord + offset) / iResolution.xy;
            uv = 2.0 * uv - 1.0;
            uv.x *= iResolution.x / iResolution.y;
            vec3 camera = vec3(1.0, -1.0, 0.6);
            vec3 lookat = vec3(0.0);
            vec3 up = vec3(0.0, 0.0, 1.0);
            // set camera
            vec3 ro = camera;
            R(ro.xy, T);
            mat3 M = viewMatrix(ro, lookat, up);
            // put screen at distance FOV_DISt in front of the camera
            vec3 rd = M * normalize(vec3(uv, -FOV_DIST));
            vec3 lig = normalize(vec3(1.0));
            vec3 col = render(ro, rd, lig, fragCoord);
            tot += col;
        }
    }
    tot /= float(AA * AA);
    fragColor = vec4(tot, 1.0);
}

/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
Boyd-Maxwell circle packings of hyperbolic Coxeter groups: rank 4/level 2 case
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

For more information check the github repo:

    https://github.com/neozhaoliang/Hyperbolic-Honeycombs

Required input:

The Coxeter diagram of a rank 4 Coxeter group G = <A, B, C, D>, with dihedral
angles m_{s,t} in the order (AB, AC, AD, BC, BD, CD).

Note:

1. CD must be finite
2. The triangle group <B, C, D> must be hyperbolic

If you see only a sphere and a small disk under it, try comment out `#define
useMouseInvert` below.
*/

#define useMouseInvert

#define shiny 5.

//#define config      initLevel2Graph(float[6](4., 2., 7., 4., 2., 5.))
//#define config      initLevel2Graph(float[6](3., 2., 2., 3., 2., 7.))
//#define config      initLevel2Graph(float[6](3., 3., 2., 3., 2., 7.))
//#define config      initLevel2Graph(float[6](4., 4., 4., 4., 4., 4.))
//#define config      initLevel2Graph(float[6](2., 2., 7., inf, 3., 3.))
//#define config      initLevel2Graph(float[6](3., 3., 2., 7., 2., 3.))
#define config initLevel2Graph(float[6](5., 2., 3., 4., 3., 3.))

vec2 map(vec3 p) {
  float d1 = sdSphere(p);
  float d2 = sdPlane(p);
  float id = (d1 < d2) ? 0. : 1.;
  return vec2(min(d1, d2), id);
}

// standard scene normal
vec3 getNormal(vec3 p) {
  const vec2 e = vec2(0.0001, 0.);
  return normalize(vec3(map(p + e.xyy).x - map(p - e.xyy).x,
                        map(p + e.yxy).x - map(p - e.yxy).x,
                        map(p + e.yyx).x - map(p - e.yyx).x));
}

// get the signed distance to an object and object id
vec2 raymarch(in vec3 ro, in vec3 rd) {
  float t = MIN_TRACE_DIST;
  vec2 h;
  for (int i = 0; i < MAX_TRACE_STEPS; i++) {
    h = map(ro + t * rd);
    if (h.x < PRECISION * (t * 0.125 + 1.))
      return vec2(t, h.y);

    if (t > MAX_TRACE_DIST)
      break;

    t += h.x;
  }
  return vec2(-1.0);
}

float calcOcclusion(vec3 p, vec3 n) {
  float occ = 0.0;
  float sca = 1.0;
  for (int i = 0; i < 5; i++) {
    float h = 0.01 + 0.15 * float(i) / 4.0;
    float d = map(p + h * n).x;
    occ += (h - d) * sca;
    sca *= 0.85;
  }
  return clamp(1.0 - occ, 0.2, 1.0);
}

float softShadow(vec3 ro, vec3 rd, float tmin, float tmax, float k) {
  float res = 1.0;
  float t = tmin;
  for (int i = 0; i < 20; i++) {
    float h = map(ro + rd * t).x;
    res = min(res, k * h / t);
    t += clamp(h, 0.001, 0.1);
    if (h < 0.0001 || t > tmax)
      break;
  }
  return clamp(res, 0., 1.0);
}

vec3 getColor(vec3 ro, vec3 rd, vec3 pos, vec3 nor, vec3 lp, vec3 basecol) {
  vec3 col = vec3(0.0);
  vec3 ld = lp - pos;
  float lDist = max(length(ld), .001);
  ld /= lDist;
  float ao = calcOcclusion(pos, nor);
  float sh = softShadow(pos + 0.001 * nor, ld, 0.01, lDist, 8.);
  float diff = clamp(dot(nor, ld), 0., 1.);
  float atten = shiny / (1. + lDist * lDist * .001);
  float spec = pow(max(dot(reflect(-ld, nor), -rd), 0.0), 6.);
  float fres = clamp(1.0 + dot(rd, nor), 0.0, 1.0);
  col += basecol * diff;
  col += basecol * vec3(.8, 1., 0.3) * spec * 4.;
  col += basecol * vec3(1.2, 1., .8) * fres * fres * 2.;
  col *= ao * atten * sh;
  col += basecol * clamp(0.8 + 0.2 * nor.z, 0., 1.);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec3 scenecol = vec3(0.);

  // initialize the mirrors

  config;

  vec3 camera = vec3(4, 1.5, 4);
  vec3 lp = vec3(.5, .5, 4.); // light position
  camera.xy = rot2d(camera.xy, iTime * 0.3);
  vec3 lookat = vec3(0.);
  vec3 up = vec3(0, 0, 1);
  vec3 forward = normalize(lookat - camera);
  vec3 right = normalize(cross(forward, up));
  up = normalize(cross(right, forward));

  float lw = 8. / iResolution.y;

  // antialiasing loop

  for (int ii = 0; ii < AA; ii++) {
    for (int jj = 0; jj < AA; jj++) {
      vec2 o = vec2(float(ii), float(jj)) / float(AA);
      vec2 uv = (2. * fragCoord + o - iResolution.xy) / iResolution.y;
      vec3 rd = normalize(uv.x * right + uv.y * up + 1.5 * forward);
      vec2 res = raymarch(camera, rd);
      float t = res.x;
      float id = res.y;
      vec3 pos = camera + t * rd;

      float ed;
      vec3 col;
      vec2 p0;
      vec3 nor;

      // the sphere is hit
      if (id == 0.) {
        nor = pos;
        p0 = sphereToPlane(pos);
      }

      // the plane is hit
      else if (id == 1.) {
        nor = vec3(0, 0, 1);
        p0 = pos.xy;
      }
#ifdef useMouseInvert
      vec2 mouse = (iMouse.xy - 2. * iResolution.xy) / iResolution.y;
      if (iMouse.z == 0.)
        mouse = vec2(1.5, 1.5);
      mouse = rot2d(mouse, iTime * 0.1);
      float k = 1. / L2(mouse);
      vec2 invCtr = k * mouse;
      float tt = (k - 1.) / L2(p0 - invCtr);
      p0 = tt * p0 + (1. - tt) * invCtr;
#endif
      vec3 basecol = getcolor(p0, ed);
      col = getColor(camera, rd, pos, nor, lp, basecol);
      float dist = distanceToMirrors(p0);
      col = mix(col, vec3(0.), 1.0 - exp(-0.01 * t * t));
      scenecol += col;
    }
  }
  scenecol /= (float(AA) * float(AA));

  fragColor = vec4(sqrt(max(scenecol, 0.0)), 1.0);
}

#define inf 1.0 // Vinberg convention for edge labels
#define MAX_REFLECTIONS 500
#define AA 2
#define PI 3.141592653
#define L2(x) dot(x, x)
#define L2XY(x, y) L2(x - y)
#define Hyperbolic -1.0
#define Euclidean 0.
#define Spherical 1.
#define MAX_TRACE_STEPS 500
#define MIN_TRACE_DIST 0.001
#define MAX_TRACE_DIST 100.0
#define PRECISION 0.0001
#define EdgeSize1 0.0035
#define EdgeSize2 0.01
#define ColorScale 0.8

// Move a vertex of Euclidean type (if there is any) to infinity,
// so the pattern tiles the entire plane
#define moveVertexToInf

// 4x4 Cartan matrix for the Coxeter group
mat4 cartan;

// geometry type of each vertex in the Coxeter diagram
// -1 for hyerbolic, 0 for Euclidean, 1 for spherial
vec4 vertexType = vec4(1);

// coordinates of a Euclidean vertex
vec2 euclideanVertex;

// if we are outside of the unit circle
// if a point lies outside of unit circle, the distance
// estimation becomes inaccurate. We use EdgeSize2 to control the edge size for
// this case.
bool outside = false;

// if there is an Euclidean vertex, i.e. if remove this vertex, the remain
// diagram is affine
bool hasEuclideanVertex;

// compute cos(PI / x), for x = inf this is just inf
float dihedral(float x) { return x == inf ? inf : cos(PI / x); }

// complex multiplication
vec2 cmul(vec2 p, vec2 q) {
  return vec2(p.x * q.x - p.y * q.y, p.x * q.y + p.y * q.x);
}

// complex division
vec2 cdiv(vec2 z, vec2 w) {
  return vec2(z.x * w.x + z.y * w.y, -z.x * w.y + z.y * w.x) / dot(w, w);
}

// compute the vertex type of each vertex.
// for each vertex in the Coxeter diagram, its vertex type is determined by the
// triangle group G formed by the remaining three vertices. This vertex is of
// hyperbolic/spherical/euclidean iff G is hyperbolic/spherical/euclidean,
// respectively. This can be checked from the determinant of the leading minors
// of the Cartan matrix.
void checkCartan(mat4 M) {
  const float e = 0.001;
  float det;
  det = determinant(mat3(M[1].yzw, M[2].yzw, M[3].yzw));
  vertexType.x = step(-e, det) + step(e, det) - 1.;

  det = determinant(mat3(M[0].xzw, M[2].xzw, M[3].xzw));
  vertexType.y = step(-e, det) + step(e, det) - 1.;

  det = determinant(mat3(M[0].xyw, M[1].xyw, M[3].xyw));
  vertexType.z = step(-e, det) + step(e, det) - 1.;

  det = determinant(mat3(M[0].xyz, M[1].xyz, M[2].xyz));
  vertexType.w = step(-e, det) + step(e, det) - 1.;
}

// For spheres cen is the center, r is the radius
// For planes cen is the normal vector, r is the offset from the origin along
// the normal. if invert is true then the inside/outside of the sphere is
// exchanged.
struct Ball {
  vec2 cen;
  float r;
  bool isplane;
  bool invert;
};

// coclusters are mirror balls, they corresponde to root vectors (space-like)
Ball[4] coclusters;
// clusters are real balls, they corresponde to space-like weight vectors
Ball[4] clusters;

// create a default ball (unit circle)
Ball defaultBall() { return Ball(vec2(0), 1., false, false); }

Ball from_plane(vec2 normal, float offset) {
  return Ball(normal, offset, true, false);
}

Ball from_sphere(vec2 cen, float r) { return Ball(cen, r, false, false); }

void invertBall(inout Ball B) { B.invert = !B.invert; }

// try to reflect a point p to the positive half space bounded by a ball
// if we are already in the positive half space, do nothing and return true,
// else reflect about the ball and return false
// if B is a sphere we try to reflect p into the interior of B
bool try_reflect(inout vec2 p, Ball B, inout int count) {
  vec2 cen = B.cen;
  float r = B.r;
  if (B.isplane) {
    float k = dot(vec3(p, 1), vec3(cen, r));
    if (k >= 0.)
      return true;
    p -= 2. * k * cen;
    count += 1;
    return false;
  } else {
    vec2 q = p - cen;
    float d2 = dot(q, q);
    float k = (r * r) / d2;
    if (k < 1.0)
      return true;
    p = k * q + cen;
    count += 1;
    return false;
  }
}

vec2 getIntersection(Ball B1, Ball B2, Ball B3) {
  vec2 dir = vec2(-B3.cen.y, B3.cen.x);
  float r1 = B1.r, r2 = B2.r;
  float k = (L2(B1.cen) - L2(B2.cen) - (r1 * r1 - r2 * r2)) /
            (2. * dot(B1.cen - B2.cen, dir));
  return k * dir;
}

Ball solveBall(Ball B1, Ball B2, Ball B3) {
  vec2 dir = vec2(-B3.cen.y, B3.cen.x);
  float r1 = B1.r, r2 = B2.r;
  float k = (L2(B1.cen) - L2(B2.cen) - (r1 * r1 - r2 * r2)) /
            (2. * dot(B1.cen - B2.cen, dir));
  vec2 cen = k * dir;
  float r = sqrt(L2XY(cen, B1.cen) - r1 * r1);
  return from_sphere(cen, r);
}

float sdistanceToBall(vec2 p, Ball B) {
  if (B.isplane) {
    float k = dot(vec3(p, 1), vec3(B.cen, B.r));
    return k;
  } else {
    float k = length(p - B.cen) - B.r;
    return B.invert ? -k : k;
  }
}

void initLevel2Graph(in float[6] diagram) {
  Ball B0, B1, B2, B3;
  float c01 = dihedral(diagram[0]);
  float c02 = dihedral(diagram[1]);
  float c03 = dihedral(diagram[2]);
  float c12 = dihedral(diagram[3]);
  float c13 = dihedral(diagram[4]);
  float c23 = dihedral(diagram[5]);

  cartan = mat4(1, -c01, -c02, -c03, -c01, 1, -c12, -c13, -c02, -c12, 1, -c23,
                -c03, -c13, -c23, 1);
  checkCartan(cartan);

  float s23 = sqrt(1. - c23 * c23);

  // The two virtual balls B2, B3 (reflection mirrors) are lines through the
  // origin
  B2 = from_plane(vec2(1, 0), 0.);
  B3 = from_plane(vec2(-c23, s23), 0.);

  // The mirror ball B1(x, y, r) satisfy
  // Angle with B2: dot((x, y), (1, 0)) / r = c12
  // Angle with B3: dot((x, y), (-c23, s23)) / r = c13
  // Orthogonal to the real ball (unit ball) C0: x^2 + y^2 = r^2 + 1
  float k1 = c12;
  float k2 = (c13 + c23 * c12) / s23;
  float r = 1. / sqrt(k1 * k1 + k2 * k2 - 1.);

  B1 = from_sphere(vec2(k1 * r, k2 * r), r);

  // The mirror ball B0(x, y, r) satisfy
  // Angle with B2: dot((x, y), (1, 0)) / r = c02
  // Angle with B3: dot((x, y), (-c23, s23)) / r = c03
  // Angle with B1:
  //
  //        r^2 + r1^2 - |(x,y) - (x1,y1)|^2
  //        --------------------------------  = -c01
  //                   2 * r * r1
  k1 = c02;
  k2 = (c03 + c23 * c02) / s23;

  float a = k1 * k1 + k2 * k2 - 1.;
  float b = dot(vec3(k1, k2, c01), vec3(B1.cen, B1.r));
  float c = L2(B1.cen) - B1.r * B1.r;

  r = b / a - sqrt(b * b - a * c) / a;
  B0 = from_sphere(vec2(k1 * r, k2 * r), r);

  coclusters = Ball[4](B0, B1, B2, B3);

  for (int k = 0; k < 4; k++) {
    clusters[k] = defaultBall();
  }

  invertBall(clusters[0]);

  if (vertexType.y == Hyperbolic) {
    float r = sqrt(L2(B0.cen) - B0.r * B0.r);
    clusters[1] = from_sphere(vec2(0), r);
  }
  if (vertexType.z == Hyperbolic) {
    clusters[2] = solveBall(B0, B1, B3);
  }
  if (vertexType.w == Hyperbolic) {
    clusters[3] = solveBall(B0, B1, B2);
  }

  if (vertexType.y == Euclidean) {
    hasEuclideanVertex = true;
    euclideanVertex = vec2(0);
    return;
  }

  if (vertexType.z == Euclidean) {
    hasEuclideanVertex = true;
    euclideanVertex = getIntersection(B0, B1, B3);
    return;
  }

  if (vertexType.w == Euclidean) {
    hasEuclideanVertex = true;
    euclideanVertex = getIntersection(B0, B1, B2);
    return;
  }
}

vec2 applyMobius(vec2 p) {
  if (hasEuclideanVertex) {
    vec2 A = euclideanVertex;
    vec2 B = vec2(0, 0);
    vec2 C = vec2(1, 0);
    vec2 D = vec2(4, 0);
    p = cdiv(cmul(p, A) + B, cmul(C, p) + D);
  }
  return p;
}

float distanceToMirrors(vec2 p) {
  float d = abs(sdistanceToBall(p, coclusters[0]));

  if (length(p) > 1.) {
    p /= L2(p);
    outside = true;
  }

  for (int k = 1; k < 4; k++) {
    d = min(d, abs(sdistanceToBall(p, coclusters[k])));
  }
  return d;
}

void fold(inout vec2 p, inout int count, inout int index) {
#ifdef moveVertexToInf
  p = applyMobius(p);
#endif
  for (int i = 0; i < MAX_REFLECTIONS; i++) {
    bool cond = true;
    for (int k = 0; k < 4; k++) {
      cond = try_reflect(p, coclusters[k], count) && cond;
    }
    if (cond)
      break;
  }
  for (int k = 0; k < 4; k++) {
    if (vertexType[k] == Hyperbolic &&
        sdistanceToBall(p, clusters[k]) < -0.0001) {
      index = k;
      break;
    }
  }
}

// signed distance to unit ball and plane z=-1
float sdSphere(vec3 p) { return length(p) - 1.0; }
float sdPlane(vec3 p) { return p.z + 1.0; }

// project points on the unit ball to plabe z=-1
vec2 sphereToPlane(vec3 p) { return 2. * p.xy / (1. - p.z); }

// 2d rotation
vec2 rot2d(vec2 p, float a) { return p * cos(a) + vec2(-p.y, p.x) * sin(a); }

// you can implement your color functions here
vec3 colormap(int index, float t) {
  float c = float(index) + 1.;
  return .5 + .45 * cos(2. * PI * pow(t, 0.3) * ColorScale * c + vec3(0, 1, 2));
}

vec3 getcolor(inout vec2 p, out float ed) {
  int index = -1;
  int count = 0;
  fold(p, count, index);
  float dist = distanceToMirrors(p);
  float t = clamp(float(count + 1) / float(MAX_REFLECTIONS), 0., 1.);
  vec3 col = colormap(index, t);
  float es = outside ? EdgeSize2 : EdgeSize1;
  float aa = fwidth(dist) / 2.;
  col = mix(col, vec3(0), 1. - smoothstep(es - aa, es + aa, dist));
  return col;
}

/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
Boyd-Maxwell circle packings of hyperbolic Coxeter groups: rank 4/level 2 case
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

For more information check the github repo:

    https://github.com/neozhaoliang/Hyperbolic-Honeycombs

Required input:

The Coxeter diagram of a rank 4 Coxeter group G = <A, B, C, D>, with dihedral
angles m_{s,t} in the order (AB, AC, AD, BC, BD, CD).

Note:

1. CD must be finite
2. The triangle group <B, C, D> must be hyperbolic

If you see only a sphere and a small disk under it, try comment out `#define
useMouseInvert` below.
*/

#define useMouseInvert

#define shiny 5.

//#define config      initLevel2Graph(float[6](4., 2., 7., 4., 2., 5.))
//#define config      initLevel2Graph(float[6](3., 2., 2., 3., 2., 7.))
//#define config      initLevel2Graph(float[6](3., 3., 2., 3., 2., 7.))
//#define config      initLevel2Graph(float[6](4., 4., 4., 4., 4., 4.))
//#define config      initLevel2Graph(float[6](2., 2., 7., inf, 3., 3.))
//#define config      initLevel2Graph(float[6](3., 3., 2., 7., 2., 3.))
#define config initLevel2Graph(float[6](5., 2., 3., 4., 3., 3.))

vec2 map(vec3 p) {
  float d1 = sdSphere(p);
  float d2 = sdPlane(p);
  float id = (d1 < d2) ? 0. : 1.;
  return vec2(min(d1, d2), id);
}

// standard scene normal
vec3 getNormal(vec3 p) {
  const vec2 e = vec2(0.0001, 0.);
  return normalize(vec3(map(p + e.xyy).x - map(p - e.xyy).x,
                        map(p + e.yxy).x - map(p - e.yxy).x,
                        map(p + e.yyx).x - map(p - e.yyx).x));
}

// get the signed distance to an object and object id
vec2 raymarch(in vec3 ro, in vec3 rd) {
  float t = MIN_TRACE_DIST;
  vec2 h;
  for (int i = 0; i < MAX_TRACE_STEPS; i++) {
    h = map(ro + t * rd);
    if (h.x < PRECISION * (t * 0.125 + 1.))
      return vec2(t, h.y);

    if (t > MAX_TRACE_DIST)
      break;

    t += h.x;
  }
  return vec2(-1.0);
}

float calcOcclusion(vec3 p, vec3 n) {
  float occ = 0.0;
  float sca = 1.0;
  for (int i = 0; i < 5; i++) {
    float h = 0.01 + 0.15 * float(i) / 4.0;
    float d = map(p + h * n).x;
    occ += (h - d) * sca;
    sca *= 0.85;
  }
  return clamp(1.0 - occ, 0.2, 1.0);
}

float softShadow(vec3 ro, vec3 rd, float tmin, float tmax, float k) {
  float res = 1.0;
  float t = tmin;
  for (int i = 0; i < 20; i++) {
    float h = map(ro + rd * t).x;
    res = min(res, k * h / t);
    t += clamp(h, 0.001, 0.1);
    if (h < 0.0001 || t > tmax)
      break;
  }
  return clamp(res, 0., 1.0);
}

vec3 getColor(vec3 ro, vec3 rd, vec3 pos, vec3 nor, vec3 lp, vec3 basecol) {
  vec3 col = vec3(0.0);
  vec3 ld = lp - pos;
  float lDist = max(length(ld), .001);
  ld /= lDist;
  float ao = calcOcclusion(pos, nor);
  float sh = softShadow(pos + 0.001 * nor, ld, 0.01, lDist, 8.);
  float diff = clamp(dot(nor, ld), 0., 1.);
  float atten = shiny / (1. + lDist * lDist * .001);
  float spec = pow(max(dot(reflect(-ld, nor), -rd), 0.0), 6.);
  float fres = clamp(1.0 + dot(rd, nor), 0.0, 1.0);
  col += basecol * diff;
  col += basecol * vec3(.8, 1., 0.3) * spec * 4.;
  col += basecol * vec3(1.2, 1., .8) * fres * fres * 2.;
  col *= ao * atten * sh;
  col += basecol * clamp(0.8 + 0.2 * nor.z, 0., 1.);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec3 scenecol = vec3(0.);

  // initialize the mirrors

  config;

  vec3 camera = vec3(4, 1.5, 4);
  vec3 lp = vec3(.5, .5, 4.); // light position
  camera.xy = rot2d(camera.xy, iTime * 0.3);
  vec3 lookat = vec3(0.);
  vec3 up = vec3(0, 0, 1);
  vec3 forward = normalize(lookat - camera);
  vec3 right = normalize(cross(forward, up));
  up = normalize(cross(right, forward));

  float lw = 8. / iResolution.y;

  // antialiasing loop

  for (int ii = 0; ii < AA; ii++) {
    for (int jj = 0; jj < AA; jj++) {
      vec2 o = vec2(float(ii), float(jj)) / float(AA);
      vec2 uv = (2. * fragCoord + o - iResolution.xy) / iResolution.y;
      vec3 rd = normalize(uv.x * right + uv.y * up + 1.5 * forward);
      vec2 res = raymarch(camera, rd);
      float t = res.x;
      float id = res.y;
      vec3 pos = camera + t * rd;

      float ed;
      vec3 col;
      vec2 p0;
      vec3 nor;

      // the sphere is hit
      if (id == 0.) {
        nor = pos;
        p0 = sphereToPlane(pos);
      }

      // the plane is hit
      else if (id == 1.) {
        nor = vec3(0, 0, 1);
        p0 = pos.xy;
      }
#ifdef useMouseInvert
      vec2 mouse = (iMouse.xy - 2. * iResolution.xy) / iResolution.y;
      if (iMouse.z == 0.)
        mouse = vec2(1.5, 1.5);
      mouse = rot2d(mouse, iTime * 0.1);
      float k = 1. / L2(mouse);
      vec2 invCtr = k * mouse;
      float tt = (k - 1.) / L2(p0 - invCtr);
      p0 = tt * p0 + (1. - tt) * invCtr;
#endif
      vec3 basecol = getcolor(p0, ed);
      col = getColor(camera, rd, pos, nor, lp, basecol);
      col = mix(col, vec3(0.), 1.0 - exp(-0.01 * t * t));
      scenecol += col;
    }
  }
  scenecol /= (float(AA) * float(AA));

  fragColor = vec4(sqrt(max(scenecol, 0.0)), 1.0);
}

// Vinberg convention for edge labels
#define inf 1.0
#define MAX_REFLECTIONS 500
#define AA 2
#define PI 3.141592653
#define L2(x) dot(x, x)
#define L2XY(x, y) L2(x - y)
#define Hyperbolic -1.0
#define Euclidean 0.
#define Spherical 1.
#define MAX_TRACE_STEPS 500
#define MIN_TRACE_DIST 0.001
#define MAX_TRACE_DIST 100.0
#define PRECISION 0.0001
#define EdgeSize1 0.0035
#define EdgeSize2 0.01
#define ColorScale 0.8

// Move a vertex of Euclidean type (if there is any) to infinity,
// so the pattern tiles the entire plane
#define moveVertexToInf

// 4x4 Cartan matrix for the Coxeter group
mat4 cartan;

// geometry type of each vertex in the Coxeter diagram
// -1 for hyerbolic, 0 for Euclidean, 1 for spherial
vec4 vertexType = vec4(1);

// coordinates of a Euclidean vertex
vec2 euclideanVertex;

// if we are outside of the unit circle
// if a point lies outside of unit circle, the distance
// estimation becomes inaccurate. We use EdgeSize2 to control the edge size for
// this case.
bool outside = false;

// if there is an Euclidean vertex, i.e. if remove this vertex, the remain
// diagram is affine
bool hasEuclideanVertex;

// compute cos(PI / x), for x = inf this is just inf
float dihedral(float x) { return x == inf ? inf : cos(PI / x); }

// complex multiplication
vec2 cmul(vec2 p, vec2 q) {
  return vec2(p.x * q.x - p.y * q.y, p.x * q.y + p.y * q.x);
}

// complex division
vec2 cdiv(vec2 z, vec2 w) {
  return vec2(z.x * w.x + z.y * w.y, -z.x * w.y + z.y * w.x) / dot(w, w);
}

// compute the vertex type of each vertex.
// for each vertex in the Coxeter diagram, its vertex type is determined by the
// triangle group G formed by the remaining three vertices. This vertex is of
// hyperbolic/spherical/euclidean iff G is hyperbolic/spherical/euclidean,
// respectively. This can be checked from the determinant of the leading minors
// of the Cartan matrix.
void checkCartan(mat4 M) {
  const float e = 0.001;
  float det;
  det = determinant(mat3(M[1].yzw, M[2].yzw, M[3].yzw));
  vertexType.x = step(-e, det) + step(e, det) - 1.;

  det = determinant(mat3(M[0].xzw, M[2].xzw, M[3].xzw));
  vertexType.y = step(-e, det) + step(e, det) - 1.;

  det = determinant(mat3(M[0].xyw, M[1].xyw, M[3].xyw));
  vertexType.z = step(-e, det) + step(e, det) - 1.;

  det = determinant(mat3(M[0].xyz, M[1].xyz, M[2].xyz));
  vertexType.w = step(-e, det) + step(e, det) - 1.;
}

// For spheres cen is the center, r is the radius
// For planes cen is the normal vector, r is the offset from the origin along
// the normal. if invert is true then the inside/outside of the sphere is
// exchanged.
struct Ball {
  vec2 cen;
  float r;
  bool isplane;
  bool invert;
};

// coclusters are mirror balls, they corresponde to root vectors (space-like)
Ball[4] coclusters;
// clusters are real balls, they corresponde to space-like weight vectors
Ball[4] clusters;

// create a default ball (unit circle)
Ball defaultBall() { return Ball(vec2(0), 1., false, false); }

Ball from_plane(vec2 normal, float offset) {
  return Ball(normal, offset, true, false);
}

Ball from_sphere(vec2 cen, float r) { return Ball(cen, r, false, false); }

void invertBall(inout Ball B) { B.invert = !B.invert; }

// try to reflect a point p to the positive half space bounded by a ball
// if we are already in the positive half space, do nothing and return true,
// else reflect about the ball and return false
// if B is a sphere we try to reflect p into the interior of B
bool try_reflect(inout vec2 p, Ball B, inout int count) {
  vec2 cen = B.cen;
  float r = B.r;
  if (B.isplane) {
    float k = dot(vec3(p, 1), vec3(cen, r));
    if (k >= 0.)
      return true;
    p -= 2. * k * cen;
    count += 1;
    return false;
  } else {
    vec2 q = p - cen;
    float d2 = dot(q, q);
    float k = (r * r) / d2;
    if (k < 1.0)
      return true;
    p = k * q + cen;
    count += 1;
    return false;
  }
}

vec2 getIntersection(Ball B1, Ball B2, Ball B3) {
  vec2 dir = vec2(-B3.cen.y, B3.cen.x);
  float r1 = B1.r, r2 = B2.r;
  float k = (L2(B1.cen) - L2(B2.cen) - (r1 * r1 - r2 * r2)) /
            (2. * dot(B1.cen - B2.cen, dir));
  return k * dir;
}

Ball solveBall(Ball B1, Ball B2, Ball B3) {
  vec2 dir = vec2(-B3.cen.y, B3.cen.x);
  float r1 = B1.r, r2 = B2.r;
  float k = (L2(B1.cen) - L2(B2.cen) - (r1 * r1 - r2 * r2)) /
            (2. * dot(B1.cen - B2.cen, dir));
  vec2 cen = k * dir;
  float r = sqrt(L2XY(cen, B1.cen) - r1 * r1);
  return from_sphere(cen, r);
}

float sdistanceToBall(vec2 p, Ball B) {
  if (B.isplane) {
    float k = dot(vec3(p, 1), vec3(B.cen, B.r));
    return k;
  } else {
    float k = length(p - B.cen) - B.r;
    return B.invert ? -k : k;
  }
}

void initLevel2Graph(in float[6] diagram) {
  Ball B0, B1, B2, B3;
  float c01 = dihedral(diagram[0]);
  float c02 = dihedral(diagram[1]);
  float c03 = dihedral(diagram[2]);
  float c12 = dihedral(diagram[3]);
  float c13 = dihedral(diagram[4]);
  float c23 = dihedral(diagram[5]);

  cartan = mat4(1, -c01, -c02, -c03, -c01, 1, -c12, -c13, -c02, -c12, 1, -c23,
                -c03, -c13, -c23, 1);
  checkCartan(cartan);

  float s23 = sqrt(1. - c23 * c23);

  // The two virtual balls B2, B3 (reflection mirrors) are lines through the
  // origin
  B2 = from_plane(vec2(1, 0), 0.);
  B3 = from_plane(vec2(-c23, s23), 0.);

  // The mirror ball B1(x, y, r) satisfy
  // Angle with B2: dot((x, y), (1, 0)) / r = c12
  // Angle with B3: dot((x, y), (-c23, s23)) / r = c13
  // Orthogonal to the real ball (unit ball) C0: x^2 + y^2 = r^2 + 1
  float k1 = c12;
  float k2 = (c13 + c23 * c12) / s23;
  float r = 1. / sqrt(k1 * k1 + k2 * k2 - 1.);

  B1 = from_sphere(vec2(k1 * r, k2 * r), r);

  // The mirror ball B0(x, y, r) satisfy
  // Angle with B2: dot((x, y), (1, 0)) / r = c02
  // Angle with B3: dot((x, y), (-c23, s23)) / r = c03
  // Angle with B1:
  //
  //        r^2 + r1^2 - |(x,y) - (x1,y1)|^2
  //        --------------------------------  = -c01
  //                   2 * r * r1
  k1 = c02;
  k2 = (c03 + c23 * c02) / s23;

  float a = k1 * k1 + k2 * k2 - 1.;
  float b = dot(vec3(k1, k2, c01), vec3(B1.cen, B1.r));
  float c = L2(B1.cen) - B1.r * B1.r;

  r = b / a - sqrt(b * b - a * c) / a;
  B0 = from_sphere(vec2(k1 * r, k2 * r), r);

  coclusters = Ball[4](B0, B1, B2, B3);

  for (int k = 0; k < 4; k++) {
    clusters[k] = defaultBall();
  }

  invertBall(clusters[0]);

  if (vertexType.y == Hyperbolic) {
    float r = sqrt(L2(B0.cen) - B0.r * B0.r);
    clusters[1] = from_sphere(vec2(0), r);
  }
  if (vertexType.z == Hyperbolic) {
    clusters[2] = solveBall(B0, B1, B3);
  }
  if (vertexType.w == Hyperbolic) {
    clusters[3] = solveBall(B0, B1, B2);
  }

  if (vertexType.y == Euclidean) {
    hasEuclideanVertex = true;
    euclideanVertex = vec2(0);
    return;
  }

  if (vertexType.z == Euclidean) {
    hasEuclideanVertex = true;
    euclideanVertex = getIntersection(B0, B1, B3);
    return;
  }

  if (vertexType.w == Euclidean) {
    hasEuclideanVertex = true;
    euclideanVertex = getIntersection(B0, B1, B2);
    return;
  }
}

vec2 applyMobius(vec2 p) {
  if (hasEuclideanVertex) {
    vec2 A = euclideanVertex;
    vec2 B = vec2(0, 0);
    vec2 C = vec2(1, 0);
    vec2 D = vec2(4, 0);
    p = cdiv(cmul(p, A) + B, cmul(C, p) + D);
  }
  return p;
}

float distanceToMirrors(vec2 p) {
  float d = abs(sdistanceToBall(p, coclusters[0]));

  if (length(p) > 1.) {
    p /= L2(p);
    outside = true;
  }

  for (int k = 1; k < 4; k++) {
    d = min(d, abs(sdistanceToBall(p, coclusters[k])));
  }
  return d;
}

void fold(inout vec2 p, inout int count, inout int index) {
#ifdef moveVertexToInf
  p = applyMobius(p);
#endif
  for (int i = 0; i < MAX_REFLECTIONS; i++) {
    bool cond = true;
    for (int k = 0; k < 4; k++) {
      cond = try_reflect(p, coclusters[k], count) && cond;
    }
    if (cond)
      break;
  }
  for (int k = 0; k < 4; k++) {
    if (vertexType[k] == Hyperbolic &&
        sdistanceToBall(p, clusters[k]) < -0.0001) {
      index = k;
      break;
    }
  }
}

// signed distance to unit ball and plane z=-1
float sdSphere(vec3 p) { return length(p) - 1.0; }
float sdPlane(vec3 p) { return p.z + 1.0; }

// project points on the unit ball to plabe z=-1
vec2 sphereToPlane(vec3 p) { return 2. * p.xy / (1. - p.z); }

// 2d rotation
vec2 rot2d(vec2 p, float a) { return p * cos(a) + vec2(-p.y, p.x) * sin(a); }

// you can implement your color functions here
vec3 colormap(int index, float t) {
  float c = float(index) + 1.;
  return .5 + .45 * cos(2. * PI * pow(t, 0.3) * ColorScale * c + vec3(0, 1, 2));
}

vec3 getcolor(inout vec2 p, out float ed) {
  int index = -1;
  int count = 0;
  fold(p, count, index);
  float dist = distanceToMirrors(p);
  float t = clamp(float(count + 1) / float(MAX_REFLECTIONS), 0., 1.);
  vec3 col = colormap(index, t);
  float es = outside ? EdgeSize2 : EdgeSize1;
  float aa = fwidth(dist) / 2.;
  col = mix(col, vec3(0), 1. - smoothstep(es - aa, es + aa, dist));
  return col;
}

/**
Periodic solutions of the three body problem (equal mass)

Use adaptive RK45 with a fixed number of max iterations

*/

#define get(n, i) texelFetch(iChannel0, ivec2(i, n), 0).xy

const vec3 col[3] = vec3[3](
    vec3(0.1, 0.9, 0.1),
    vec3(0.9, 0.05, 0.05),
    vec3(0.02, 0.02, 0.8));
const vec3 weights = vec3(0.299, 0.587, 0.114);

const float lum[3] = float[3](dot(col[0], weights),
                              dot(col[1], weights),
                              dot(col[2], weights));

const float scales[6] = float[6](2.1, 2.8, 3.4, 2.0, 1.7, 1.9);

const float fade = 0.025;
const float trail_strength = 0.008;
const float particle_strength = 0.04;

vec3 glowPoint(vec2 p, vec2 center, vec3 col, float lum, float strength) {
    float d = max(abs(length(p - center)), 1e-5);
    d = pow(strength / d, 2.);
    d /= lum;
    return 1.0 - exp(-d * col);
}

vec2 sdSegment(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a;
    vec2 ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    float d = length(pa - ba * h);
    d = max(abs(d), 1e-5);
    return vec2(d, h);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    ivec2 grid = ivec2(3, 2);
    ivec2 cell = ivec2(uv * vec2(grid));
    int simIndex = cell.y * grid.x + cell.x;

    vec2 cellCenter = (vec2(cell) + 0.5) / vec2(grid);
    vec2 shift = vec2(0);

    vec2 localUV = (uv - cellCenter) * vec2(grid);
    float aspect =
        (iResolution.x / float(grid.x)) / (iResolution.y / float(grid.y));
    localUV.x *= aspect;

    float zoom = scales[simIndex];
    localUV *= zoom;

    if (simIndex == 4)
        shift = vec2(-0.15, 0.1);
    else if (simIndex == 5)
        shift = vec2(0.1, -0.1);
    localUV -= shift;

    vec3 color = vec3(0.0);
    for (int i = 0; i < min(iFrame, NTRAIL - 2); i++) {
        for (int j = 0; j < 3; j++) {
            int idx = simIndex * 3 + j;
            vec2 a = get(idx, i);
            vec2 b = get(idx, i + 1);
            vec2 seg = sdSegment(localUV, a, b);
            float fade_factor = (seg.y + float(i)) / float(NTRAIL);
            float d = pow(trail_strength / (seg.x + fade * fade_factor), 4.0) /
                      lum[j];
            color += 1.0 - exp(-col[j] * d);
        }
    }

    for (int j = 0; j < 3; j++) {
        int idx = simIndex * 3 + j;
        vec2 end = get(idx, 0);
        color += glowPoint(localUV, end, col[j], lum[j], particle_strength);
    }

    fragColor =
        vec4(pow(clamp(color, 0.0, 1.0), vec3(0.4545)), 1.0);
}

vec2 accel(vec2 pi, vec2 pj) {
    vec2 r = pj - pi;
    float k = dot(r, r);
    float invDist3 = inversesqrt(k * k * k);
    return G * r * invDist3;
}

vec2[3] acceleration(vec2[3] positions) {
    vec2 a[3];
    a[0] =
        accel(positions[0], positions[1]) + accel(positions[0], positions[2]);
    a[1] =
        accel(positions[1], positions[0]) + accel(positions[1], positions[2]);
    a[2] =
        accel(positions[2], positions[0]) + accel(positions[2], positions[1]);
    return a;
}


void RK4(inout vec2[3] p, inout vec2[3] v) {
    const float dt = 0.01;
    vec2[3] k1v = acceleration(p);
    vec2[3] k1p = v;

    vec2[3] p2, v2;
    for (int i = 0; i < 3; i++) {
        p2[i] = p[i] + 0.5 * dt * k1p[i];
        v2[i] = v[i] + 0.5 * dt * k1v[i];
    }
    vec2[3] k2v = acceleration(p2), k2p = v2;

    vec2[3] p3, v3;
    for (int i = 0; i < 3; i++) {
        p3[i] = p[i] + 0.5 * dt * k2p[i];
        v3[i] = v[i] + 0.5 * dt * k2v[i];
    }
    vec2[3] k3v = acceleration(p3), k3p = v3;

    vec2[3] p4, v4;
    for (int i = 0; i < 3; i++) {
        p4[i] = p[i] + dt * k3p[i];
        v4[i] = v[i] + dt * k3v[i];
    }
    vec2[3] k4v = acceleration(p4), k4p = v4;

    for (int i = 0; i < 3; i++) {
        p[i] += dt * (k1p[i] + 2. * k2p[i] + 2. * k3p[i] + k4p[i]) / 6.0;
        v[i] += dt * (k1v[i] + 2. * k2v[i] + 2. * k3v[i] + k4v[i]) / 6.0;
    }
}


// suggested by @rreusser and code taken from his notebook
// https://observablehq.com/@rreusser/magnetic-pendulum#drawField
void RK45(inout vec2[3] p, inout vec2[3] v, inout float dt) {
    const float safety = 0.95;
    const float maxDecrease = 0.2;
    const float maxIncrease = 5.0;
    const float tol2 = 1e-8;

    vec2[3] k1p = v;
    vec2[3] k1v = acceleration(p);

    vec2[3] p2, v2;
    for (int i = 0; i < 3; i++) {
        p2[i] = p[i] + dt * 0.2 * k1p[i];
        v2[i] = v[i] + dt * 0.2 * k1v[i];
    }
    vec2[3] k2p = v2, k2v = acceleration(p2);

    vec2[3] p3, v3;
    for (int i = 0; i < 3; i++) {
        p3[i] = p[i] + dt * (0.075 * k1p[i] + 0.225 * k2p[i]);
        v3[i] = v[i] + dt * (0.075 * k1v[i] + 0.225 * k2v[i]);
    }
    vec2[3] k3p = v3, k3v = acceleration(p3);

    vec2[3] p4, v4;
    for (int i = 0; i < 3; i++) {
        p4[i] = p[i] + dt * (0.3 * k1p[i] - 0.9 * k2p[i] + 1.2 * k3p[i]);
        v4[i] = v[i] + dt * (0.3 * k1v[i] - 0.9 * k2v[i] + 1.2 * k3v[i]);
    }
    vec2[3] k4p = v4, k4v = acceleration(p4);

    vec2[3] p5, v5;
    for (int i = 0; i < 3; i++) {
        p5[i] = p[i] + dt * (-0.2037037 * k1p[i] + 2.5 * k2p[i] - 2.5925926 * k3p[i] + 1.2962963 * k4p[i]);
        v5[i] = v[i] + dt * (-0.2037037 * k1v[i] + 2.5 * k2v[i] - 2.5925926 * k3v[i] + 1.2962963 * k4v[i]);
    }
    vec2[3] k5p = v5, k5v = acceleration(p5);

    vec2[3] p6, v6;
    for (int i = 0; i < 3; i++) {
        p6[i] = p[i] + dt * (0.0294958 * k1p[i] + 0.3417969 * k2p[i] + 0.0415943 * k3p[i] + 0.4003454 * k4p[i] + 0.0617676 * k5p[i]);
        v6[i] = v[i] + dt * (0.0294958 * k1v[i] + 0.3417969 * k2v[i] + 0.0415943 * k3v[i] + 0.4003454 * k4v[i] + 0.0617676 * k5v[i]);
    }
    vec2[3] k6p = v6, k6v = acceleration(p6);
    float err2 = 0.0;
    for (int i = 0; i < 3; i++) {
        vec2 err = dt * (
            0.00429377 * k1p[i]
            - 0.0186686 * k3p[i]
            + 0.0341550 * k4p[i]
            + 0.0193220 * k5p[i]
            - 0.0391022 * k6p[i]
        );
        err2 += dot(err, err);
    }

    bool accept = err2 <= tol2;

    if (accept) {
        for (int i = 0; i < 3; i++) {
            p[i] += dt * (0.0978836 * k1p[i] + 0.4025765 * k3p[i] + 0.2104377 * k4p[i] + 0.2891022 * k6p[i]);
            v[i] += dt * (0.0978836 * k1v[i] + 0.4025765 * k3v[i] + 0.2104377 * k4v[i] + 0.2891022 * k6v[i]);
        }
    }
    float scale = clamp(safety * pow(tol2 / (err2 + 1e-12), accept ? 0.125 : 0.1), maxDecrease, maxIncrease);
    dt *= scale;
}


void adaptiveRK45(inout vec2[3] p, inout vec2[3] v) {
    float t = 0.0;
    float totalDt = 0.01;
    float dt = totalDt / float(MaxIterEachStep);

    for (int i = 0; i < MaxIterEachStep; i++) {
        if (t + dt > totalDt) {
            dt = totalDt - t;
        }

        vec2[3] pTemp = p;
        vec2[3] vTemp = v;
        float dtTemp = dt;

        RK45(pTemp, vTemp, dtTemp);

        if (dtTemp >= dt * 0.99) {
            p = pTemp;
            v = vTemp;
            t += dt;
        }

        dt = dtTemp;

        if (t >= totalDt - 1e-6) break;
    }
}


void initState(int idx, out vec2[3] p, out vec2[3] v) {
    if (idx == 0) {
        p = vec2[3](vec2(-1, 0), vec2(1, 0), vec2(0, 0));
        v = vec2[3](vec2(0.347111, 0.532728), vec2(0.347111, 0.532728),
                    vec2(-2. * 0.347111, -2. * 0.532728));
    } else if (idx == 1) {
        p = vec2[3](vec2(0.2843198916, 0), vec2(0.8736097872, 0),
                    vec2(-1.1579296788, 0));
        v = vec2[3](vec2(0, 1.3774179570), vec2(0, -0.4884226932),
                    vec2(0, -0.8889952638));
    } else if (idx == 2) {
        p = vec2[3](vec2(0.0132604844, 0), vec2(1.4157286016, 0),
                    vec2(-1.4289890859, 0));
        v = vec2[3](vec2(0, 1.054151921), vec2(0, -0.2101466639),
                    vec2(0, -0.8440052572));

    } else if (idx == 3) {
        p = vec2[3](vec2(0.8733047091, 0), vec2(-0.6254030288, 0),
                    vec2(-0.2479016803, 0));
        v = vec2[3](vec2(0, 1.0107764436), vec2(0, -1.6833533458),
                    vec2(0, 0.6725769022));

    } else if (idx == 4) {
        p = vec2[3](vec2(0.6661637520772179, -0.081921852656887),
                    vec2(-0.025192663684493022, 0.45444857588251897),
                    vec2(-0.10301329374224, -0.765806200083609));
        v = vec2[3](vec2(0.84120297540307, 0.029746212757039),
                    vec2(0.142642469612081, -0.492315648524683),
                    vec2(-0.98384544501151, 0.462569435774018));
    } else if (idx == 5) {

        p = vec2[3](vec2(0.486657678894505, 0.755041888583519),
                    vec2(-0.681737994414464, 0.29366023319721),
