    } else {
        p = vec2[3](vec2(0.5, 0), vec2(-0.5, 0), vec2(0, 0));
        v = vec2[3](vec2(0, 0.3), vec2(0, 0.3), vec2(0, -0.6));
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 uv = ivec2(fragCoord);
    int objIdx = uv.y / 3;
    int localIdx = uv.y % 3;

    vec2[3] p, v;

    if (uv.x == 0 && uv.y < 18) {
        if (iFrame == 0) {
            initState(objIdx, p, v);
        } else {
            for (int i = 0; i < 3; i++) {
                vec4 data = texelFetch(iChannel0, ivec2(0, objIdx * 3 + i), 0);
                p[i] = data.xy;
                v[i] = data.zw;
            }
            #ifdef USE_ADAPTIVE_RK45
                adaptiveRK45(p, v);
            #else
                RK4(p,v);
            #endif
        }
        fragColor = vec4(p[localIdx], v[localIdx]);
    } else if (uv.x > 0 && uv.x < NTRAIL && uv.y < 18) {
        vec4 prev = texelFetch(iChannel0, ivec2(uv.x - 1, uv.y), 0);
        fragColor = vec4(prev.xy, 0.0, 1.0);
    } else {
        fragColor = vec4(0.0);
    }
}

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001

// --- SDFï¼æåè·ç¦»å½æ°ï¼å®ä¹ ---

float sdSphere(vec3 p, float r) { return length(p) - r; }
float sdPlane(vec3 p) { return p.z; }

// --- åºæ¯æå»º ---
// mapå½æ°è¿åæè¿ç©ä½çè·ç¦»åID (1.0: Sphere, 2.0: Plane)
vec2 map(vec3 p) {
    float sphereDist = sdSphere(p, 1.0);
    float planeDist = sdPlane(p);

    if (sphereDist < planeDist) {
        return vec2(sphereDist, 1.0);
    } else {
        return vec2(planeDist, 2.0);
    }
}

// --- æ ¸å¿ç®æ³ ---

// Raymarching (åçº¿æ­¥è¿)
vec2 raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + t * rd;
        vec2 res = map(p);
        if (res.x < SURF_DIST) return vec2(t, res.y);
        t += res.x;
        if (t > MAX_DIST) break;
    }
    return vec2(MAX_DIST, 0.0);
}

// è®¡ç®è¡¨é¢æ³çº¿
vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    float d = map(p).x;
    vec3 n = d - vec3(map(p - e.xyy).x, map(p - e.yxy).x, map(p - e.yyx).x);
    return normalize(n);
}

// è®¡ç®è½¯é´å½±
float getSoftShadow(vec3 ro, vec3 rd, float k) {
    float res = 1.0;
    float t = 0.01;
    for (int i = 0; i < 50; i++) {
        float h = map(ro + rd * t).x;
        if (h < SURF_DIST) return 0.0;
        res = min(res, k * h / t);
        t += h;
    }
    return clamp(res, 0.0, 1.0);
}

// è®¡ç®ç¯å¢åé®è½ (AO)
float getAO(vec3 p, vec3 nor) {
    float occ = 0.0, sca = 1.0;
    for(int i = 0; i < 5; i++) {
        float hr = 0.01 + 0.12 * float(i) / 4.0;
        vec3 aopos = p + nor * hr;
        float dd = map(aopos).x;
        occ += -(dd - hr) * sca;
        sca *= 0.75;
    }
    return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

// --- æ¸²æä¸çè² ---

// å°è£ä¸ä¸ªå½æ°æ¥åç¬æ¸²æå¹³é¢ï¼æ¹ä¾¿å¤ç¨
vec3 shadePlane(vec3 p, vec3 normal, vec3 lightDir, float ao, float shadow) {
    // æ£çæ ¼çº¹ç
    vec2 uv = p.xy * 2.0;
    float c = mod(floor(uv.x) + floor(uv.y), 2.0);
    vec3 baseColor = vec3(0.4 + c * 0.1);

    // æ¼«åå°
    float diffuse = max(0.0, dot(normal, lightDir));

    // ç»ååç§
    return baseColor * (diffuse * 0.6 + 0.4) * shadow * ao;
}


// ä¸»æ¸²æå½æ°
vec3 render(vec3 ro, vec3 rd) {
    // 1. ç¬¬ä¸æ¬¡åçº¿æ­¥è¿
    vec2 hit1 = raymarch(ro, rd);
    float t1 = hit1.x;
    float materialID1 = hit1.y;

    // å¦ææªå½ä¸­ä»»ä½ç©ä½ï¼è¿åèæ¯è²
    if (t1 > MAX_DIST - 1.0) return vec3(0.1, 0.1, 0.2);

    // 2. è®¡ç®ç¬¬ä¸ä¸ªäº¤ç¹çä¿¡æ¯
    vec3 p1 = ro + t1 * rd;
    vec3 n1 = getNormal(p1);

    // 3. è®¾ç½®åç§
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec3 lightPos = vec3(4.0 * (mouse.x - 0.5) * 2.0, 2.0 * mouse.y * 2.0, 4.0);
    if(iMouse.z <= 0.0) lightPos = vec3(2.0, 3.0, 4.0);
    vec3 lightDir = normalize(lightPos - p1);

    // è®¡ç®åç§ææ
    float ao = getAO(p1, n1);
    float shadow = getSoftShadow(p1, lightDir, 16.0);

    // 4. æ ¹æ®æè´¨IDè¿è¡çè²
    vec3 finalColor = vec3(0.0);

    if (materialID1 == 2.0) { // å¦æç´æ¥å»ä¸­äºå¹³é¢
        finalColor = shadePlane(p1, n1, lightDir, ao, shadow);
    } 
    else if (materialID1 == 1.0) { // å¦æå»ä¸­äºçä½ (æ ¸å¿éæé»è¾)

        // a. è®¡ç®çä½è¡¨é¢çé¢è²è´¡ç® (æ¼«åå°+é«å)
        vec3 sphereBaseColor = vec3(0.2, 0.5, 0.9); // èè²
        float diffuse = max(0.0, dot(n1, lightDir));

        vec3 viewDir = -rd;
        vec3 halfVec = normalize(lightDir + viewDir);
        float spec = pow(max(0.0, dot(n1, halfVec)), 64.0);

        vec3 sphereSurfaceColor = (sphereBaseColor * diffuse + vec3(1.0) * spec) * shadow * ao;

        // b. è®¡ç®éè¿çä½çå°çèæ¯é¢è²
        // ä»ç¬¬ä¸ä¸ªäº¤ç¹p1å¤ç»§ç»­æ­¥è¿
        vec3 ro2 = p1 + rd * SURF_DIST * 2.0; // å°èµ·ç¹ååæ¨ä¸ç¹ï¼é²æ­¢èªäº¤
        vec2 hit2 = raymarch(ro2, rd);

        vec3 backgroundColor;
        if(hit2.x < MAX_DIST - 1.0) {
            // å¦æå»ä¸­äºåé¢çç©ä½ï¼å¹³é¢ï¼
            vec3 p2 = ro2 + hit2.x * rd;
            vec3 n2 = getNormal(p2);
            vec3 lightDir2 = normalize(lightPos-p2);
            float ao2 = getAO(p2, n2);
            // æ³¨æï¼è¿éçé´å½±è®¡ç®åºè¯¥ä»p2ç¹å¼å§ï¼ä½ä¸ºäºæ§è½å¯ä»¥ç®å
            float shadow2 = getSoftShadow(p2, lightDir2, 16.0);
            backgroundColor = shadePlane(p2, n2, lightDir2, ao2, shadow2);
        } else {
            // å¦æåé¢æ²¡æç©ä½ï¼åçå°çæ¯å¤©ç©ºèæ¯è²
            backgroundColor = vec3(0.1, 0.1, 0.2);
        }

        // c. æ ¹æ®è²æ¶å°æåºæ··ååæ¯åèæ¯
        // è²æ¶å°æåºï¼è§çº¿ä¸æ³çº¿è¶åç´ï¼åå°/ä¸éæåº¦è¶é«
        float fresnel = pow(1.0 + dot(rd, n1), 4.0);

        // å®ä¹åºç¡ä¸éæåº¦ï¼å¹¶ç¨è²æ¶å°æåºè¿è¡è°å¶
        float baseOpacity = 0.2;
        float finalOpacity = baseOpacity + (1.0 - baseOpacity) * fresnel;
        finalOpacity = clamp(finalOpacity, 0.0, 1.0);

        // ä½¿ç¨mixå½æ°æ··å
        // mix(background, foreground, alpha) = background * (1-alpha) + foreground * alpha
        finalColor = mix(backgroundColor, sphereSurfaceColor, finalOpacity);
    }

    return finalColor;
}

// --- ä¸»å½æ° ---
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0.0, 2.5, 3.5); // æåæºä½ç½®
    vec3 lookAt = vec3(0.0, 0.0, 0.0); // è§å¯ç®æ 

    vec3 fwd = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 0.0, 1.0), fwd));
    vec3 up = cross(fwd, right);

    vec3 rd = normalize(fwd + uv.x * right + uv.y * up);

    vec3 col = render(ro, rd);

    col = pow(col, vec3(0.4545)); // Gammaæ ¡æ­£
    fragColor = vec4(col, 1.0);
}

/*#define MAX_STEPS 100         // æå¤§æ­¥è¿æ¬¡æ°
