float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + t * rd;
        float dist = map(p).x;
        if (dist < SURF_DIST) {
            return t;
        }
        t += dist;
        if (t > MAX_DIST) {
