#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001

const float vertexRadius  = 0.035;
const float edgeThickness = 0.012;

// --- Constants & Basis Vectors ---
#define PI 3.14159265359
#define PHI 1.61803398875 // The Golden Ratio

// The 6 projection vectors from 6D to 3D.
const vec3 K[6] = vec3[](
    normalize(vec3( PHI, 1.0, 0.0)),
    normalize(vec3(-PHI, 1.0, 0.0)),
    normalize(vec3( 1.0, 0.0, PHI)),
    normalize(vec3(-1.0, 0.0, PHI)),
    normalize(vec3( 0.0, PHI, 1.0)),
    normalize(vec3( 0.0,-PHI, 1.0))
);

// --- Main Distance Function (map) ---
float map(vec3 p) {
    // A fixed 6D offset ("phase"). CRITICAL: The values must be "generic"
    // to avoid rational approximants. Simple fractions (like 1/6) or highly
    // symmetric values will cause the structure to degenerate into a
    // periodic crystal. These "boring" numbers ensure a true quasicrystal.
    const float phase[6] = float[6](0.123, 0.456, 0.789, 0.321, 0.654, 0.987);

    // 1. Lift the 3D point p to its 6 coordinates in the 6D space.
    float n[6];
    for(int i=0; i<6; ++i) {
        n[i] = dot(p, K[i]) + phase[i];
    }

    // 2. Find the distance to the center of the containing hyper-cubic cell.
    float d[6];
    for (int i=0; i<6; ++i) {
        d[i] = abs(n[i] - round(n[i]));
    }

    // 3. Sort the distances to find the three smallest: d1, d2, d3.
    // A simple, unrolled bubble sort is clear and efficient enough.
    for (int i=0; i<5; ++i) {
        for (int j=i+1; j<6; ++j) {
            if(d[i] > d[j]) {
                float temp = d[i];
                d[i] = d[j];
                d[j] = temp;
            }
        }
    }
    float d1 = d[0], d2 = d[1], d3 = d[2];

    // 4. The SDF is the distance to the nearest vertex or edge locus.
    float vertDist = sqrt(d1*d1 + d2*d2 + d3*d3) - vertexRadius;
    float edgeDist = sqrt(d1*d1 + d2*d2) - edgeThickness;

    return min(vertDist, edgeDist);
}

// --- Standard Raymarching Boilerplate ---
vec3 getNormal(vec3 p) {
    vec2 e = vec2(SURF_DIST*0.5, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

mat3 setCamera(vec3 ro, vec3 ta) {
    vec3 cw = normalize(ta-ro);
    vec3 cu = normalize(cross(cw, vec3(0,1,0)));
    vec3 cv = cross(cu,cw);
    return mat3(cu, cv, cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // --- Camera ---
    float time = iTime * 0.25;
    float dist = 5.0;

    vec2 mouse = iMouse.xy/iResolution.xy;
    float rot_y = -PI, rot_x = 0.4;
    if(iMouse.z > 0.0) {
        rot_y = (mouse.x - 0.5) * 2.0 * PI;
        rot_x = (mouse.y - 0.5) * PI;
    } else {
        rot_y += time;
    }

    vec3 ro = vec3(sin(rot_y)*cos(rot_x), sin(rot_x), cos(rot_y)*cos(rot_x)) * dist;
    vec3 ta = vec3(0.0);
    mat3 ca = setCamera(ro, ta);
    vec3 rd = ca * normalize(vec3(uv.xy, 2.0)); // Zoom

    // --- Raymarching ---
    float t = 0.0;
    vec3 bcol = vec3(0.1, 0.15, 0.2);
    vec3 col = bcol;

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 pos = ro + t*rd;
        float h = map(pos);
        if(abs(h) < SURF_DIST*t || t > MAX_DIST) {
            if (h < MAX_DIST) t += h;
            break;
        }
        t += h * 0.8;
    }

    if(t < MAX_DIST) {
        vec3 pos = ro + t*rd;
        vec3 nor = getNormal(pos);
        vec3 lightDir = normalize(vec3(0.8, 0.9, 0.3));

        float dif = clamp(dot(nor, lightDir), 0.0, 1.0);
        float rim = pow(1.0 - clamp(dot(nor, -rd), 0.0, 1.0), 2.0);

        col = vec3(1.0, 0.7, 0.4) * dif + bcol * 0.8 + vec3(1.0,0.9,0.8)*rim*0.4;
        col = mix(col, bcol, 1.0 - exp(-0.01*t*t)); // Fog
    }

    col = pow(col, vec3(0.4545));
    fragColor = vec4(col,1.0);
}

vec3 postProcess(in vec3 col, in vec2 q) {
    col = pow(clamp(col, 0.0, 1.0), vec3(1.0 / 2.2));
    col = col * 0.6 + 0.4 * col * col * (3.0 - 2.0 * col); // contrast
    col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);      // satuation
    col *= 0.5 + 0.5 * pow(19.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y),
                           0.7); // vigneting
    return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 tex = texelFetch(iChannel0, ivec2(fragCoord), 0);
    vec3 c = tex.rgb / tex.a;
    c = postProcess(c, uv);
    fragColor = vec4(c, 1.0);
}

#define PI 3.141592654
#define FAR 1e30
#define MAX_TRACE_STEPS 500
#define FOV 1.0
#define MAX_BOUNCES 1
#define MAX_ITER 40

// ===========
// !Important:
// ===========
// Due to the way we construct the mirrors, the first three mirrors must have
// spherical type, so for paracompact honeycombs like (4, 4, x), (6, 3, x), (3,
// 6, x), you need to swap the order of the last two mirrors, i.e. use (4, 2, 2,
// 2, 4, 4) or (6, 2, 2, 2, 3, x) in the Coxeter diagram. Suggested by @mla. For
// diagram = (a, b, c, d, e, f) the corresponding Coxeter matrix is
//
// | 1  a  b  c |
// | a  1, d  e |
// | b  d  1  f |
// | c  e  f  1 |
//
const float[6] diagram = float[6](5., 2., 2., 3., 2., 4.);

// active mirrors, 1 for active, 0 for non-active. This determines the position
// of the initial vertex hence the truncation type of the honeycomb
const vec4 active_mirrors = vec4(1, 0, 0, 0);

// size of the vertex and segments
// you may need to adjust these for different scenes
const float vradius = 0.14;
const float sradius = 0.05;

const vec3 vertexColor = vec3(0.71, 0.65, 0.26);
const vec3 edgeColors[4] =
    vec3[4](vec3(0.74, 0.62, 0.56), vec3(0.90, 0.91, 0.98),
            vec3(0.72, 0.45, 0.2), vec3(0.32, 0.79, 0.36));

float seed;

float hash() {
    float p = fract((seed++) * 0.1031);
    p += (p * (p + 19.19)) * 3.;
    return fract((p + p) * p);
}

vec2 hash2() { return vec2(hash(), hash()); }

// normal vectors of the four mirrors
mat4 M;

// the initial vertex
vec4 v0;

// 2d rotation
vec2 rot2d(vec2 p, float a) { return cos(a) * p + sin(a) * vec2(p.y, -p.x); }

// Minkowski inner product with Sylvester type (3, 1)
float hdot(in vec4 p, in vec4 q) { return dot(p.xyz, q.xyz) - p.w * q.w; }

// normalize a time-like vector <v, v> < 0
vec4 normalize_t(vec4 v) { return v / sqrt(-hdot(v, v)); }
// normalize a space-like vector <v, v> > 0
vec4 normalize_s(vec4 v) { return v / sqrt(hdot(v, v)); }

float arccosh(float x) { return log(x + sqrt(x * x - 1.0)); }

// reflection about a plane with normal `n`
float href(inout vec4 p, in vec4 n) {
    float k = min(0., hdot(p, n));
    p -= 2. * k * n;
    return k;
}

vec4 reflect_hyp(vec4 p, vec4 n) { return p - 2. * hdot(p, n) * n; }

float hyp_distance(vec4 p, vec4 q) { return arccosh(-hdot(p, q)); }

// Given the Coxeter diagram and active mirrors (on/off),
// get the normals of the reflection mirrors and the initial vertex.
// Warning: due to the procedure used here, the first three mirrors must
// have "spherical" type.
void init(in float[6] diagram, in vec4 active_mirrors, inout mat4 M,
          inout vec4 v0) {
    // Cartan matrix of the Coxeter group:
    // ( -1  c01  c02  c03)
    // (c01   -1  c12  c13)
    // (c02  c12   -1  c23)
    // (c03  c13  c23   -1)
    // cij = -cos(PI / (Coxeter label bewteen i-th and j-th mirror))
    float c01 = -cos(PI / diagram[0]);
    float c02 = -cos(PI / diagram[1]);
    float c03 = -cos(PI / diagram[2]);
    float c12 = -cos(PI / diagram[3]);
    float c13 = -cos(PI / diagram[4]);
    float c23 = -cos(PI / diagram[5]);

    // find the reflection mirrors A, B, C, D.
    // A can be always chosen as x-axis
    vec4 A, B, C, D;
    A = vec4(1, 0, 0, 0);

    B = vec4(c01, sqrt(1. - c01 * c01), 0., 0.);

    C = vec4(c02, 0, 0, 0);
    C.y = (c12 - C.x * B.x) / B.y;
    C.z = sqrt(
        abs(1. - dot(C.xy, C.xy))); // avoid rounding error in paracompact case

    D = vec4(c03, 0, 0, 0);
    D.y = (c13 - D.x * B.x) / B.y;
    D.z = (c23 - dot(D.xy, C.xy)) / C.z;
    // !important: if you want to make the fundamental chamber lie in the upper
    // sheet of the hyperboloid then you must use "-" sign here.
    D.w = -sqrt(abs(dot(D.xyz, D.xyz) - 1.));

    vec4 H = vec4(1, 1, 1, -1);
    v0 =
        normalize_t(active_mirrors * inverse(mat4(H * A, H * B, H * C, H * D)));
    M = mat4(A, B, C, D);
}

// fold a point p in 4d up to a given maximum of steps, return true if
// we successfully transformed p into the fundamental domain else return false.
bool fold(inout vec4 p, mat4 M) {
    float k;
    for (int i = 0; i < MAX_ITER; i++) {
        k = 0.;
        p.x = abs(p.x);
        k += href(p, M[1]);
        k += href(p, M[2]);
        k += href(p, M[3]);
        if (k == 0.)
            return true;
    }
    return false;
}

// vec3 environment(vec3 dir) {
//     dir = normalize(dir);
//     float t = 0.5 * (dir.y + 1.0);
//     vec3 bg = mix(vec3(0.2, 0.3, 0.5), vec3(0.8, 0.9, 1.0), t);
//     return bg * 0.33;
// }

vec2 map_hyp(vec4 q) {
    vec4 p = q; // Use a copy to fold
    bool found = fold(p, M);

    if (found) {
        float id;

        // Distance to the central vertex (hyperbolic)
        // d = d(p, v0) - radius
        float d_vert = hyp_distance(p, v0) - vradius;

        // Distance to the 4 base edges (hyperbolic)
        float d_edge = FAR; // A large number
        for (int i = 0; i < 4; i++) {
            vec4 n = M[i];
            // Project p onto the geodesic defined by v0 and n
            float pn = hdot(p, n);
            float pv = hdot(p, v0);
            float nv = hdot(n, v0);
            float det = -1.0 - nv * nv;
            float a = (-nv * pv - pn) / det;
            float b = (pv - pn * nv) / det;
            vec4 pj = normalize_t(min(a, 0.) * n + b * v0);

            // d = d(p, pj) - radius
            float d_seg = hyp_distance(p, pj) - sradius;

            if (d_seg < d_edge) {
                d_edge = d_seg;
                id = float(i);
            }
        }

        if (d_vert < d_edge) {
            id = 4.0;
            return vec2(d_vert, id);
        }
        return vec2(d_edge, id);
    }
    return vec2(FAR, -1.0);
}

vec2 trace_hyp(vec4 ro_hyp, vec4 rd_hyp) {
    float t = 0.0; // Total hyperbolic distance traveled
    const float eps = 1e-4;

    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        // Current point on the geodesic
        vec4 p = ro_hyp * cosh(t) + rd_hyp * sinh(t);
        vec2 res = map_hyp(p);
        float d = res.x;
        if (d < eps) {
            return vec2(t, res.y);
        }
        t += d;
        if (t > FAR) {
            break;
        }
    }
    return vec2(FAR, -1.0);
}


vec4 calcNormal_hyp(vec4 p) {
    float inv_w1 = 1.0 / (1.0 + p.w);
    // Orthonormal basis in the tangent space at p
    vec4 Bx = vec4(1.0 + p.x * p.x * inv_w1, p.x * p.y * inv_w1,
                   p.x * p.z * inv_w1, p.x);
    vec4 By = vec4(p.y * p.x * inv_w1, 1.0 + p.y * p.y * inv_w1,
                   p.y * p.z * inv_w1, p.y);
    vec4 Bz = vec4(p.z * p.x * inv_w1, p.z * p.y * inv_w1,
                   1.0 + p.z * p.z * inv_w1, p.z);
    const float eps = 5e-4; // Small step for finite differences

    // Sample the SDF in 6 directions around p
    float dx = map_hyp(normalize_t(p + eps * Bx)).x -
               map_hyp(normalize_t(p - eps * Bx)).x;
    float dy = map_hyp(normalize_t(p + eps * By)).x -
               map_hyp(normalize_t(p - eps * By)).x;
    float dz = map_hyp(normalize_t(p + eps * Bz)).x -
               map_hyp(normalize_t(p - eps * Bz)).x;
    // The gradient is a linear combination of the basis vectors
    return normalize_s(dx * Bx + dy * By + dz * Bz);
}

float softShadow_hyp(vec4 ro_hyp, vec4 lp_hyp, float t_max, float k) {
    float t = 1e-4; // Start a bit away from the surface
    float res = 1.0;
    // Calculate the direction vector from surface to light in tangent space
    vec4 rd_hyp = normalize_s(lp_hyp + hdot(lp_hyp, ro_hyp) * ro_hyp);

    for (int i = 0; i < 24; i++) {
        // Current point on the geodesic towards the light
        vec4 p = ro_hyp * cosh(t) + rd_hyp * sinh(t);
        float h = map_hyp(p).x;
        res = min(res, smoothstep(0.0, 1.0,
                                  k * h / t)); // Use sinh(t) for penumbra

        t += clamp(h, 0.01, .1);
        if (h < 1e-4 || t > t_max)
            break;
    }
    return clamp(res, 0.0, 1.0);
}

float calcAO_hyp(vec4 p, vec4 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.001 + float(i) / 10.; // Hyperbolic step distance
        // Point stepped along the normal direction
        vec4 p_occ = p * cosh(h) + n * sinh(h);
        float d = map_hyp(p_occ).x;
        occ += (h - d) * sca;
        sca *= 0.9;
    }
    return clamp(1.0 - occ, 0.0, 1.0);
}

vec3 lighting_hyp(vec4 pos, vec4 nor, vec4 view_dir, vec4 light_pos,
                  vec3 basecol) {
    vec3 col = vec3(0.0);

    // Calculate light direction vector IN THE TANGENT SPACE at the surface
    // point 'pos'
    float ldist = hyp_distance(pos, light_pos);
    vec4 ld = normalize_s(light_pos + hdot(light_pos, pos) * pos);

    // AO and Shadow are already hyperbolic
    float ao = calcAO_hyp(pos, nor);
    float sh = softShadow_hyp(pos, light_pos, ldist, 32.0);

    // Diffuse term using hyperbolic dot product
    float diff = clamp(hdot(nor, ld), 0., 1.);

    // Attenuation based on true hyperbolic distance
    float atten = 1.0 / pow(clamp(sinh(ldist), 0.001, 1.), 2.);

    // Specular term
    // Reflection of light direction about the normal in hyperbolic space
    vec4 reflect_dir = normalize_s(reflect_hyp(ld, nor));
    float spec = pow(max(hdot(reflect_dir, -view_dir), 0.0), 32.0);

    col += basecol * diff;
    col += basecol * spec * 6.;
    //col *= ao * atten * sh;
    col *= sh;
    return col;
}

vec4 R3_to_hyp(vec3 p) {
    float r2 = dot(p, p);
    return vec4(2. * p, 1. + r2) / (1. - r2);
}

vec3 render(vec3 ro, vec3 rd, vec3 lp_euc) {
    vec3 col = vec3(0);
    vec3 throughput = vec3(1);

    // Initial ray setup in Hyperboloid model
    vec4 ro_hyp = R3_to_hyp(ro);
    vec4 rd_hyp = vec4(rd, 0.);

    vec4 lp_hyp = R3_to_hyp(lp_euc);

    for (int i = 0; i < MAX_BOUNCES + 1; i++) {
        vec2 res = trace_hyp(ro_hyp, rd_hyp);
        float t = res.x;
        float id = res.y;

        if (id < 0.0) {
            break;
        }
        vec4 pos_hyp = ro_hyp * cosh(t) + rd_hyp * sinh(t);
        vec4 normal_hyp = calcNormal_hyp(pos_hyp);
        vec4 view_dir_hyp = -(ro_hyp * sinh(t) + rd_hyp * cosh(t));
        vec3 basecol;
        if (id < 4.)
            basecol = edgeColors[int(id)];
        else
            basecol = vertexColor;
        vec3 directLight =
            lighting_hyp(pos_hyp, normal_hyp, view_dir_hyp, lp_hyp, basecol);
        col += throughput * directLight;

        throughput *= basecol * 0.5;
        if (max(throughput.r, max(throughput.g, throughput.b)) < 0.05)
            break;

        // Reflection is the same as before
        rd_hyp = normalize_s(reflect_hyp(-view_dir_hyp, normal_hyp));
        float eps = 1e-3;
        ro_hyp = normalize_t(pos_hyp * cosh(eps) + normal_hyp * sinh(eps));
    }
    return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    init(diagram, active_mirrors, M, v0);
    vec4 data = texelFetch(iChannel0, ivec2(fragCoord), 0);

    seed = float((iFrame * 73856093) % 38069);

    vec3 ro = vec3(0, 0, 0);
    vec3 up = vec3(0, 1, 0);
    vec3 lookat = vec3(0, 0, -1);
    vec3 forward = normalize(lookat - ro);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));

    vec3 lp = vec3(.3, 0.2, 0.1);

    vec2 jittered = fragCoord + hash2() - 0.5;
    vec2 uv = (2. * jittered - iResolution.xy) / iResolution.y;

    vec3 rd = normalize(forward * FOV + uv.x * right + uv.y * up);
    vec3 col = render(ro, rd, lp);
    col = clamp(col, 0., 1.);

    if (isinf(col.x) || isinf(col.y) || isinf(col.z) || isnan(col.x) ||
        isnan(col.y) || isnan(col.z)) {
        col = vec3(0.0);
    } else
        data += vec4(col, 1.);

    if (iMouse.z > 0.) {
        data *= 0.0;
    }
    fragColor = data;
}

#define MAX_STEPS 100       // æå¤§æ­¥è¿æ¬¡æ°
#define MAX_DIST 100.0      // æå¤§è¿½è¸ªè·ç¦»
#define HIT_PRECISION 0.001 // å½ä¸­ç²¾åº¦

#define ROOM_SIZE 4.0 // æ¿é´çåè¾¹é¿ (ä»åç¹å°å¢å£çè·ç¦»)

// ä¹é«å°äººé¢è²
#define COLOR_HEAD   vec3(0.9, 0.8, 0.2) // é»è²
#define COLOR_TORSO  vec3(0.8, 0.1, 0.1) // çº¢è²
#define COLOR_LEGS   vec3(0.1, 0.2, 0.8) // èè²
#define COLOR_FLOOR1 vec3(0.8)
#define COLOR_FLOOR2 vec3(0.2)

// --- SDF åºç¡å ä½ä½ (SDF Primitives) ---
// æ¥èª Inigo Quilez çç½ç« (iq.com/articles/distfunctions)

float sdSphere(vec3 p, float s) {
    return length(p) - s;
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdCylinder(vec3 p, float h, float r) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

// --- SDF è¿ç®ç¬¦ (SDF Operators) ---
float opU(float d1, float d2) { return min(d1, d2); }


// --- ä¹é«å°äºº SDF (LEGO Minifigure SDF) ---
// è¿åä¸ä¸ª vec2: .x æ¯è·ç¦», .y æ¯æè´¨ID (1=å¤´, 2=èº«ä½, 3=è¿)
vec2 sdfLego(vec3 p) {
    // æ´ä½å±ä¸­åä¸ç§»å¨
    p.y -= 0.95;

    // 1. è¿ (Legs) - ID 3.0
    vec3 legSize = vec3(0.2, 0.4, 0.25);
    vec3 legP = p;
    legP.x = abs(legP.x) - 0.22; // ä¸¤æ¡è¿åå¼
    float legs = sdBox(legP - vec3(0, 0.4, 0), legSize);
    vec2 res = vec2(legs, 3.0);

    // 2. èº«ä½/èº¯å¹² (Torso) - ID 2.0
    vec3 torsoSize = vec3(0.45, 0.55, 0.25);
    float torso = sdBox(p - vec3(0, 1.05, 0), torsoSize);
    if (torso < res.x) res = vec2(torso, 2.0);

    // 3. å¤´é¨ (Head) - ID 1.0
    float head = sdCylinder(p - vec3(0.0, 1.8, 0.0), 0.35, 0.3);
    // å¤´é¡¶çå¸èµ·
    float headStud = sdCylinder(p - vec3(0.0, 2.2, 0.0), 0.05, 0.15);
    head = opU(head, headStud);
    if (head < res.x) res = vec2(head, 1.0);

    return res;
}


// --- æ´ä½åºæ¯ SDF (Main Scene SDF) ---
// è¿åä¸ä¸ª vec2: .x æ¯è·ç¦», .y æ¯æè´¨ID (0=å°é¢, 1,2,3=ä¹é«)
vec2 map(vec3 p) {
    // 1. æ ééé¢ææ (æ ¸å¿)
    // éè¿ "ç©ºé´æå " å°æ éç©ºé´æ å°åä¸­å¿æ¿é´
    // è¿æ ·ï¼åçº¿å¨é»è¾ä¸ç©¿è¿äºå¢å£ï¼ä½å ä½ä½å´è¢«éåå°éå¤åºç°
    vec3 q = p;
    q.xz = abs(mod(p.xz + ROOM_SIZE, 2.0 * ROOM_SIZE) - ROOM_SIZE);

    // 2. è·åæå ç©ºé´ä¸­çä¹é«å°äººè·ç¦»
    vec2 lego = sdfLego(q);

    // 3. å°é¢ (y=0)
    // å°é¢æ°¸è¿æ¯ y=0 çå¹³é¢ï¼æä»¥è·ç¦»å°±æ¯ p.y
    vec2 floor = vec2(p.y, 0.0);

    // 4. è¿ååºæ¯ä¸­æè¿çç©ä½
    return (lego.x < floor.x) ? lego : floor;
}


// --- æ³çº¿è®¡ç® (Calculate Normal) ---
vec3 getNormal(vec3 p) {
    vec2 e = vec2(HIT_PRECISION, 0.0);
    return normalize(vec3(
        map(p + e.xyy).x - map(p - e.xyy).x,
        map(p + e.yxy).x - map(p - e.yxy).x,
        map(p + e.yyx).x - map(p - e.yyx).x
    ));
}

// --- ä¸»æ¸²æå½æ° ---
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // 1. æ ååå±å¹åæ 
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    // 2. ç¸æºè®¾ç½® (Camera Setup)
    // ä½¿ç¨é¼ æ æ§å¶ç¸æºæè½¬
    float angX = 3.14 * (iMouse.x / iResolution.x - 0.5);
    float angY = 1.57 * (iMouse.y / iResolution.y - 0.5);

    vec3 ro; // Ray Origin (åçº¿èµ·ç¹)
    if(iMouse.z > 0.0){
        // æä½é¼ æ æ¶ï¼ä½¿ç¨æè½¬è§è§
        ro = vec3(cos(angY) * sin(angX), sin(angY), cos(angY) * cos(angX)) * 3.0;
    } else {
        // é»è®¤è§è§
        ro = vec3(0.0, 1.5, -3.0);
    }
    ro.y = max(ro.y, 0.5) + 1.0; // ç¡®ä¿ç¸æºä¸ä½äºå°é¢ï¼å¹¶æ¬é«

    vec3 lookAt = vec3(0.0, 1.0, 0.0); // è§çº¿ç¦ç¹ (ä¹é«å°äººä¸­å¿)
    vec3 f = normalize(lookAt - ro);
    vec3 r = normalize(cross(vec3(0,1,0), f));
    vec3 u = cross(f, r);
    vec3 rd = normalize(f + uv.x * r + uv.y * u); // Ray Direction (åçº¿æ¹å)

    // 3. Raymarching å¾ªç¯
    float t = 0.0; // æ»è¡è¿è·ç¦»
    vec2 res;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + t * rd;
        res = map(p);
        if (res.x < HIT_PRECISION || t > MAX_DIST) break;
        t += res.x;
    }

    // 4. çè² (Shading)
    vec3 col = vec3(0.0); // æç»é¢è²
    if (t < MAX_DIST) {
        vec3 p = ro + t * rd;
        vec3 normal = getNormal(p);

        // --- è·åç©ä½åºç¡è² (Get Base Color) ---
        vec3 baseColor;
        if (res.y == 0.0) { // å°é¢
            // æ£çæ ¼å°é¢
            float checker = mod(floor(p.x) + floor(p.z), 2.0);
            baseColor = mix(COLOR_FLOOR1, COLOR_FLOOR2, checker);
        } else if (res.y == 1.0) { // å¤´
            baseColor = COLOR_HEAD;
        } else if (res.y == 2.0) { // èº«ä½
            baseColor = COLOR_TORSO;
        } else { // è¿
            baseColor = COLOR_LEGS;
        }

        // --- åç§è®¡ç® (Lighting) ---
        vec3 lightPos = vec3(0.0, 5.0, -3.0); // ä¸ä¸ªç¹åæº
        vec3 lightDir = normalize(lightPos - p);

        // æ¼«åå° (Diffuse)
        float diff = max(0.0, dot(normal, lightDir));

        // ç®åçç¯å¢åé®è½ (Ambient Occlusion)
        float ao = 0.0;
        float step = 0.05;
        for(int j=1; j<5; j++){
            ao += (float(j)*step - map(p + normal * float(j)*step).x);
        }
        ao = 1.0 - clamp(ao, 0.0, 1.0);

        // ç»ååç§
        vec3 lighting = vec3(0.2) + diff * vec3(1.0); // ç¯å¢å + æ¼«åå°
        col = baseColor * lighting * ao;

        // æ·»å é¾æï¼å¢å¼ºæ·±åº¦æ
        col *= exp(-0.01 * t * t);
    }

    // 5. è¾åºé¢è²
    fragColor = vec4(col, 1.0);
}

/*
Fig 9.1 and Fig 9.15(ii) in Indra's pearls,
the 1/15 and 1/9 double cusp groups.
Using Ford domain method.

Translated from knighty's evaldraw script.
**ALL the difficult parts were done by knighty**.
I'm lucky and grateful to have his selfless help.

Keyboadr control:

+ Press 1/2/3 to switch mode (limit set, circle packing, tiling)
+ Press w/s to zoom in/out
+ Press a/b/c to see the action of a, b and successive action of a^i
*/
#define AA 3
#define CURVE_THICKNESS 0.007

// uncomment to show the 1/9 cusp group
//#define cusp_1_9

const int MAX_ITER = 100;

const vec2 tb = vec2(2, 0);

#ifdef cusp_1_9
    const int N = 13;
    const vec2 ta = vec2(1.8964073,-0.0487530);
    const float rr = 0.3217;
    vec2 pts[] = vec2[] (
            vec2(-1, 0), vec2(1, 0), vec2(0, -1),
            vec2(0,1)
        );
#else
    const int N = 17;
    const vec2 ta = vec2(1.95859103, -0.01127856);
    const float rr = 0.3287582641552815;
    vec2 pts[] = vec2[] (
            vec2(-1, 0), vec2(1, 0), vec2(0, -1),
            vec2(0,1), vec2(0.405415, -0.19312647)
        );
#endif

const vec3 trap1 = vec3(0, 0, 1);
const vec3 trap2 = vec3(1. - rr, 0, rr * rr);
const vec3 trap3 = vec3(rr - 1., 0, rr * rr);

float zoom;
int mode;

vec2 cmul(vec2 z, vec2 w) { return vec2(z.x*w.x - z.y*w.y, z.x*w.y + z.y*w.x); }

vec2 cdiv(vec2 z, vec2 w) {
    float d2 = dot(w, w); if (d2 < 1e-15) return vec2(1e7);
    return cmul(z, vec2(w.x, -w.y)) / d2;
}

vec2 csqrt(vec2 z) {
    float r = length(z); float angle = atan(z.y, z.x);
    return sqrt(r) * vec2(cos(0.5*angle), sin(0.5*angle));
}

vec2 solveQuadratic(vec2 b, vec2 c) {
    vec2 delta = cmul(b, b) - 4. * c;
    vec2 sqrt_delta = csqrt(delta);
    return (-b - sqrt_delta) / 2.;
}

struct Mobius { vec2 a, b, c, d; };

vec2 applyMobius(Mobius m, vec2 z) {
    vec2 num = cmul(m.a, z) + m.b;
    vec2 den = cmul(m.c, z) + m.d;
    return cdiv(num, den);
}

Mobius mulMobius(Mobius m1, Mobius m2) {
    return Mobius(cmul(m1.a, m2.a)+cmul(m1.b, m2.c), cmul(m1.a, m2.b)+cmul(m1.b, m2.d),
                  cmul(m1.c, m2.a)+cmul(m1.d, m2.c), cmul(m1.c, m2.b)+cmul(m1.d, m2.d));
}

Mobius invMobius(Mobius m) { return Mobius(m.d, -m.b, -m.c, m.a); }

Mobius interpolateMobius(Mobius m1, Mobius m2, float t) {
    return Mobius(
        mix(m1.a, m2.a, t),
        mix(m1.b, m2.b, t),
        mix(m1.c, m2.c, t),
        mix(m1.d, m2.d, t)
    );
}

const Mobius identity = Mobius(vec2(1,0), vec2(0), vec2(0), vec2(1,0));
Mobius a, b, A, B;
Mobius transforms[N];

//-----------------------------------------------------

void getGenerators(inout Mobius a, inout Mobius b) {
    // compute tab
    vec2 ta2 = cmul(ta, ta);
    vec2 tb2 = cmul(tb, tb);
    vec2 tab = solveQuadratic(-cmul(ta, tb), ta2 + tb2);

    // compute z0
    vec2 num = cmul(tab - vec2(2, 0), tb);
    vec2 den = cmul(tb, tab) - 2. * ta + cmul(vec2(0, 2), tab);
    vec2 z0 = cdiv(num, den);

    // compute b
    b.a = tb / 2. - vec2(0, 1);
    b.d = tb / 2. + vec2(0, 1);
    b.c = b.b = tb / 2.;

    // compute ab
    Mobius ab = Mobius(
        tab / 2.,
        cdiv(tab / 2. - vec2(1, 0), z0),
        cmul(tab / 2. + vec2(1, 0), z0),
        tab / 2.
    );

    // a = (ab)B
    a = mulMobius(ab, invMobius(b));
}

bool Equal(vec2 z, vec2 a, float deriv) {
    float k = 2. + length(z);
    return k * length(z - a) * deriv / zoom < CURVE_THICKNESS;
}

void setupTransforms() {
    getGenerators(a, b);
    A = invMobius(a);
    B = invMobius(b);

    transforms[0] = a;

#ifdef cusp_1_9
    for (int i = 1; i < 9; i++)
        transforms[i] = mulMobius(transforms[i-1], a);
    transforms[9] = b;
    transforms[10] = mulMobius(a, mulMobius(b, A));
    transforms[11] = mulMobius(transforms[8], B);
    transforms[12] = mulMobius(invMobius(transforms[7]), transforms[10]);
#else

    for (int i = 1; i < 15; i++)
        transforms[i] = mulMobius(transforms[i-1], a);

    transforms[15] = b;
    transforms[16] = mulMobius(mulMobius(a, b), A);
#endif
}


float sdTrapped(vec2 p) {
    float d1 = 1. - dot(p, p);
    float d2 = dot(p - trap2.xy, p - trap2.xy) - trap2.z;
    float d3 = dot(p - trap3.xy, p - trap3.xy) - trap3.z;
    return min(d1, min(d2, d3));
}

struct BestTransformChoice {
    vec2  z_new;
    float nsd;
    bool  found;
};

void fold(inout vec2 z, inout float count, inout float deriv) {
    for (int i = 0; i < MAX_ITER; i++) {

        float min_nsd = 1.0;
        BestTransformChoice best_choice;
        best_choice.found = false;
        for (int j = 0; j < N; j++) {
            Mobius g = transforms[j];
            vec2 den_g = cmul(g.c, z) + g.d;
            float nsd_g = dot(den_g, den_g);

            if (nsd_g < min_nsd) {
                min_nsd = nsd_g;
                best_choice.z_new = applyMobius(g, z);
                best_choice.nsd = nsd_g;
                best_choice.found = true;
            }
            Mobius g_inv = invMobius(g);
            vec2 den_g_inv = cmul(g_inv.c, z) + g_inv.d;
            float nsd_g_inv = dot(den_g_inv, den_g_inv);

            if (nsd_g_inv < min_nsd) {
                min_nsd = nsd_g_inv;
                best_choice.z_new = applyMobius(g_inv, z);
                best_choice.nsd = nsd_g_inv;
                best_choice.found = true;
            }
        }
        if (best_choice.found) {
            z = best_choice.z_new;    
            deriv *= best_choice.nsd;
            count += 1.0;
        }
        else {
            break;
        }
    }
}
vec3 hsv2rgb(vec3 c){
    vec3 rgb = clamp(abs(mod(c.x*6.0 + vec3(0.0,4.0,2.0), 6.0)-3.0)-1.0, 0.0, 1.0);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

vec3 getColor(vec2 z, vec2 uv, in float deriv) {

    float count = 0.0;
    fold(z, count, deriv);
    float id;
    if (dot(z, z) > 1.)
        { id = 0.; }
    else if (dot(z - trap2.xy, z - trap2.xy) < trap2.z)
        { id = 1.; }
    else if (dot(z - trap3.xy, z - trap3.xy) < trap3.z)
        { id = 2.; }
    else
        id = 3.;

    float d = abs(sdTrapped(z) * deriv) / zoom;
    if (mode == 1) {
        vec3 col = vec3(1);
        for (int i = 0; i < pts.length(); i++) {
            if (Equal(z, pts[i], deriv)) {
                col = vec3(0);
                break;
            }
        }
        return col;
    }
    else if (mode == 0) {
        if (count > float(MAX_ITER))
            return vec3(0);
        float pi = 3.1415926;
        float t = pi * count / 14.;

        vec3 col = 0.5 + 0.5 *cos(
            t + vec3(3., 4.2, 4.9) * 0.95);
        col = pow(col, vec3(2.2));
        return col;
    }
    else {
        vec3 col=vec3(1);
        if (id == 1. || id == 2.)
            col = 0.5 + 0.45 * cos((id - 1.)*14.+vec3(0,1,2));

        if (length(z) > 1.) {
            float id2 = mod(count, 2.);
            if (id2 == 1.)
                col = vec3(0.85, 0.85, 0.95);
        }

        for (int i = 0; i < pts.length(); i++) {
            if (Equal(z, pts[i], deriv)) {
                col = vec3(0);
                break;
            }
        }
        return col;
    }
}

Mobius getTransformForK(int k, const in Mobius B) {
    const Mobius identity = Mobius(vec2(1,0), vec2(0), vec2(0), vec2(1,0));
    if (k == 0) {
        return identity;
    }
    Mobius a_k = transforms[k - 1];
    return invMobius(a_k);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    setupTransforms();
    vec4 pan_vals = textureLod(iChannel0, vec2(0.5, 0.5) / iResolution.xy, 0.0);
    zoom = textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;
    float ab_anim_t = textureLod(iChannel0, vec2(1.5, 0.5) / iResolution.xy, 0.0).x;
    vec4 c_state = textureLod(iChannel0, vec2(2.5, 0.5) / iResolution.xy, 0.0);

    mode = int(textureLod(iChannel0, vec2(3.5, 0.5) / iResolution.xy, 0.0).x);

    int k_from = int(c_state.x);
    int k_to = int(c_state.y);
    float anim_timer = c_state.z;

    const Mobius identity = Mobius(vec2(1,0), vec2(0), vec2(0), vec2(1,0)); // Moved identity here for use in getTransformForK
    Mobius final_transform = identity;
    if (abs(ab_anim_t) > 0.01) {
        if (ab_anim_t > 0.0) {
            final_transform = interpolateMobius(identity, A, ab_anim_t);
        } else {
            final_transform = interpolateMobius(identity, B, abs(ab_anim_t));
        }
    }
    else if (k_to > 0 || anim_timer < 1.0) {
        Mobius T_from = getTransformForK(k_from, B);
        Mobius T_to = getTransformForK(k_to, B);
        float t = smoothstep(0.0, 1.0, anim_timer);

        final_transform = interpolateMobius(T_from, T_to, t);
    } 
    vec3 col = vec3(0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * (fragCoord + o) - iResolution.xy -
                       pan_vals.xy * pow(zoom, -1.0)) /
                      iResolution.y;
            uv *= zoom * 1.05; 
            vec2 den_g = cmul(final_transform.c, uv) + final_transform.d;
            float deriv = dot(den_g, den_g);
            vec2 z = applyMobius(final_transform, uv);
            col += getColor(z, uv, deriv);
        }
    }
    col /= (float(AA) * float(AA));
    fragColor = vec4(col, 1);
}

