struct QueryResult {
    float dist;
    vec2 pos;
    vec2 tangent;
};

float cross2d(vec2 a, vec2 b) { return a.x * b.y - a.y * b.x; }

QueryResult getArcInfo(vec2 p, Arc arc) {
    vec2 c = arc.circle.xy;
    float r = arc.circle.z;
    vec2 p1 = arc.p1;
    vec2 p2 = arc.p2;
    vec2 v1 = p1 - c;
    vec2 v2 = p2 - c;
    vec2 vp = p - c;
    float arc_direction = sign(cross2d(v1, v2));
    if (sign(cross2d(v1, vp)) != -arc_direction &&
        sign(cross2d(vp, v2)) != -arc_direction) {
        vec2 q = c + normalize(vp) * r;
        vec2 tangent_vec = q - c;
        vec2 tangent =
            normalize(arc_direction * vec2(-tangent_vec.y, tangent_vec.x));
        return QueryResult(distance(p, q), q, tangent);
    } else {
        float d1 = distance(p, p1);
        float d2 = distance(p, p2);
        if (d1 < d2) {
            vec2 tangent = normalize(arc_direction * vec2(-v1.y, v1.x));
            return QueryResult(d1, p1, tangent);
        } else {
            vec2 tangent = normalize(arc_direction * vec2(-v2.y, v2.x));
            return QueryResult(d2, p2, tangent);
        }
    }
}

float getCurveInfo_a(vec2 p) {
    QueryResult final_result = QueryResult(1e6, vec2(0.0), vec2(1.0, 0.0));
    if (p.x < -1.0 || p.x > 1.0) {
        vec2 q = vec2(p.x, 0.0);
        final_result = QueryResult(abs(p.y), q, vec2(1.0, 0.0));
    }
    for (int i = 0; i < 6; ++i) {
        QueryResult arc_result = getArcInfo(p, blob_a_boundary[i]);
        if (arc_result.dist < final_result.dist) {
            final_result = arc_result;
        }
    }
    vec2 vec_qp = p - final_result.pos;
    float side = sign(cross2d(final_result.tangent, vec_qp));
    return -final_result.dist * side;
}

float getCurveInfo(vec2 p, in Arc arcs[7]) {
    QueryResult final_result = QueryResult(1e6, vec2(0.0), vec2(1.0, 0.0));
    for (int i = 0; i < 7; ++i) {
        QueryResult arc_result = getArcInfo(p, arcs[i]);
        if (arc_result.dist < final_result.dist) {
            final_result = arc_result;
        }
    }
    vec2 vec_qp = p - final_result.pos;
    float side = sign(cross2d(final_result.tangent, vec_qp));
    return -final_result.dist * side;
}


const Mobius transform_a = Mobius(vec2(0.82106938, -0.38329420499999994),
                                  vec2(0.38329420499999994, -0.17893062),
                                  vec2(-0.383294205, -1.82106938),
                                  vec2(0.82106938, -0.38329420499999994));
const Mobius transform_b =
    Mobius(vec2(1, -1), vec2(1, 0), vec2(1, 0), vec2(1, 1));

const Mobius transform_A = Mobius(
    vec2(0.82106938, -0.383294205),
    vec2(-0.38329420499999994, 0.17893062000000004),
    vec2(0.3832942050000001, 1.82106938), vec2(0.82106938, -0.383294205));

const Mobius transform_B =
    Mobius(vec2(1, 1), vec2(-1, 0), vec2(-1, 0), vec2(1, -1));

const Mobius identity = Mobius(vec2(1, 0), vec2(0), vec2(0), vec2(1, 0));

Mobius interpolateMobius(Mobius m1, Mobius m2, float t) {
    return Mobius(mix(m1.a, m2.a, t), mix(m1.b, m2.b, t), mix(m1.c, m2.c, t),
                  mix(m1.d, m2.d, t));
}
float sdf_d4_unbounded_region(vec2 p) {
    // 1. SDF for "outside unit circle"
    float sdf_unit_circle_out = length(p) - 1.0;

    // 2. SDF for "y < 0" region (lower half-plane)
    float sdf_lower_half_plane = p.y;

    // 3. SDF for "outside circ_b"
    float sdf_circ_b_out = length(p - circ_b.xy) - circ_b.z;

    // 4. SDF for "outside circ_B"
    float sdf_circ_B_out = length(p - circ_B.xy) - circ_B.z;

    // æ±ææåºåçäº¤é (Intersection = max of all SDFs)
    float d4 = max(sdf_unit_circle_out, sdf_lower_half_plane);
    d4 = max(d4, sdf_circ_b_out);
    d4 = max(d4, sdf_circ_B_out);

    return d4;
}
float inside_blob_a(vec2 p) { return getCurveInfo_a(p); }
float inside_blob_b(vec2 p) { return getCurveInfo(p, blob_b_boundary); }
float inside_blob_A(vec2 p) { return getCurveInfo(p, blob_A_boundary); }
float inside_blob_B(vec2 p) { return getCurveInfo(p, blob_B_boundary); }

void fold(inout vec2 p, inout float count, inout float deriv) {
    for (int i = 0; i < MAX_ITER; i++) {
        // å¨å¾ªç¯å¼å§æ¶ï¼å°±æ£æ¥æ¯å¦å·²ç»è½å¥åºæ¬åºå
        float d1 = length(p - c5.xy) - c5.z;
        float d2 = length(p - c2.xy) - c2.z;
        float d3 = length(p - d.xy) - d.z;
        float d4 = sdf_d4_unbounded_region(p);

        // å¦æç¹på¨ä»»ä½ä¸ä¸ªåºæ¬åºååé¨ï¼è¿­ä»£å°±åºè¯¥ç»æ
        if (d1 < 0. || d2 < 0. || d3 < 0. || d4 < 0.) {
            break;
        }

        // å¦æè¿æ²¡è½å¥åºæ¬åºåï¼ææ ¹æ® blob åºç¨åæ¢
        // æ³¨æï¼è¿éç if é¡ºåºåéå åºåå¤çå¯è½ä¹éè¦è°æ´ï¼ä½æä»¬åè§£å³æ ¸å¿é®é¢
        if (inside_blob_a(p) < 0.) {
            deriv *= L2(cmul(transform_A.c, p) + transform_A.d);
            p = applyMobius(transform_A, p);
            count += 1.;
            continue;
        }
        if (inside_blob_b(p) < 0.) {
            deriv *= L2(cmul(transform_B.c, p) + transform_B.d);
            p = applyMobius(transform_B, p);
            count += 1.;
            continue;
        }
        if (inside_blob_A(p) < 0.) {
            deriv *= L2(cmul(transform_a.c, p) + transform_a.d);
            p = applyMobius(transform_a, p);
            count += 1.;
            continue;
        }
        if (inside_blob_B(p) < 0.) {
            deriv *= L2(cmul(transform_b.c, p) + transform_b.d);
            p = applyMobius(transform_b, p);
            count += 1.;
            continue;
        }

        // å¦ææ¢ä¸å¨åºæ¬åºåï¼ä¹ä¸å¨ä»»ä½blobéï¼è¯´ææé®é¢ï¼æåéåº
        break;
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 pan_vals = texelFetch(iChannel0, ivec2(0, 0), 0);
    zoom = texelFetch(iChannel0, ivec2(10, 10), 0).x;
    float ab_anim_t = texelFetch(iChannel0, ivec2(1, 0), 0).x;
    float show_curves = texelFetch(iChannel0, ivec2(2, 0), 0).x;

    Mobius final_transform = identity;
    if (abs(ab_anim_t) > 0.001) {
        if (ab_anim_t > 0.0) {
            final_transform =
                interpolateMobius(identity, transform_A, ab_anim_t);
        } else {
            final_transform =
                interpolateMobius(identity, transform_B, abs(ab_anim_t));
        }
    }

    vec3 finalCol = vec3(0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA) - 0.5;
            vec2 uv = (2. * (fragCoord + o) - iResolution.xy -
                       pan_vals.xy * pow(zoom, -1.0)) /
                      iResolution.y;
            uv *= zoom * 1.1;

            float count = 0.;
            float deriv = 1.;
            vec2 p = uv;
            deriv *= L2(cmul(final_transform.c, p) + final_transform.d);
            p = applyMobius(final_transform, uv);
            fold(p, count, deriv);

            float id = 0.;
            float d1 = length(p - c5.xy) - c5.z;
            float d2 = length(p - c2.xy) - c2.z;
            float d3 = length(p - d.xy) - d.z;
            float d4 = sdf_d4_unbounded_region(p);

            vec3 col;
            if (mode == 1) {
             if (d1 <= 0.)
                    col = vec3(1., 0.7, 0.7);
                else if (d2 <= 0.)
                    col = vec3(0.5, 0.1, 0.1);
                else if (d3 <= 0. || d4 <= 0.) {
                    float id2 = mod(count, 2.);
                    if (id2 == 0.)
                        col = vec3(.3, .3, 0.6);
                    else
                        col = vec3(1);
                }
            }
            else {
                if (d1 < 0. || d2 < 0.)
                    col = vec3(1., 0.9, 0.3);
                else if (d3 < 0. || d4 < 0.)
                    col = vec3(1, 0, 0);
                else
                    col = vec3(1, 0.9, 0.3);
            }
            float d = min(min(d1, d2), min(d3, d4));
            d = abs(d) * sqrt(abs(deriv)) / zoom;

            float lw = 0.03;
            float aa = fwidth(d) * 2.;
            col = mix(col, vec3(0), 1. - smoothstep(lw - aa, lw + aa, d));

            if (show_curves > 0.5) {
                vec2 z = uv;
                deriv = sqrt(L2(cmul(final_transform.c, z) + final_transform.d));
                z = applyMobius(final_transform, uv);

                float lw = 0.012;
                float sd_curve = inside_blob_a(z);
                sd_curve = abs(sd_curve) * deriv / zoom;
                aa = fwidth(sd_curve) * 2.;
                col = mix(col, vec3(1),
                          1. - smoothstep(lw - aa, lw + aa, sd_curve / zoom));

                sd_curve = inside_blob_A(z);
                sd_curve = abs(sd_curve) * deriv / zoom;
                aa = fwidth(sd_curve) * 2.;
                col = mix(col, vec3(1),
                          1. - smoothstep(lw - aa, lw + aa, sd_curve / zoom));

                sd_curve = inside_blob_b(z);
                sd_curve = abs(sd_curve) * deriv / zoom;
                aa = fwidth(sd_curve) * 2.;
                col = mix(col, vec3(.2, 1., 0.3),
                          1. - smoothstep(lw - aa, lw + aa, sd_curve / zoom));

                sd_curve = inside_blob_B(z);
                sd_curve = abs(sd_curve) * deriv / zoom;
                aa = fwidth(sd_curve) * 2.;
                col = mix(col, vec3(.2, 1., 0.3),
                          1. - smoothstep(lw - aa, lw + aa, sd_curve / zoom));
            }
            finalCol += col;
        }
    }
    finalCol /= float(AA * AA);
    fragColor = vec4(finalCol, 1.0);
}

const int Key_A = 65;
const int Key_B = 66;
const int Key_C = 67;
const int Key_S = 83;
const int Key_W = 87;

float ReadKeyInternal(int key, bool toggle) {
    return textureLod(iChannel1,
                      vec2((float(key) + .5) / 256.0, toggle ? .75 : .25), 0.0)
        .x;
}

float ReadKey(int key) { return ReadKeyInternal(key, false); }
float ReadKeyToggle(int key) { return ReadKeyInternal(key, true); }

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // é»è®¤æåµä¸ï¼ä¿æä¸ä¸å¸§çç¶æ
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // 1. å¹³ç§»ç¶æå­å¨å¨åç´  (0,0)
    if (ivec2(fragCoord.xy) == ivec2(0, 0)) {
        vec4 prev_info = texelFetch(iChannel0, ivec2(0,0), 0);
        float Zoom = texelFetch(iChannel0, ivec2(10,10), 0).x;

        float prev_pan_x = prev_info.x;
        float prev_pan_y = prev_info.y;
        float legacy_pan_x = prev_info.z;
        float legacy_pan_y = prev_info.w;

        // å½é¼ æ æä¸æ¶ï¼æ ¹æ®é¼ æ ä½ç§»æ´æ°å¹³ç§»é
        float pan_x = (iMouse.x - abs(iMouse.z)) * Zoom + legacy_pan_x;
        float pan_y = (iMouse.y - abs(iMouse.w)) * Zoom + legacy_pan_y;

        // å½é¼ æ æ¾å¼æ¶ï¼è®°å½å½åå¹³ç§»éï¼ä¸ºä¸ä¸æ¬¡æå¨ååå¤
        if (iMouse.z < 0.0) {
            legacy_pan_x = prev_pan_x;
            legacy_pan_y = prev_pan_y;
            pan_x = legacy_pan_x;
            pan_y = legacy_pan_y;
        }
        fragColor = vec4(pan_x, pan_y, legacy_pan_x, legacy_pan_y);
    }

    // 2. ç¼©æ¾ç¶æå­å¨å¨åç´  (10,10)
    if (ivec2(fragCoord.xy) == ivec2(10, 10)) {
        // .y ééç¨ä½ç´¯å å¨
        float x = texelFetch(iChannel0, ivec2(10,10), 0).y;
        // æ W å¢å ï¼æ S åå°
        x += (ReadKey(Key_W) - ReadKey(Key_S)) * 0.5; // åæ¢ç¼©æ¾éåº¦
        // æ ¹æ®ç´¯å å¼è®¡ç®ç¼©æ¾ç³»æ°ï¼.x ééå­å¨æç»ç zoom å¼
        float Zoom = pow(1.1, -x); 
        fragColor = vec4(Zoom, x, 0.0, 0.0);
    }

    // 3. 'a'/'b' åæ¢å¨ç»ç¶æå­å¨å¨åç´  (1,0)
    if (ivec2(fragCoord.xy) == ivec2(1, 0)) {
        float prev_anim_t = texelFetch(iChannel0, ivec2(1,0), 0).x;

        float key_a = ReadKey(Key_A);
        float key_b = ReadKey(Key_B);

        // ç®æ å¼ï¼æ a ä¸º 1, æ b ä¸º -1, å¦åä¸º 0
        float target_t = 0.0;
        if (key_a > 0.0) {
            target_t = 1.0;
        } else if (key_b > 0.0) {
            target_t = -1.0;
        }

        // ä½¿ç¨ mix å exp å®ç°å¹³æ»å¨ç»
        float speed = 4.0;
        float anim_t = mix(prev_anim_t, target_t, 1.0 - exp(-iTimeDelta * speed));
        fragColor = vec4(anim_t, 0.0, 0.0, 1.0);
    }

    // 4. 'c' é®åæ¢ç¶æå­å¨å¨åç´  (2,0)
    if (ivec2(fragCoord.xy) == ivec2(2, 0)) {
        vec4 prev_state = texelFetch(iChannel0, ivec2(2,0), 0);
        float current_toggle = prev_state.x;
        float prev_key_c_state = prev_state.y;

        float key_c = ReadKey(Key_C);

        // æ£æµæé®çä¸åæ²¿ï¼æä¸ç¬é´ï¼
        if (key_c > 0.0 && prev_key_c_state == 0.0) {
            current_toggle = 1.0 - current_toggle; // ç¿»è½¬ç¶æ (0 -> 1, 1 -> 0)
        }

        // .x å­å¨åæ¢ç¶æï¼.y å­å¨ä¸ä¸å¸§çæé®ç¶æç¨äºè¾¹ç¼æ£æµ
        fragColor = vec4(current_toggle, key_c, 0.0, 1.0);
    }
}

vec2 cmul(vec2 z, vec2 w) { return vec2(z.x * w.x - z.y * w.y, dot(z, w.yx)); }
vec2 conj(vec2 z) { return vec2(z.x, -z.y); }
vec2 cdiv(vec2 z, vec2 w) { return cmul(z, conj(w)) / dot(w, w); }

struct Mobius {
    vec2 a, b, c, d;
};

vec2 applyMobius(Mobius m, vec2 z) {
    return cdiv(cmul(m.a, z) + m.b, cmul(m.c, z) + m.d);
}

vec3 getCircleFromThreePoints(vec2 p1, vec2 p2, vec2 p3) {
    // ä½¿ç¨è¡åå¼æ¹æ³æ±è§£ï¼åè https://math.stackexchange.com/a/1460096
    // æ https://www.ambrsoft.com/TrigoCalc/Circle3D.htm

    float p1_sq = dot(p1, p1);
    float p2_sq = dot(p2, p2);
    float p3_sq = dot(p3, p3);

    // è®¡ç®åæ¯ï¼å¦ææ¥è¿0ï¼åä¸ç¹å±çº¿
    float denominator = 2.0 * (p1.x * (p2.y - p3.y) - p1.y * (p2.x - p3.x) + p2.x * p3.y - p3.x * p2.y);
    // è®¡ç®åå¿åæ 
    float center_x = (p1_sq * (p2.y - p3.y) - p2_sq * (p1.y - p3.y) + p3_sq * (p1.y - p2.y)) / denominator;
    float center_y = (p1_sq * (p3.x - p2.x) - p2_sq * (p3.x - p1.x) + p3_sq * (p2.x - p1.x)) / denominator;

    vec2 center = vec2(center_x, center_y);
    float radius = distance(center, p1);

    return vec3(center, radius);
}


vec3 applyMobiusToCircle(Mobius m, vec3 C) {
    vec2 center = C.xy;
    float radius = C.z;

    // å¦æåå§åæ¯ä¸ä¸ªç¹ (åå¾å¾å°)ï¼ç´æ¥åæ¢è¯¥ç¹å¹¶è¿åä¸ä¸ªå°å
    if (radius < 1e-6) {
        return vec3(applyMobius(m, center), 1e-6);
    }

    // å¦æåå§ååå¾å¾å¤§ (è¿ä¼¼äºç´çº¿)ï¼å¤çä¼åå¾ä¸ç¨³å®ã
    // ä¸ä¸ªæ´é²æ£çå®ç°éè¦åç¬å¤çç´çº¿çæåµã
    // ä½å¯¹äºæ®éåï¼ä¸ç¹æ³æ¯æå¥½çã

    // 1. å¨åå§åä¸åä¸ä¸ªä¸åçç¹
    // æä»¬éæ© (c+r,0), (c-r,0), (c,r) è¿ä¸ä¸ªç¹æ¥è·å¾è¾å¥½çæ°å¼ç¨³å®æ§
    vec2 p1 = center + vec2(radius, 0.0);
    vec2 p2 = center + vec2(-radius, 0.0);
    vec2 p3 = center + vec2(0.0, radius);

    // 2. å°è¿ä¸ä¸ªç¹éè¿è«æ¯ä¹æ¯åæ¢è¿è¡æ å°
    vec2 q1 = applyMobius(m, p1);
    vec2 q2 = applyMobius(m, p2);
    vec2 q3 = applyMobius(m, p3);

    // 3. éè¿è¿ä¸ä¸ªæ°ç¹ç¡®å®æ°å
    return getCircleFromThreePoints(q1, q2, q3);
}

vec3 getOrthogonalCircle(vec3 C, vec2 z1, vec2 z2) {
    vec2 c = C.xy;

    // --- è§£çº¿æ§æ¹ç¨ç» Ax=b ---
    // a11*x + a12*y = b1
    // a21*x + a22*y = b2

    // æ¹ç¨1: c' å¨ z1, z2 çä¸­åçº¿ä¸
    // dot(c', z2-z1) = dot((z1+z2)/2, z2-z1)
    vec2 v_perp = z2 - z1; // ä¸­åçº¿çæ³åé
    float a11 = v_perp.x;
    float a12 = v_perp.y;
    float b1 = dot(0.5 * (z1 + z2), v_perp);

    // æ¹ç¨2: c' å¨ C äº z1 ç¹çæ³çº¿ä¸ (è¿æ¡æ³çº¿æ¯ C' å¨ z1 çåçº¿) çæ³çº¿ä¸
    // dot(c' - z1, z1 - c) = 0  => dot(c', z1-c) = dot(z1, z1-c)
    vec2 v_tan = z1 - c; // C' å¨ z1 çåçº¿æ¹å (å³Cçæ³çº¿æ¹å)
    float a21 = v_tan.x;
    float a22 = v_tan.y;
    float b2 = dot(z1, v_tan);

    // ä½¿ç¨åè±å§æ³åè§£æ¹ç¨ç»
    float det = a11 * a22 - a12 * a21;

    // å¦æ det æ¥è¿ 0ï¼è¯´æä¸­åçº¿åæ³çº¿å¹³è¡ã
    // è¿åçå¨ z1, z2 æ¯ C çç´å¾ç«¯ç¹æ¶ï¼æ­¤æ¶æ­£äº¤âåâæ¯ä¸æ¡ç´çº¿ã
    if (abs(det) < 1e-9) {
        // è¿åä¸æ¡éè¿ z1, z2 çç´çº¿ï¼ç¨å¤§åå¾åè¿ä¼¼ï¼
        // ç´çº¿çâåå¿âå¯ä»¥åä¸­ç¹ï¼ä½æ¹ååç´äº v_perp
        vec2 mid_point = 0.5 * (z1 + z2);
        vec2 dir = vec2(-v_perp.y, v_perp.x);
        // æåå¿æ¨å°å¾è¿çå°æ¹
        return vec3(mid_point + dir * 1e5, 1e5); 
    }

    // è§£åºæ°åå¿ c'
    vec2 c_prime;
    c_prime.x = (b1 * a22 - b2 * a12) / det;
    c_prime.y = (b2 * a11 - b1 * a21) / det;

    // åå¾å°±æ¯æ°åå¿å° z1 (æ z2) çè·ç¦»
    float r_prime = distance(c_prime, z1);

    return vec3(c_prime, r_prime);
}

/*
Fig 9.1 in Indra's pearls, the 1/15 cusp group.
Using Ford domain method.

Translated from knighty's evaldraw script.
**ALL the difficult parts were done by knighty**.
I'm lucky and grateful to have his selfless help.

