#define AA 3
#define L2(x)  dot(x,x) 
const int MAX_ITER = 150;
const vec2 ta = vec2(-1.9,0.1);
const vec2 tb = vec2(1.9,0.06);

const vec3 trap0 = vec3(-3.5, 0.1425, 0.25*(81.+0.285*0.2852));
const vec3 trap1 = vec3(-trap0.xy, trap0.z);
const vec3 trap2 = vec3(0.615, 0.016, 0.25*(0.77*0.77+0.032*0.032));
const vec3 trap3 = vec3(-trap2.xy, trap2.z);
float zoom;
int mode;

vec2 cmul(vec2 z, vec2 w) { return vec2(z.x*w.x - z.y*w.y, z.x*w.y + z.y*w.x); }

vec2 cdiv(vec2 z, vec2 w) {
    float d2 = dot(w, w); if (d2 < 1e-15) return vec2(1e7);
    return cmul(z, vec2(w.x, -w.y)) / d2;
}

vec2 csqrt(vec2 z) {
    float r = length(z); float angle = atan(z.y, z.x);
    return sqrt(r) * vec2(cos(0.5*angle), sin(0.5*angle));
}

vec2 solveQuadratic(vec2 b, vec2 c) {
    vec2 delta = cmul(b, b) - 4. * c;
    vec2 sqrt_delta = csqrt(delta);
    return (-b + sqrt_delta) / 2.;
}

struct Mobius { vec2 a, b, c, d; };

vec2 applyMobius(Mobius m, vec2 z) {
    vec2 num = cmul(m.a, z) + m.b;
    vec2 den = cmul(m.c, z) + m.d;
    return cdiv(num, den);
}

Mobius mulMobius(Mobius m1, Mobius m2) {
    return Mobius(cmul(m1.a, m2.a)+cmul(m1.b, m2.c), cmul(m1.a, m2.b)+cmul(m1.b, m2.d),
                  cmul(m1.c, m2.a)+cmul(m1.d, m2.c), cmul(m1.c, m2.b)+cmul(m1.d, m2.d));
}

Mobius invMobius(Mobius m) { return Mobius(m.d, -m.b, -m.c, m.a); }

Mobius interpolateMobius(Mobius m1, Mobius m2, float t) {
    return Mobius(
        mix(m1.a, m2.a, t),
        mix(m1.b, m2.b, t),
        mix(m1.c, m2.c, t),
        mix(m1.d, m2.d, t)
    );
}

const Mobius identity = Mobius(vec2(1,0), vec2(0), vec2(0), vec2(1,0));


//-----------------------------------------------------

const int N = 13;
Mobius transforms[N];

void getGenerators(inout Mobius a, inout Mobius b) {
    // compute tab
    vec2 ta2 = cmul(ta, ta);
    vec2 tb2 = cmul(tb, tb);
    vec2 tab = solveQuadratic(-cmul(ta, tb), ta2 + tb2);

    // compute z0
    vec2 num = cmul(tab - vec2(2, 0), tb);
    vec2 den = cmul(tb, tab) - 2. * ta + cmul(vec2(0, 2), tab);
    vec2 z0 = cdiv(num, den);

    // compute b
    b.a = tb / 2. - vec2(0, 1);
    b.d = tb / 2. + vec2(0, 1);
    b.c = b.b = tb / 2.;

    // compute ab
    Mobius ab = Mobius(
        tab / 2.,
        cdiv(tab / 2. - vec2(1, 0), z0),
        cmul(tab / 2. + vec2(1, 0), z0),
        tab / 2.
    );

    // a = (ab)B
    a = mulMobius(ab, invMobius(b));
}


void setupTransforms() {
    Mobius a, b;
    getGenerators(a, b);

    transforms[0] = a;
    for (int i = 1; i < 8; i++)
        transforms[i] = mulMobius(transforms[i-1], a);

    transforms[8] = b;
    Mobius A= invMobius(a);
    Mobius B = invMobius(b);
    transforms[9] = mulMobius(mulMobius(a, b), A);
     transforms[10] = mulMobius(b, A);
     Mobius tmp = a;
     for (int i = 0; i < 8; i++)
         tmp = mulMobius(tmp, b);

     transforms[11] = tmp; 

        transforms[12] = mulMobius(transforms[11], b);
}


float sdTrapped(vec2 z) {
    float d0 = (L2(z - trap0.xy) - trap0.z);
     float d1 = (L2(z - trap1.xy) - trap1.z);
      float d2 = (L2(z - trap2.xy) - trap2.z);
       float d3 = (L2(z - trap3.xy) - trap3.z);
    return min(min(-d1,-d0), min(d2, d3));
}

struct BestTransformChoice {
    vec2  z_new;
    float nsd;
    bool  found;
};

void fold(inout vec2 z, inout float count, inout float deriv) {
    for (int i = 0; i < MAX_ITER; i++) {

        float min_nsd = 1.0;
        BestTransformChoice best_choice;
        best_choice.found = false;
        for (int j = 0; j < N; j++) {
            Mobius g = transforms[j];
            vec2 den_g = cmul(g.c, z) + g.d;
            float nsd_g = dot(den_g, den_g);

            if (nsd_g < min_nsd) {
                min_nsd = nsd_g;
                best_choice.z_new = applyMobius(g, z);
                best_choice.nsd = nsd_g;
                best_choice.found = true;
            }
            Mobius g_inv = invMobius(g);
            vec2 den_g_inv = cmul(g_inv.c, z) + g_inv.d;
            float nsd_g_inv = dot(den_g_inv, den_g_inv);

            if (nsd_g_inv < min_nsd) {
                min_nsd = nsd_g_inv;
                best_choice.z_new = applyMobius(g_inv, z);
                best_choice.nsd = nsd_g_inv;
                best_choice.found = true;
            }
        }
        if (best_choice.found) {
            z = best_choice.z_new;    
            deriv *= best_choice.nsd;
            count += 1.0;
        }
        else {
            break;
        }
    }
}
vec3 hsv2rgb(vec3 c){
    vec3 rgb = clamp(abs(mod(c.x*6.0 + vec3(0.0,4.0,2.0), 6.0)-3.0)-1.0, 0.0, 1.0);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

vec3 getColor(vec2 z, vec2 uv, in float deriv) {

    float count = 1.0;
    fold(z, count, deriv);
    float id;
    if (L2(z - trap0.xy) > trap0.z)
        { id = 0.; }
    else if (L2(z - trap1.xy) > trap1.z)
        { id = 0.; }
    else if (L2(z - trap2.xy) < trap2.z)
        { id = 13.; }
     else if (L2(z - trap3.xy) < trap3.z)
        id = 13.;

    float d = abs(sdTrapped(z)) * abs(deriv) / zoom;
    float aa = fwidth(d) * 2.;
    float lw = 0.002;
    if (mode == 0) {
        vec3 col = 0.5+0.45 *cos(id + vec3(0,1,2));
        col = mix(col, vec3(0), 1.-smoothstep(lw-aa, lw+aa, d));
        return col;
    }
    else  {
        if (count > float(MAX_ITER - 1))
            return vec3(0);
        if (count <= 1.)
            return vec3(0);
        float pi = 3.1415926;
        float t = pi * count / 12.;
        vec3 col = 0.5 + 0.5 * cos(t + .94*vec3(4.4, 3.2, 2.));
        col = pow(col, vec3(2.2));
        return col;
    }
}

Mobius getTransformForK(int k, const in Mobius B) {
    const Mobius identity = Mobius(vec2(1,0), vec2(0), vec2(0), vec2(1,0));
    if (k == 0) {
        return identity;
    }
    Mobius a_k = transforms[k - 1];
    return invMobius(a_k);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    setupTransforms();
    Mobius a, b, A, B;
    getGenerators(a, b);
    A = invMobius(a);
    B = invMobius(b);

    vec4 pan_vals = textureLod(iChannel0, vec2(0.5, 0.5) / iResolution.xy, 0.0);
    zoom = textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;
    float ab_anim_t = textureLod(iChannel0, vec2(1.5, 0.5) / iResolution.xy, 0.0).x;
    vec4 c_state = textureLod(iChannel0, vec2(2.5, 0.5) / iResolution.xy, 0.0);

    mode = int(textureLod(iChannel0, vec2(3.5, 0.5) / iResolution.xy, 0.0).x);

    int k_from = int(c_state.x);
    int k_to = int(c_state.y);
    float anim_timer = c_state.z;

    const Mobius identity = Mobius(vec2(1,0), vec2(0), vec2(0), vec2(1,0)); // Moved identity here for use in getTransformForK
    Mobius final_transform = identity;
    if (abs(ab_anim_t) > 0.01) {
        if (ab_anim_t > 0.0) {
            final_transform = interpolateMobius(identity, A, ab_anim_t);
        } else {
            final_transform = interpolateMobius(identity, B, abs(ab_anim_t));
        }
    }
    else if (k_to > 0 || anim_timer < 1.0) {
        Mobius T_from = getTransformForK(k_from, B);
        Mobius T_to = getTransformForK(k_to, B);
        float t = smoothstep(0.0, 1.0, anim_timer);

        final_transform = interpolateMobius(T_from, T_to, t);
    } 
    vec3 col = vec3(0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * (fragCoord + o) - iResolution.xy -
                       pan_vals.xy * pow(zoom, -1.0)) /
                      iResolution.y;
            uv = uv.yx;
            uv *= zoom * 1.4; 
            vec2 den_g = cmul(final_transform.c, uv) + final_transform.d;
            float deriv = dot(den_g, den_g);
            vec2 z = applyMobius(final_transform, uv);
            col += getColor(z, uv, deriv);
        }
    }
    col /= (float(AA) * float(AA));
    fragColor = vec4(col, 1);
}

