// Storage Code For Pan and Zoom

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Default output for unused pixels
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // Pan State at (0,0)
    if (ivec2(fragCoord.xy) == ivec2(0, 0)) {
        vec4 prev_info = textureLod(iChannel0, vec2(0.0, 0.0), 0.0);
        float Zoom =
            textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;
        float prev_pan_x = prev_info.x;
        float prev_pan_y = prev_info.y;
        float legacy_pan_x = prev_info.z;
        float legacy_pan_y = prev_info.w;

        float pan_x = (iMouse.x - abs(iMouse.z)) * Zoom + legacy_pan_x;
        float pan_y = (iMouse.y - abs(iMouse.w)) * Zoom + legacy_pan_y;

        if (iMouse.z < 0.0) {
            legacy_pan_x = prev_pan_x;
            legacy_pan_y = prev_pan_y;
            pan_x = legacy_pan_x;
            pan_y = legacy_pan_y;
        }
        fragColor = vec4(pan_x, pan_y, legacy_pan_x, legacy_pan_y);
    }

    // Zoom State at (10,10)
    if (ivec2(fragCoord.xy) == ivec2(10, 10)) {
        float x =
            textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).y;
        x += ReadKey(Key_W) - ReadKey(Key_S);
        float Zoom = pow(2.71, -x * 0.05);
        fragColor = vec4(Zoom, x, 0.0, 0.0);
    }

    // Animation State at (1,0)
    if (ivec2(fragCoord.xy) == ivec2(1, 0)) {
        // Read previous animation state
        float prev_anim_t = texelFetch(iChannel0, ivec2(1,0), 0).x;

        float key_a = ReadKey(Key_A);
        float key_b = ReadKey(Key_B);

        float target_t = 0.0;
        if (key_a > 0.0) {
            target_t = 1.0;
        } else if (key_b > 0.0) {
            target_t = -1.0;
        }

        float speed = 4.0;
        float anim_t = mix(prev_anim_t, target_t, 1.0 - exp(-iTimeDelta * speed));
        fragColor = vec4(anim_t, 0.0, 0.0, 1.0);
    }
     if (ivec2(fragCoord.xy) == ivec2(2, 0)) {
         vec4 prev_c_state = texelFetch(iChannel0, ivec2(2,0), 0);
        float k_from = prev_c_state.x;
        float k_to = prev_c_state.y;
        float anim_timer = prev_c_state.z;
        float prev_key_c_state = prev_c_state.w;

        float key_c = ReadKey(Key_C);
        if (anim_timer < 1.0) {
            float anim_speed = 2.0;
            anim_timer += iTimeDelta * anim_speed;
        }
        anim_timer = min(anim_timer, 1.0);

        if (key_c > 0.0 && prev_key_c_state == 0.0 && anim_timer >= 1.0) {
            k_from = k_to;
            k_to = mod(k_to + 1.0, 16.0);
            anim_timer = 0.0;
        }

        fragColor = vec4(k_from, k_to, anim_timer, key_c);
    }
    if (ivec2(fragCoord.xy) == ivec2(3, 0)) {
        float current_mode = texelFetch(iChannel0, ivec2(3,0), 0).x;
        if(iFrame < 2) current_mode = 0.0; 
        if (ReadKey(Key_1) > 0.0) current_mode = 0.0;
        if (ReadKey(Key_2) > 0.0) current_mode = 1.0;
        if (ReadKey(Key_3) > 0.0) current_mode = 2.0;
        fragColor = vec4(current_mode, 0.0, 0.0, 1.0);
    }
}

#define N_CORNERS 4
#define N_TOTAL (N + N_CORNERS)

// --- Coordinate Transformation and Helpers ---

// Get point from Buffer A. It's already in the uv-like coordinate space.
vec2 getPoint(int i) {
    return texelFetch(iChannel0, ivec2(i, 0), 0).xy;
}

// Check for an edge in Buffer B
bool hasEdge(int i, int j) {
    // Make sure indices are ordered for the symmetric matrix optimization
    return texelFetch(iChannel1, ivec2(min(i,j), max(i,j)), 0).r > 0.5;
}

// SDF for a line segment in our abstract coordinate space
float sdfLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

// Check if point p is inside triangle (a, b, c)
bool inTriangle(vec2 p, vec2 a, vec2 b, vec2 c) {
    vec3 w; // Barycentric coordinates
    vec2 v0 = b - a, v1 = c - a, v2 = p - a;
    float d00 = dot(v0, v0);
    float d01 = dot(v0, v1);
    float d11 = dot(v1, v1);
    float d20 = dot(v2, v0);
    float d21 = dot(v2, v1);
    float denom = d00 * d11 - d01 * d01;
    w.y = (d11 * d20 - d01 * d21) / denom;
    w.z = (d00 * d21 - d01 * d20) / denom;
    w.x = 1.0 - w.y - w.z;
    return all(greaterThanEqual(w, vec3(0.0)));
}

// --- Main Rendering ---
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Convert current pixel coordinate to our new normalized coordinate system
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.1, 0.1, 0.2); // Background color

    // --- 1. Draw Faces (Triangles) ---
    for (int i = 0; i < N_TOTAL; i++) {
        for (int j = i + 1; j < N_TOTAL; j++) {
            if (!hasEdge(i, j)) continue;
            for (int k = j + 1; k < N_TOTAL; k++) {
                if (hasEdge(j, k) && hasEdge(i, k)) {
                    vec2 p_i = getPoint(i);
                    vec2 p_j = getPoint(j);
                    vec2 p_k = getPoint(k);

                    if (inTriangle(uv, p_i, p_j, p_k)) {
                        vec3 tri_color = 0.5 + 0.5 * cos(vec3(i,j,k) * 0.3 + vec3(0,2,4));
                        col = mix(col, tri_color, 0.6);
                    }
                }
            }
        }
    }

    // --- 2. Draw Edges ---
    // We define thickness in terms of our normalized coordinate system
    float line_thickness = 2.0 / iResolution.y; 
    float edge_dist = 1e6;
    for (int i = 0; i < N_TOTAL; i++) {
        for (int j = i + 1; j < N_TOTAL; j++) {
            if (hasEdge(i, j)) {
                edge_dist = min(edge_dist, sdfLine(uv, getPoint(i), getPoint(j)));
            }
        }
    }
    col = mix(vec3(0.9), col, smoothstep(line_thickness * 0.5, line_thickness * 1.5, edge_dist));

    // --- 3. Draw Points ---
    float point_radius = 6.0 / iResolution.y;
    float point_dist = 1e6;
    for (int i = 0; i < N_TOTAL; i++) {
        // Calculate distance in the normalized space
        point_dist = min(point_dist, length(uv - getPoint(i)));
    }
    col = mix(vec3(1.0, 0.8, 0.0), col, smoothstep(point_radius, point_radius + (1.0/iResolution.y), point_dist));


    fragColor = vec4(col, 1.0);
}

#define N_CORNERS 4
#define N_TOTAL (N+ N_CORNERS) // Total points: 32

// --- Helper: Pseudo-random number generator ---
vec2 hash( vec2 p ) {
    p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
    return -1.0 + 2.0 * fract(sin(p) * 43758.5453123); // Output in [-1, 1] range
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 px = ivec2(fragCoord);

    // We only use the first N_TOTAL pixels in the first row.
    if (px.y > 0 || px.x >= N_TOTAL) {
        discard;
        return;
    }

    // --- Feedback loop to preserve data after frame 0 ---
    // Make sure iChannel0 is set to Buffer A itself!
    if (iFrame > 0) {
        fragColor = texelFetch(iChannel0, px, 0);
        return;
    }

    // --- On Frame 0: Generate all points ---
    vec2 point_pos;
    float aspect = iResolution.x / iResolution.y;

    if (px.x < N_CORNERS) {
        // --- Generate the 4 corner points ---
        // We use a bit of math to generate the 4 corners from indices 0, 1, 2, 3
        // index 0: (-aspect, -1)
        // index 1: ( aspect, -1)
        // index 2: (-aspect,  1)
        // index 3: ( aspect,  1)
        float x_sign = (px.x % 2 == 0) ? -1.0 : 1.0;
        float y_sign = (px.x < 2) ? -1.0 : 1.0;
        point_pos = vec2(x_sign * aspect, y_sign);

    } else {
        // --- Generate N_RANDOM random points within the region ---
        // Use the pixel's x-coordinate as a seed.
        // The hash function gives a point in [-1, 1]x[-1, 1].
        // We scale it to fit our [-aspect, aspect]x[-1, 1] region.
        vec2 random_unit = hash(vec2(px.x, 1.0));
        point_pos = random_unit * vec2(aspect, 1.0);
    }

    // Store the point's coordinates in rg channels.
    fragColor = vec4(point_pos, 0.0, 1.0);
}

#define N_CORNERS 4
#define N_TOTAL (N + N_CORNERS)

// Fetches the position of the i-th point from Buffer A (iChannel0)
// The point is already in our desired coordinate system.
vec2 getPoint(int i) {
    return texelFetch(iChannel0, ivec2(i, 0), 0).xy;
}

// ... (The inCircumcircle function remains exactly the same as before) ...
float inCircumcircle(vec2 p_a, vec2 p_b, vec2 p_c, vec2 p_test) {
    float a_sq = dot(p_a, p_a);
    float b_sq = dot(p_b, p_b);
    float c_sq = dot(p_c, p_c);
    float test_sq = dot(p_test, p_test);

    mat4 m = mat4(
        p_a.x, p_a.y, a_sq, 1.0,
        p_b.x, p_b.y, b_sq, 1.0,
        p_c.x, p_c.y, c_sq, 1.0,
        p_test.x, p_test.y, test_sq, 1.0
    );

    float orientation = (p_b.x - p_a.x) * (p_c.y - p_a.y) - (p_b.y - p_a.y) * (p_c.x - p_a.x);
    return determinant(m) * sign(orientation);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 px = ivec2(fragCoord);
    int i = px.x;
    int j = px.y;

    // We are building an N_TOTAL x N_TOTAL adjacency matrix
    if (i >= N_TOTAL || j >= N_TOTAL || i == j) {
        fragColor = vec4(0.0);
        return;
    }

    // Symmetric matrix optimization
    if (i > j) {
        fragColor = texelFetch(iChannel1, ivec2(j, i), 0);
        return;
    }

    vec2 p_i = getPoint(i);
    vec2 p_j = getPoint(j);

    bool is_delaunay_edge = false;

    // Check all possible triangles using the edge (i, j)
    for (int k = 0; k < N_TOTAL; k++) {
        if (k == i || k == j) continue;

        vec2 p_k = getPoint(k);

        // Collinearity check
        vec2 v_ik = p_k - p_i;
        vec2 v_ij = p_j - p_i;
        if (abs(v_ik.x * v_ij.y - v_ik.y * v_ij.x) < 0.0001) continue;

        bool is_empty_circle = true;
        // Check all other points
        for (int m = 0; m < N_TOTAL; m++) {
            if (m == i || m == j || m == k) continue;

            vec2 p_m = getPoint(m);
            if (inCircumcircle(p_i, p_j, p_k, p_m) > 0.0001) { // Add a small epsilon
                is_empty_circle = false;
                break;
            }
        }

        if (is_empty_circle) {
            is_delaunay_edge = true;
            break;
        }
    }

    if (is_delaunay_edge) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(0.0);
    }
    }

/*
The glowing gasket â Fig. 7.3 of Indra's Pearls

Two fundamental domains, each one is a one-punctured torus.

Keyboard controls:
  1/2/3  - Switch mode (limit set, circle packing, tiling)
  w/s    - Zoom in / out
  a/b    - Show the action of generators a / b
  c      - Show the four circles C_a, C_A, C_b, C_B
*/

#define AA 2
#define CURVE_THICKNESS 0.015

const float PI = 3.14159265359;
const int MAX_ITER = 100;

float zoom;
int mode;
int draw_curve;

#define L2(x) dot(x, x)

bool Equal(vec2 z, vec2 a, float deriv) {
    float k = 4. + 4. * L2(z) ;
    return k * length(z - a) * deriv / zoom < CURVE_THICKNESS;
}

vec2 u1, u2;

vec2 get_tangent(vec3 c, vec3 d) {
    vec2 dir = normalize(c.xy - d.xy);
    return dir * d.r + d.xy;
}

vec2 cmul(vec2 z, vec2 w) {
    return vec2(z.x * w.x - z.y * w.y, z.x * w.y + z.y * w.x);
}

vec2 cdiv(vec2 z, vec2 w) {
    float d2 = dot(w, w);
    if (d2 < 1e-15)
        return vec2(1e7);
    return cmul(z, vec2(w.x, -w.y)) / d2;
}

struct Mobius {
    vec2 a, b, c, d;
};

vec2 applyMobius(Mobius m, vec2 z) {
    vec2 num = cmul(m.a, z) + m.b;
    vec2 den = cmul(m.c, z) + m.d;
    return cdiv(num, den);
}

Mobius invMobius(Mobius m) { return Mobius(m.d, -m.b, -m.c, m.a); }

Mobius interpolateMobius(Mobius m1, Mobius m2, float t) {
    return Mobius(mix(m1.a, m2.a, t), mix(m1.b, m2.b, t), mix(m1.c, m2.c, t),
                  mix(m1.d, m2.d, t));
}

const Mobius identity = Mobius(vec2(1, 0), vec2(0), vec2(0), vec2(1, 0));

const Mobius a = Mobius(vec2(1, 0), vec2(0), vec2(0, -2), vec2(1, 0));

const Mobius b = Mobius(vec2(1, -1), vec2(1, 0), vec2(1, 0), vec2(1, 1));

Mobius A, B;

void setup() {
    A = invMobius(a);
    B = invMobius(b);
    u1 = get_tangent(vec3(0, -0.25, 0.25), vec3(-1, -1, 1));
    u2 = get_tangent(vec3(0, -0.25, 0.25), vec3(1, -1, 1));
}

const float rr = 0.25;
const float ss = 1. / 3.;
const vec3 circ_B = vec3(-1, -1, 1);
const vec3 circ_b = vec3(1, -1, 1);
const vec3 circ_A = vec3(0, -rr, rr);
const vec3 trap1 = vec3(-0.5, 0, 0.5);
const vec3 trap2 = vec3(0.5, 0, 0.5);
const vec3 trap3 = vec3(0., ss - 1., ss);

float sdTrapped(vec2 z) {
    float db = length(z - circ_b.xy) - circ_b.z;
    float dB = length(z - circ_B.xy) - circ_B.z;
    float dA = length(z - circ_A.xy) - circ_A.z;
    float da = -z.y;
    return min(min(da, dA), min(db, dB));
}

void fold(inout vec2 z, inout float count, inout float deriv) {
    for (int i = 0; i < MAX_ITER; i++) {
        bool found = true;
        if (z.y > 0.) {
            deriv *= L2(cmul(A.c, z) + A.d);
            z = applyMobius(A, z);
            count += 1.;
            found = false;
            continue;
        }
        if (length(z - circ_A.xy) < circ_A.z) {
            deriv *= L2(cmul(a.c, z) + a.d);
            z = applyMobius(a, z);
            count += 1.;
            found = false;
            continue;
        }
        if (length(z - circ_b.xy) < circ_b.z) {
            deriv *= L2(cmul(B.c, z) + B.d);
            z = applyMobius(B, z);
            count += 1.;
            found = false;
            continue;
        }
        if (length(z - circ_B.xy) < circ_B.z) {
            deriv *= L2(cmul(b.c, z) + b.d);
            z = applyMobius(b, z);
            count += 1.;
            found = false;
            continue;
        }
        if (found)
            break;
    }
}

vec3 getColor(vec2 z, in float deriv) {
    float scale = deriv;
    vec2 w = z;
    float count = 0.0;
    fold(z, count, deriv);
    float id;
    vec2 pts[] =
            vec2[](vec2(-1, 0), vec2(1, 0), vec2(0), vec2(0, -1), vec2(0,1),u1, u2);
    if (dot(z, z) > 1.) {
        id = 0.;
    } else if (length(z - trap1.xy) < trap1.z) {
        id = 1.;
    } else if (length(z - trap2.xy) < trap2.z) {
        id = 1.;
    } else if (length(z - trap3.xy) < trap3.z) {
        id = 2.;
    } else
        id = 2.;

    vec3 col;
    if (mode == 0) {
        if (count >= float(MAX_ITER))
            col = vec3(0);
        else if (count == 0.)
            col = vec3(0);
        else {
            col = 0.5 + 0.5 * cos(
                count * PI / 15. + vec3(4.4, 3.2, 2.)
            );

        }
    } else if (mode == 1) {

        if (id == 1.) {
            col = vec3(1, 0.5, 0.75);
        } else {
            col = vec3(1);
        }
        for (int i = 0; i < pts.length(); i++) {
            if (Equal(z, pts[i], deriv)) {
                col = vec3(0);
                break;
            }
        }
    }
    else {
        float k = mod(count, 2.);
        col = 0.5 + 0.45 * cos(k * 9. + vec3(0, 1, 2));
        if (id != 1.) {
            if (k == 1.)
                col = vec3(1);
            else
                col = vec3(0.9, 0.9, 0.97);
        }
        for (int i = 0; i < pts.length(); i++) {
            if (Equal(z, pts[i], deriv)) {
                col = vec3(0);
                break;
            }
        }
    }
    if (draw_curve > 0) {
        float d1 = min(-w.y, length(w - circ_A.xy) - circ_A.z);
        float d2 = length(w - circ_b.xy) - circ_b.z;
        d2 = min(d2, length(w - circ_B.xy) - circ_B.z);
        d1 = abs(d1) * scale / zoom;
        d2 = abs(d2) * scale / zoom;
        float lw = 0.0125;
        float aa1 = fwidth(d1);
        float aa2 = fwidth(d2);
        col = mix(col, vec3(0, 0, 1), 1. - smoothstep(lw - aa2, lw + aa2, d2));
        col = mix(col, vec3(1, 0, 0), 1. - smoothstep(lw - aa1, lw + aa1, d1));
    }
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    setup();

    vec4 pan_vals = textureLod(iChannel0, vec2(0.5, 0.5) / iResolution.xy, 0.0);
    zoom = textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;
    float ab_anim_t =
        textureLod(iChannel0, vec2(1.5, 0.5) / iResolution.xy, 0.0).x;
    vec4 c_state = textureLod(iChannel0, vec2(2.5, 0.5) / iResolution.xy, 0.0);

    mode = int(textureLod(iChannel0, vec2(3.5, 0.5) / iResolution.xy, 0.0).x);
    draw_curve =
        int(textureLod(iChannel0, vec2(4.5, 0.5) / iResolution.xy, 0.0).x);
    int k_from = int(c_state.x);
    int k_to = int(c_state.y);
    float anim_timer = c_state.z;

    Mobius final_transform = identity;
    if (abs(ab_anim_t) > 0.01) {
        if (ab_anim_t > 0.0) {
            final_transform = interpolateMobius(identity, A, ab_anim_t);
        } else {
            final_transform = interpolateMobius(identity, B, abs(ab_anim_t));
        }
    }
    vec3 col = vec3(0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * (fragCoord + o) - iResolution.xy -
                       pan_vals.xy * pow(zoom, -1.0)) /
                      iResolution.y;
            uv *= zoom * 1.05;
            vec2 den_g = cmul(final_transform.c, uv) + final_transform.d;
            float deriv = dot(den_g, den_g);
            vec2 z = applyMobius(final_transform, uv);
            col += getColor(z, deriv);
        }
    }
    col /= (float(AA) * float(AA));
    fragColor = vec4(col, 1);
}

