float ReadKeyInternal(int key, bool toggle) {
    return textureLod(iChannel1,
                      vec2((float(key) + .5) / 256.0, toggle ? .75 : .25), 0.0)
        .x;
}

float ReadKey(int key) { return ReadKeyInternal(key, false); }

float ReadKeyToggle(int key) { return ReadKeyInternal(key, true); }

// Storage Code For Pan and Zoom

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Default output for unused pixels
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // Pan State at (0,0)
    if (ivec2(fragCoord.xy) == ivec2(0, 0)) {
        vec4 prev_info = textureLod(iChannel0, vec2(0.0, 0.0), 0.0);
        float Zoom =
            textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;
        float prev_pan_x = prev_info.x;
        float prev_pan_y = prev_info.y;
        float legacy_pan_x = prev_info.z;
        float legacy_pan_y = prev_info.w;

        float pan_x = (iMouse.x - abs(iMouse.z)) * Zoom + legacy_pan_x;
        float pan_y = (iMouse.y - abs(iMouse.w)) * Zoom + legacy_pan_y;

        if (iMouse.z < 0.0) {
            legacy_pan_x = prev_pan_x;
            legacy_pan_y = prev_pan_y;
            pan_x = legacy_pan_x;
            pan_y = legacy_pan_y;
        }
        fragColor = vec4(pan_x, pan_y, legacy_pan_x, legacy_pan_y);
    }

    // Zoom State at (10,10)
    if (ivec2(fragCoord.xy) == ivec2(10, 10)) {
        float x =
            textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).y;
        x += ReadKey(Key_W) - ReadKey(Key_S);
        float Zoom = pow(2.71, -x * 0.05);
        fragColor = vec4(Zoom, x, 0.0, 0.0);
    }

    // Animation State at (1,0)
    if (ivec2(fragCoord.xy) == ivec2(1, 0)) {
        // Read previous animation state
        float prev_anim_t = texelFetch(iChannel0, ivec2(1, 0), 0).x;

        float key_a = ReadKey(Key_A);
        float key_b = ReadKey(Key_B);

        float target_t = 0.0;
        if (key_a > 0.0) {
            target_t = 1.0;
        } else if (key_b > 0.0) {
            target_t = -1.0;
        }
        float speed = 3.;
        float anim_t =
            mix(prev_anim_t, target_t, 1.0 - exp(-iTimeDelta * speed));
        fragColor = vec4(anim_t, 0.0, 0.0, 1.0);
    }
    if (ivec2(fragCoord.xy) == ivec2(2, 0)) {
        vec4 prev_c_state = texelFetch(iChannel0, ivec2(2, 0), 0);
        float k_from = prev_c_state.x;
        float k_to = prev_c_state.y;
        float anim_timer = prev_c_state.z;
        float prev_key_c_state = prev_c_state.w;

        float key_c = ReadKey(Key_C);
        if (anim_timer < 1.0) {
            float anim_speed = 1.;
            anim_timer += iTimeDelta * anim_speed;
        }
        anim_timer = min(anim_timer, 1.0);

        if (key_c > 0.0 && prev_key_c_state == 0.0 && anim_timer >= 1.0) {
            k_from = k_to;
            k_to = mod(k_to + 1.0, 16.0);
            anim_timer = 0.0;
        }

        fragColor = vec4(k_from, k_to, anim_timer, key_c);
    }
    if (ivec2(fragCoord.xy) == ivec2(3, 0)) {
        float current_mode = texelFetch(iChannel0, ivec2(3, 0), 0).x;
        if (iFrame < 2)
            current_mode = 0.0;
        if (ReadKey(Key_1) > 0.0)
            current_mode = 0.0;
        if (ReadKey(Key_2) > 0.0)
            current_mode = 1.0;
        if (ReadKey(Key_3) > 0.0)
            current_mode = 2.0;

        fragColor = vec4(current_mode, 0.0, 0.0, 1.0);
    }
    if (ivec2(fragCoord.xy) == ivec2(4, 0)) {
        vec4 prev_state = texelFetch(iChannel0, ivec2(4, 0), 0);
        float current_toggle = prev_state.x;
        float prev_key_c_state = prev_state.y;

        float key_c = ReadKey(Key_C);
        if (key_c > 0.0 && prev_key_c_state == 0.0) {
            current_toggle = 1.0 - current_toggle;
        }
        fragColor = vec4(current_toggle, key_c, 0.0, 1.0);
    }
}

/*
The glowing gasket â Fig. 7.3 of Indra's Pearls

Two fundamental domains, each one is a one-punctured torus.

Keyboard controls:
  1/2/3  - Switch mode
  w/s    - Zoom in / out
  a/b    - Show the action of generators a / b
  c      - Show the four circles C_a, C_A, C_b, C_B
*/

#define AA 2

const float PI = 3.14159265359;
const int MAX_ITER = 100;

float zoom;
int mode;
int draw_curve;

#define L2(x) dot(x, x)

vec2 cmul(vec2 z, vec2 w) {
    return vec2(z.x * w.x - z.y * w.y, z.x * w.y + z.y * w.x);
}

vec2 cdiv(vec2 z, vec2 w) {
    float d2 = dot(w, w);
    if (d2 < 1e-15)
        return vec2(1e7);
    return cmul(z, vec2(w.x, -w.y)) / d2;
}

struct Mobius {
    vec2 a, b, c, d;
};

vec2 applyMobius(Mobius m, vec2 z) {
    vec2 num = cmul(m.a, z) + m.b;
    vec2 den = cmul(m.c, z) + m.d;
    return cdiv(num, den);
}

Mobius invMobius(Mobius m) { return Mobius(m.d, -m.b, -m.c, m.a); }

Mobius interpolateMobius(Mobius m1, Mobius m2, float t) {
    return Mobius(mix(m1.a, m2.a, t), mix(m1.b, m2.b, t), mix(m1.c, m2.c, t),
                  mix(m1.d, m2.d, t));
}

const Mobius identity = Mobius(vec2(1, 0), vec2(0), vec2(0), vec2(1, 0));

const Mobius a = Mobius(vec2(1, 0), vec2(0, 0), vec2(-2, 0), vec2(1, 0));
const Mobius A = Mobius(vec2(1, 0), vec2(0, 0), vec2(2, 0), vec2(1, 0));

const Mobius b = Mobius(vec2(1, 0), vec2(2, 0), vec2(0, 0), vec2(1, 0));
const Mobius B = Mobius(vec2(1, 0), vec2(-2, 0), vec2(0, 0), vec2(1, 0));
const Mobius U2P = Mobius(
    vec2(0, -1), vec2(0, -1), vec2(1, 0), vec2(0, -1)
);
const Mobius P2U = Mobius(
    vec2(0, 1), vec2(0, 1), vec2(-1, 0), vec2(1, 0)
);
const float rr = 0.5;
const vec3 circ_a = vec3(-rr, 0, rr);
const vec3 circ_A = vec3(rr, 0, rr);

// A simple 1D hash function - takes a float, returns a float
float hash1(float n) {
    return fract(sin(n) * 43758.5453123);
}

// A hash function that returns a more "structured" random number, good for patterns
float hash1b(float n) {
    // The constants are just large prime numbers for good pseudo-random properties
    return fract(sin(n*12.9898)*43758.5453);
}
float sdTrapped(vec2 z) {
    float da = length(z - circ_a.xy) - circ_a.z;
    float dA = length(z - circ_A.xy) - circ_A.z;
    float dB = z.x + 1.;
    float db = 1. - z.x;
    return min(min(da, dA), min(db, dB));
}

void fold(inout vec2 z, inout float count, inout float deriv) {
    for (int i = 0; i < MAX_ITER; i++) {
        bool found = true;
        if (length(z - circ_a.xy) < circ_a.z) {
            deriv *= L2(cmul(A.c, z) + A.d);
            z = applyMobius(A, z);
            count += 1.;
            found = false;
            continue;
        }
        if (length(z - circ_A.xy) < circ_A.z) {
            deriv *= L2(cmul(a.c, z) + a.d);
            z = applyMobius(a, z);
            count += 1.;
            found = false;
            continue;
        }
        if (z.x > 1.) {
            z = applyMobius(B, z);
            count += 1.;
            found = false;
            continue;
        }
        if (z.x < -1.) {
            z = applyMobius(b, z);
            count += 1.;
            found = false;
            continue;
        }
        if (found)
            break;
    }
}

vec3 getColor(vec2 z, in float deriv, vec2 fragCoord) {
    vec2 w = z; // The coordinate in the Upper-Half-Plane
    float count = 0.0;
    float scale = deriv;
    // We need a separate variable for the coloring decision
    vec2 z_for_coloring = z;
    float count_for_coloring = 0.0;
    float deriv_for_coloring = deriv;
    fold(z_for_coloring, count_for_coloring, deriv_for_coloring);

    // Determine the base color and if we are in a hatchable area
    float id = (z_for_coloring.x < 0.) ? 0. : 1.;
    bool in_hatch_area = (id == 1.0);
    vec3 col = in_hatch_area ? vec3(.6, .9, 0.75) : vec3(1.0);

    // --- START OF BOUNDARY-AWARE HATCHING LOGIC ---

    // 1. Parameters
    float line_spacing = 0.09; // The distance between each horizontal line

    // 2. Check if the current pixel's y-level is designated for a line
    float lane_id = floor(w.y / line_spacing);
    float lane_pos = fract(w.y / line_spacing);

    // We only draw a line if we are on a specific "lane"
    // and if the base color is the one we want to hatch
    if (abs(lane_pos - 0.5) < 0.05 && in_hatch_area) { // Draw line in the middle of the lane

        // 3. Find the left and right boundaries of the region at this y-level
        // We use a simple root-finding (bisection-like) method
        float step = 0.1; // Initial search step, can be adjusted

        // Search for the left boundary (-x direction)
        vec2 left_pos = w;
        bool left_boundary_found = false;
        for(int i = 0; i < 15; i++) {
            vec2 p = left_pos;
            float c = 0.0, drv = deriv;
            fold(p, c, drv);
            if (p.x < 0.0) { // We crossed into the white region
                left_pos += vec2(step, 0.0);
                step *= 0.5; // Refine search
            } else {
                left_pos -= vec2(step, 0.0);
                left_boundary_found = true;
            }
        }

        // Search for the right boundary (+x direction)
        vec2 right_pos = w;
        step = 0.1; // Reset step
        bool right_boundary_found = false;
        for(int i = 0; i < 15; i++) {
            vec2 p = right_pos;
            float c = 0.0, drv = deriv;
            fold(p, c, drv);
            if (p.x < 0.0) { // We are still in the white region (error case) or started there
                // This case is complex, for simplicity we assume we start in the green region
                // If not, we just won't find a boundary
                break;
            } else { // We are in the green region
                right_pos += vec2(step, 0.0);
                right_boundary_found = true;
            }
        }

        // After finding we are in green, we need to find where it becomes white
        right_pos = w;
        step = 0.1;
        for(int i=0; i<15; i++) {
             vec2 p = right_pos;
             float c = 0.0, drv = deriv;
             fold(p,c,drv);
             if (p.x > 0.0) { // Still in green
                 right_pos += vec2(step, 0.0);
             } else { // Crossed to white
                 right_pos -= vec2(step, 0.0);
                 step *= 0.5;
             }
        }

        // 4. If both boundaries are valid, draw the line
        if (left_boundary_found && right_boundary_found) {
            vec3 hatch_color = vec3(0.3, 0.5, 0.4);

        // è°ç¨éç¬çº¹ç
        vec2 qq = w * 8.0 * (iResolution.y / 450.0);
        vec3 pencil_col = pencil(hatch_color, qq, 4);

        // æ··ååè²ä¸éç¬çº¹ç
        col = mix(hatch_color, pencil_col, 0.85);
        }
    }

    // --- END OF HATCHING LOGIC ---

    // Draw the main black boundary lines on top of everything
    vec2 p_boundary = w;
    float c_boundary = 0.0, drv_boundary = deriv;
    fold(p_boundary, c_boundary, drv_boundary);
    float d = abs(sdTrapped(p_boundary)) / zoom;
    float d2 = abs(p_boundary.x) / zoom;
    float aa2 = fwidth(d2) / 2.;
    float aa = fwidth(d) / 2.0;
    float lw = 0.012;
    col = mix(col, vec3(0), 1. - smoothstep(lw-aa, lw+aa, d));
     col = mix(col, vec3(0.5), 1. - smoothstep(lw-aa2, lw+aa2, d2));

    // Draw debug curves (unchanged)
    if (draw_curve > 0) {
        float d1 = min(1. - w.x, w.x + 1.);
        float d2 = length(w - circ_a.xy) - circ_a.z;
        d2 = min(d2, length(w - circ_A.xy) - circ_A.z);
        d1 = abs(d1) * scale / zoom;
        d2 = abs(d2) * scale / zoom;
        float lw = 0.0125;
        float aa1 = fwidth(d1);
        float aa2 = fwidth(d2);
        col = mix(col, vec3(0, 0, 1), 1. - smoothstep(lw - aa2, lw + aa2, d2));
        col = mix(col, vec3(1, 0, 0), 1. - smoothstep(lw - aa1, lw + aa1, d1));
    }
    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 pan_vals = textureLod(iChannel0, vec2(0.5, 0.5) / iResolution.xy, 0.0);
    zoom = textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;
    float ab_anim_t =
        textureLod(iChannel0, vec2(1.5, 0.5) / iResolution.xy, 0.0).x;
    vec4 c_state = textureLod(iChannel0, vec2(2.5, 0.5) / iResolution.xy, 0.0);

    mode = int(textureLod(iChannel0, vec2(3.5, 0.5) / iResolution.xy, 0.0).x);
    draw_curve =
        int(textureLod(iChannel0, vec2(4.5, 0.5) / iResolution.xy, 0.0).x);
    int k_from = int(c_state.x);
    int k_to = int(c_state.y);
    float anim_timer = c_state.z;

    const Mobius identity =
        Mobius(vec2(1, 0), vec2(0), vec2(0),
               vec2(1, 0)); 
    Mobius final_transform = identity;
    if (abs(ab_anim_t) > 0.01) {
        if (ab_anim_t > 0.0) {
            final_transform = interpolateMobius(identity, A, ab_anim_t);
        } else {
            final_transform = interpolateMobius(identity, B, abs(ab_anim_t));
        }
    }
    vec3 col = vec3(0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);

            // Screen coordinates setup
            vec2 uv = (2. * (fragCoord + o) - iResolution.xy) / iResolution.y;
            uv -= pan_vals.xy * pow(zoom, -1.0) / iResolution.y * 2.0;
            uv *= zoom;

            float deriv = 1.0;

            if (mode == 1) {
                if (length(uv) > 1.0) {
                    col += vec3(0.0);
                    continue;
                }

                deriv *= L2(cdiv(vec2(1.0), cmul(P2U.c, uv) + P2U.d));
                uv = applyMobius(P2U, uv);
            }
            else {
                uv *= 1.5;
                uv.y += 1.5;
            }

            vec2 den = cmul(final_transform.c, uv) + final_transform.d;
            deriv *= L2(den);
            vec2 z = applyMobius(final_transform, uv);

            // STEP 2: Pass the screen coordinate to getColor
            col += getColor(z, deriv, fragCoord + o); 
        }
    }
    col /= (float(AA) * float(AA));

    if (mode == 1) {
        vec2 uv_center = (2. * fragCoord - iResolution.xy) / iResolution.y;
        uv_center -= pan_vals.xy * pow(zoom, -1.0) / iResolution.y * 2.0;
        uv_center *= zoom;
        col = mix(vec3(0.1), col, smoothstep(1.0, 1.0 - 2.0/iResolution.y, length(uv_center)));
    }

    fragColor = vec4(col, 1);
}

