float hash21(vec2 p) {
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

// ç®æ fbmï¼ç¨äº pencil() ç¬è§¦æ°å¨
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    float a = hash21(i);
    float b = hash21(i + vec2(1,0));
    float c = hash21(i + vec2(0,1));
    float d = hash21(i + vec2(1,1));
    vec2 u = f*f*(3.0-2.0*f);
    return mix(a, b, u.x) + (c - a)*u.y*(1.0 - u.x) + (d - b)*u.x*u.y;
}

float fbm(vec2 p) {
    float f = 0.0;
    f += 0.5  * noise(p); p *= 2.02;
    f += 0.25 * noise(p); p *= 2.03;
    f += 0.125* noise(p); p *= 2.01;
    f += 0.0625*noise(p);
    return f;
}

// éç¬çº¹çå½æ°
vec3 pencil(vec3 baseCol, vec2 uv, int darkLines) {
    // ç¬è§¦æ°å¨
    float pat = fbm(uv*1.5) * 0.8 + fbm(uv*4.0) * 0.2;
    // å æ¹åæ§æ¡çº¹
    float lines = sin(uv.x*60.0 + pat*2.0);
    float mask = smoothstep(-0.2, 0.2, lines);
    vec3 ink = mix(vec3(1.0), vec3(0.0), mask * 0.6);
    vec3 finalCol = baseCol * ink;
    if (darkLines == 1) {
        finalCol *= 0.85;
    }
    return finalCol;
}

#define CELL_SIZE 7.0
#define GRID_WIDTH  floor(iResolution.x / CELL_SIZE)
#define GRID_HEIGHT floor(iResolution.y / CELL_SIZE)

#define STATE_WALL 0.0
#define STATE_TREE 1.0

vec4 getCell(ivec2 pos) {
    if (pos.x < 0 || pos.x >= int(GRID_WIDTH) ||
        pos.y < 0 || pos.y >= int(GRID_HEIGHT)) {
        return vec4(STATE_WALL);
    }
    return texelFetch(iChannel0, pos, 0); // iChannel0 is Buffer A
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 grid_pixel_size = vec2(GRID_WIDTH, GRID_HEIGHT) * CELL_SIZE;
    vec2 offset = (iResolution.xy - grid_pixel_size) / 2.0;
    ivec2 p_grid = ivec2(floor((fragCoord - offset) / CELL_SIZE));

    if (p_grid.x < 0 || p_grid.x >= int(GRID_WIDTH) ||
        p_grid.y < 0 || p_grid.y >= int(GRID_HEIGHT)) {
        fragColor = vec4(0.05, 0.0, 0.1, 1.0);
        return;
    }

    vec4 cell_main = getCell(p_grid);
    float state = cell_main.r;

    vec3 finalColor = vec3(0.1, 0.1, 0.15); // Wall

    if (state > STATE_TREE) { 
        finalColor = vec3(1.0, 0.6, 0.0); // Path is bright orange
    } else if (state == STATE_TREE) {
        finalColor = vec3(1.0); // Tree is white
    }

    // Gap filling logic
    if (state == STATE_WALL) {
        vec4 N = getCell(p_grid + ivec2(0, 1)); vec4 S = getCell(p_grid - ivec2(0, 1));
        vec4 E = getCell(p_grid + ivec2(1, 0)); vec4 W = getCell(p_grid - ivec2(1, 0));
        if (N.r > STATE_TREE && S.r > STATE_TREE) finalColor = vec3(1.0, 0.6, 0.0);
        if (E.r > STATE_TREE && W.r > STATE_TREE) finalColor = vec3(1.0, 0.6, 0.0);
        if (N.r == STATE_TREE && S.r == STATE_TREE) finalColor = vec3(1.0);
        if (E.r == STATE_TREE && W.r == STATE_TREE) finalColor = vec3(1.0);
    }

    fragColor = vec4(finalColor, 1.0);
}

#define CELL_SIZE 7.0
#define SPEED 1

// --- DERIVED CONSTANTS ---
#define GRID_WIDTH  floor(iResolution.x / CELL_SIZE)
#define GRID_HEIGHT floor(iResolution.y / CELL_SIZE)

#define STATE_WALL 0.0
#define STATE_TREE 1.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    ivec2 p = ivec2(fragCoord);

    if (float(p.x) >= GRID_WIDTH || float(p.y) >= GRID_HEIGHT) {
        fragColor = vec4(0.0); return;
    }

    // --- INITIALIZATION ---
    if (iFrame < 5 || texelFetch(iChannel1, ivec2(0), 0).x < 0.0) { // Reset on click
        fragColor = vec4(STATE_WALL);
        return;
    }

    vec4 prev_cell = texelFetch(iChannel0, p, 0);
    // Read PREVIOUS agent state from Buffer B
    vec4 prev_agent = texelFetch(iChannel1, ivec2(0), 0);
    fragColor = prev_cell;

    // --- GRID LOGIC ---
    // The only thing the grid does is mark the agent's previous position.
    if (p == ivec2(prev_agent.xy)) {
        fragColor.r = 100.0; // Mark as path with a high value
    }
}

#define CELL_SIZE 7.0
#define SPEED 1

// --- DERIVED CONSTANTS ---
#define GRID_WIDTH  floor(iResolution.x / CELL_SIZE)
#define GRID_HEIGHT floor(iResolution.y / CELL_SIZE)

// Helper functions
float rand(vec2 co){ return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453); }

ivec2 randomNeighbour(ivec2 cell, float seed) {
    ivec2 neighbours[4];
    int count = 0;
    if (cell.x >= 2)   neighbours[count++] = ivec2(cell.x - 2, cell.y);
    if (cell.y >= 2)   neighbours[count++] = ivec2(cell.x, cell.y - 2);
    if (cell.x <= int(GRID_WIDTH) - 3)  neighbours[count++] = ivec2(cell.x + 2, cell.y);
    if (cell.y <= int(GRID_HEIGHT) - 3) neighbours[count++] = ivec2(cell.x, cell.y + 2);
    if (count == 0) return cell;
    return neighbours[int(floor(seed * float(count)))];
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // This shader only needs to run for the single pixel at (0,0)
    if (ivec2(fragCoord) != ivec2(0)) {
        fragColor = vec4(0.0);
        return;
    }

    // --- INITIALIZATION ---
    if (iFrame < 5 || iMouse.z > 0.0) {
        // Start walking from a fixed position
        // We use a negative X value as a "reset" signal for Buffer A
        if (iMouse.z > 0.0) {
             fragColor = vec4(-1.0, 0.0, 0.0, 0.0);
        } else {
             fragColor = vec4(floor(GRID_WIDTH/2.0)*2.0+1.0, floor(GRID_HEIGHT/2.0)*2.0+1.0, 2.0, 1.0);
        }
        return;
    }

    vec4 prev_agent = texelFetch(iChannel0, ivec2(0), 0);
    vec4 agent = prev_agent;

    for (int k=0; k<SPEED; ++k) {
        ivec2 current_pos = ivec2(agent.xy);
        float seed = rand(vec2(iTime, agent.x * 13.7 + agent.y * 3.1 + agent.z));
        ivec2 next_pos = randomNeighbour(current_pos, seed);
        agent = vec4(vec2(next_pos), agent.z + 1.0, 1.0);
    }

    fragColor = agent;
}

#define AA 2

const float PI = 3.14159265359;
const int MAX_ITER = 100;

float zoom;
int mode;
int draw_curve;

#define L2(x) dot(x, x)

vec2 cmul(vec2 z, vec2 w) {
    return vec2(z.x * w.x - z.y * w.y, z.x * w.y + z.y * w.x);
}

vec2 cdiv(vec2 z, vec2 w) {
    float d2 = dot(w, w);
    if (d2 < 1e-15)
        return vec2(1e7);
    return cmul(z, vec2(w.x, -w.y)) / d2;
}

struct Mobius {
    vec2 a, b, c, d;
};

vec2 applyMobius(Mobius m, vec2 z) {
    vec2 num = cmul(m.a, z) + m.b;
    vec2 den = cmul(m.c, z) + m.d;
    return cdiv(num, den);
}

Mobius invMobius(Mobius m) { return Mobius(m.d, -m.b, -m.c, m.a); }

Mobius interpolateMobius(Mobius m1, Mobius m2, float t) {
    return Mobius(mix(m1.a, m2.a, t), mix(m1.b, m2.b, t), mix(m1.c, m2.c, t),
                  mix(m1.d, m2.d, t));
}

const Mobius I = Mobius(vec2(1, 0), vec2(0), vec2(0), vec2(1, 0));
Mobius a, b, A, B;

float sdFundDomainUHP(vec2 z) {
    float da = length(z - vec2(-0.5, 0)) - 0.5;
    float dA = length(z - vec2(0.5, 0)) - 0.5;
    float dB = z.x + 1.;
    float db = 1. - z.x;
    return min(min(da, dA), min(db, dB));
}

void foldUHP(inout vec2 z, inout float count, inout float deriv) {
    for (int i = 0; i < MAX_ITER; i++) {
        bool found = true;
        if (length(z - vec2(-0.5, 0)) < 0.5) {
            deriv *= L2(cmul(A.c, z) + A.d);
            z = applyMobius(A, z);
            count += 1.;
            found = false;
            continue;
        }
        if (length(z - vec2(0.5, 0)) < 0.5) {
            deriv *= L2(cmul(a.c, z) + a.d);
            z = applyMobius(a, z);
            count += 1.;
            found = false;
            continue;
        }
        if (z.x > 1.) {
            z.x -= 2.;
            count += 1.;
            found = false;
            continue;
        }
        if (z.x < -1.) {
            z.x += 2.;
            count += 1.;
            found = false;
            continue;
        }
        if (found)
            break;
    }
}

void setup() {

    a = Mobius(vec2(1, 0), vec2(0, 0), vec2(-2, 0), vec2(1, 0));
    A = Mobius(vec2(1, 0), vec2(0, 0), vec2(2, 0), vec2(1, 0));

    b = Mobius(vec2(1, 0), vec2(2, 0), vec2(0, 0), vec2(1, 0));
    B = Mobius(vec2(1, 0), vec2(-2, 0), vec2(0, 0), vec2(1, 0));
}

vec3 getColor(vec2 z, in float deriv, vec2 fragCoord) {
    vec2 w = z;
    float count = 0.0;
    float scale = deriv;

    foldUHP(z, count, deriv);

    float id = (z.x < 0.) ? 0. : 1.;
    vec3 col = (id == 1.) ? vec3(.6, .9, 0.75) : vec3(1.0);

    float d = abs(sdFundDomainUHP(z)) / zoom;
    float d2 = abs(z.x) / deriv / zoom;
    float aa = fwidth(d) / 2.0;
    float aa2 = fwidth(d2) / 2.;
    float lw = 0.004;
    col = mix(col, vec3(0), 1. - smoothstep(0., 0.01, d-aa));
    //col = mix(col, vec3(0.5), 1. - smoothstep(lw - aa2, lw + aa2, d2));

    if (draw_curve > 0) {
        float d1 = min(1. - w.x, w.x + 1.);
        float d2 = length(w - vec2(-0.5, 0)) - 0.5;
        d2 = min(d2, length(w - vec2(0.5, 0)) - 0.5);
        d1 = abs(d1) * scale / zoom;
        d2 = abs(d2) * scale / zoom;
        float lw = 0.0125;
        float aa1 = fwidth(d1);
        float aa2 = fwidth(d2);
        col = mix(col, vec3(0, 0, 1), 1. - smoothstep(lw - aa2, lw + aa2, d2));
        col = mix(col, vec3(1, 0, 0), 1. - smoothstep(lw - aa1, lw + aa1, d1));
    }
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    setup();

    vec4 pan_vals = textureLod(iChannel0, vec2(0.5, 0.5) / iResolution.xy, 0.0);
    zoom = textureLod(iChannel0, vec2(10.5, 10.5) / iResolution.xy, 0.0).x;
    float ab_anim_t =
        textureLod(iChannel0, vec2(1.5, 0.5) / iResolution.xy, 0.0).x;
    vec4 c_state = textureLod(iChannel0, vec2(2.5, 0.5) / iResolution.xy, 0.0);

    mode = int(textureLod(iChannel0, vec2(3.5, 0.5) / iResolution.xy, 0.0).x);
    draw_curve =
        int(textureLod(iChannel0, vec2(4.5, 0.5) / iResolution.xy, 0.0).x);
    int k_from = int(c_state.x);
    int k_to = int(c_state.y);
    float anim_timer = c_state.z;

    Mobius final_transform = I;
    if (abs(ab_anim_t) > 0.01) {
        if (ab_anim_t > 0.0) {
            final_transform = interpolateMobius(I, A, ab_anim_t);
        } else {
            final_transform = interpolateMobius(I, B, abs(ab_anim_t));
        }
    }
    vec3 col = vec3(0);
    for (int ii = 0; ii < AA; ii++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * (fragCoord + o) - iResolution.xy) / iResolution.y;
            uv -= pan_vals.xy * pow(zoom, -1.0) / iResolution.y * 2.0;
            uv *= 1.5;
            uv.y += 1.5;
            uv *= zoom;

            float deriv = 1.0;
            vec2 den = cmul(final_transform.c, uv) + final_transform.d;
            deriv *= L2(den);
            vec2 z = applyMobius(final_transform, uv);
            col += getColor(z, deriv, fragCoord + o);
        }
    }
    col /= (float(AA) * float(AA));
    fragColor = vec4(col, 1);
}

