#define AA 3

const float TUBE_RADIUS = 0.035;
const float MAX_DIST = 100.0;
const float SURF_DIST = 0.001;

int gcd(int a, int b) {
    a = abs(a);
    b = abs(b);
    while (b != 0) {
        int temp = b;
        b = a % b;
        a = temp;
    }
    return (a == 0) ? 1 : a;
}

float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}
float sdGeodesicTube(vec3 pos, vec2 tor, float p_t, float q_t) {
    float d_torus = sdTorus(pos, tor);
    float u = atan(pos.z, pos.x);
    float v = atan(pos.y, length(pos.xz) - tor.x + 1e-9);
    float val = p_t * u - q_t * v;
    float s = abs(mod(val + PI, 2. * PI) - PI);
    float n = length(vec2(p_t, q_t));
    if (n < 0.1)
        n = 1.;
    float c = s / n;
    float d_geodesic = tor.y * c;
    return length(vec2(d_torus, d_geodesic)) - TUBE_RADIUS;
}
vec2 map(vec3 pos, vec2 tor, float p_t, float q_t) {
    float dt = sdTorus(pos, tor);
    float dtu = sdGeodesicTube(pos, tor, p_t, q_t);
    if (dtu < dt)
        return vec2(dtu, 2.);
    return vec2(dt, 1.);
}
vec3 calcNormal(vec3 pos, vec2 tor, float p_t, float q_t) {
    vec2 e = vec2(.001, 0.);
    float d = map(pos, tor, p_t, q_t).x;
    return normalize(vec3(d - map(pos - e.xyy, tor, p_t, q_t).x,
                          d - map(pos - e.yxy, tor, p_t, q_t).x,
                          d - map(pos - e.yyx, tor, p_t, q_t).x));
}
vec2 raymarch(vec3 ro, vec3 rd, vec2 tor, float p_t, float q_t) {
    float t = 0., m = -1.;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + t * rd;
        vec2 res = map(p, tor, p_t, q_t);
        float d = res.x;
        if (!(d > -1000. && d < 1000.))
            break;
        m = res.y;
        if (d < SURF_DIST || t > MAX_DIST)
            break;
        t += d;
    }
    if (t > MAX_DIST)
        t = -1.;
    return vec2(t, m);
}

mat2 getAnimatedMatrix(out mat2 M_inv, out float show_cross) {
    vec2 active_ab = texelFetch(iChannel0, REG_ACTIVE_MATRIX_AB, 0).xy;
    vec2 active_cd = texelFetch(iChannel0, REG_ACTIVE_MATRIX_CD, 0).xy;
    vec2 old_active_ab = texelFetch(iChannel0, REG_OLD_ACTIVE_MATRIX_AB, 0).xy;
    vec2 old_active_cd = texelFetch(iChannel0, REG_OLD_ACTIVE_MATRIX_CD, 0).xy;

    vec4 anim_state = texelFetch(iChannel0, REG_ANIM_TIMER, 0);
    float anim_timer = anim_state.x;
    show_cross = anim_state.y;

    mat2 M_old = mat2(old_active_ab.x, old_active_cd.x, old_active_ab.y,
                      old_active_cd.y);
    mat2 M_new = mat2(active_ab.x, active_cd.x, active_ab.y, active_cd.y);
    float t = smoothstep(0.0, 1.0, anim_timer);
    mat2 M_anim = M_old * (1.0 - t) + M_new * t;


    float det = determinant(M_anim);
    if (abs(det) > 0.0001) {
        M_inv = inverse(M_anim);
    } else {
        M_inv = mat2(1.0);
    }

    return M_anim;
}

vec3 render_scene(vec2 fragCoord, vec2 resolution, float p, float q,
                  vec2 camera_rot) {
    mat2 M_inv;
    float show_cross_unused;
    mat2 M_anim = getAnimatedMatrix(M_inv, show_cross_unused);

    ivec2 slope_int = ivec2(round(p), round(q));
    int common_divisor = gcd(slope_int.x, slope_int.y);
    vec2 reduced_slope = vec2(slope_int) / float(common_divisor);

    vec2 twisted_slope = M_anim * reduced_slope;
    float p_twisted = twisted_slope.x;
    float q_twisted = twisted_slope.y;

    float yaw = camera_rot.x;
    float pitch = camera_rot.y;
    if (iMouse.z <= 0.0) {
        yaw += iTime * 0.3;
    }
    float dist = 3.0;
    vec3 ro;
    ro.x = dist * cos(pitch) * sin(yaw);
    ro.y = dist * sin(pitch);
    ro.z = dist * cos(pitch) * cos(yaw);
    ro.y += 0.5;
    vec3 ta = vec3(0.0, -0.1, 0.0);
    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww, vec3(0.0, 1.0, 0.0)));
    vec3 vv = normalize(cross(uu, ww));
    vec2 p_scene = (-resolution + 2.0 * fragCoord) / resolution.y;
    vec3 rd = normalize(p_scene.x * uu + p_scene.y * vv + 1.5 * ww);
    vec2 torus = vec2(1.0, 0.5);
    vec2 hit = raymarch(ro, rd, torus, p_twisted, q_twisted);
    float t = hit.x;
    float materialID = hit.y;
    vec3 col = vec3(0.08) * (1.0 - 0.3 * length(p_scene)) + 0.02 * rd.y;

    if (t > 0.0) {
        vec3 pos = ro + t * rd;
        vec3 nor = calcNormal(pos, torus, p_twisted, q_twisted);
        vec3 lig = normalize(vec3(0.7, 0.6, 0.3));
        vec3 hal = normalize(-rd + lig);
        float dif = clamp(dot(nor, lig), 0.0, 1.0);
        float amb = clamp(0.7, 0.0, 1.0) + 0.3 * nor.y;
        if (materialID > 1.5) {
            col = UI_ACCENT_COLOR;
            col = col * (amb * 0.3 + dif * 0.7);
            float spe = pow(clamp(dot(nor, hal), 0.0, 1.0), 32.0);
            col += vec3(1.0) * spe * 0.5;
        } else {
            const float fr = 16.0;
            vec2 uv =
                vec2(atan(pos.z, pos.x), atan(pos.y, length(pos.xz) - torus.x));
            vec2 transformed_uv = M_inv * (uv / (2.0 * PI));
            vec2 scaled_uv = transformed_uv * fr;
            vec2 iuv = floor(scaled_uv);
            float checker_pattern = mod(iuv.x + iuv.y, 2.0);
            vec3 base_col = (checker_pattern > 0.5) ? vec3(1.0) : vec3(0.6);
            vec2 fuv = fract(scaled_uv);
            vec2 dist_to_edge = min(fuv, 1.0 - fuv);
            float dist_to_line = min(dist_to_edge.x, dist_to_edge.y);
            float line_width = 0.02;
            float aa = fwidth(dist_to_line) * 1.5;
            float line_intensity =
                1.0 -
                smoothstep(line_width - aa, line_width + aa, dist_to_line);
            vec3 line_color = vec3(0.1);
            col = mix(base_col, line_color, line_intensity);
            col *=
                vec3(0.15, 0.25, 0.35) * amb + 1.05 * vec3(1.0, 0.9, 0.7) * dif;
        }
    }
    return sqrt(col);
}

vec3 render_ui(vec2 fragCoord, float p, float q, float a, float b, float c,
               float d) {
    vec3 final_col = UI_BACKGROUND_COLOR;
    float aa = 1.5 / iResolution.y;
    float text_dist = 1e5;
    vec3 button_layer = vec3(0.0);
    float title_size = iResolution.y * UI_TITLE_TEXT_SIZE_FRAC;
    float num_size_pq = iResolution.y * UI_PQ_NUM_SIZE_FRAC;
    float num_size_abcd = iResolution.y * UI_ABCD_NUM_SIZE_FRAC;
    float btn_radius_pq = iResolution.y * UI_PQ_BTN_RADIUS_FRAC;
    float btn_radius_abcd = iResolution.y * UI_ABCD_BTN_RADIUS_FRAC;
    int slope_chars[5] = int[](83, 76, 79, 80, 69);
    text_dist = min(text_dist, get_string_dist(iChannel1, fragCoord,
                                               SLOPE_TITLE_POS(iResolution.xy),
                                               title_size, slope_chars, 5));
    text_dist = min(text_dist,
                    get_number_dist(iChannel1, fragCoord,
                                    P_NUM_POS(iResolution.xy), num_size_pq, p));
    button_layer += draw_button(
        fragCoord, iMouse.xy, P_BTN_UP_POS(iResolution.xy), btn_radius_pq, PI);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, P_BTN_DOWN_POS(iResolution.xy),
                    btn_radius_pq, 0.0);
    text_dist = min(text_dist,
                    get_number_dist(iChannel1, fragCoord,
                                    Q_NUM_POS(iResolution.xy), num_size_pq, q));
    button_layer += draw_button(
        fragCoord, iMouse.xy, Q_BTN_UP_POS(iResolution.xy), btn_radius_pq, PI);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, Q_BTN_DOWN_POS(iResolution.xy),
                    btn_radius_pq, 0.0);
    int twist_chars[5] = int[](84, 87, 73, 83, 84);
    text_dist = min(text_dist, get_string_dist(iChannel1, fragCoord,
                                               TWIST_TITLE_POS(iResolution.xy),
                                               title_size, twist_chars, 5));
    text_dist = min(text_dist, get_number_dist(iChannel1, fragCoord,
                                               A_NUM_POS(iResolution.xy),
                                               num_size_abcd, a));
    text_dist = min(text_dist, get_number_dist(iChannel1, fragCoord,
                                               B_NUM_POS(iResolution.xy),
                                               num_size_abcd, b));
    text_dist = min(text_dist, get_number_dist(iChannel1, fragCoord,
                                               C_NUM_POS(iResolution.xy),
                                               num_size_abcd, c));
    text_dist = min(text_dist, get_number_dist(iChannel1, fragCoord,
                                               D_NUM_POS(iResolution.xy),
                                               num_size_abcd, d));
    button_layer +=
        draw_button(fragCoord, iMouse.xy, A_BTN_UP_POS(iResolution.xy),
                    btn_radius_abcd, PI);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, A_BTN_DOWN_POS(iResolution.xy),
                    btn_radius_abcd, 0.0);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, B_BTN_UP_POS(iResolution.xy),
                    btn_radius_abcd, PI);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, B_BTN_DOWN_POS(iResolution.xy),
                    btn_radius_abcd, 0.0);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, C_BTN_UP_POS(iResolution.xy),
                    btn_radius_abcd, PI);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, C_BTN_DOWN_POS(iResolution.xy),
                    btn_radius_abcd, 0.0);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, D_BTN_UP_POS(iResolution.xy),
                    btn_radius_abcd, PI);
    button_layer +=
        draw_button(fragCoord, iMouse.xy, D_BTN_DOWN_POS(iResolution.xy),
                    btn_radius_abcd, 0.0);
    final_col =
        mix(final_col, UI_TEXT_COLOR, 1.0 - smoothstep(-aa, aa, text_dist));
    final_col += button_layer;
    text_dist = 1e5;
    text_dist =
        min(text_dist, font_dist(iChannel1,
                                 fragCoord - vec2(UI_CENTER_X(iResolution.xy),
                                                  P_NUM_POS(iResolution.xy).y),
                                 num_size_pq, 47));
    final_col = mix(final_col, UI_ELEMENT_COLOR,
                    1.0 - smoothstep(-0.5, 0.5, text_dist));
    float h_offset = iResolution.y * UI_MATRIX_BRACKET_H_OFFSET_FRAC;
    float v_offset = iResolution.y * UI_MATRIX_BRACKET_V_OFFSET_FRAC;
    float line_x_pos_left = A_NUM_POS(iResolution.xy).x - h_offset;
    float line_x_pos_right = B_NUM_POS(iResolution.xy).x + h_offset;
    float line_y_start = A_NUM_POS(iResolution.xy).y - v_offset;
    float line_y_end = C_NUM_POS(iResolution.xy).y + v_offset;
    float matrix_line =
        sdSegment(fragCoord, vec2(line_x_pos_left, line_y_start),
                  vec2(line_x_pos_left, line_y_end));
    matrix_line = min(matrix_line,
                      sdSegment(fragCoord, vec2(line_x_pos_right, line_y_start),
                                vec2(line_x_pos_right, line_y_end)));
    final_col = mix(final_col, UI_ELEMENT_COLOR,
                    1.0 - smoothstep(0.8, 1.2, matrix_line));

    mat2 M_inv_unused;
    float show_cross;
    mat2 M_anim = getAnimatedMatrix(M_inv_unused, show_cross);

    if (show_cross > 0.5) {
        vec2 cross_center = vec2(
            UI_CENTER_X(iResolution.xy),
            (A_NUM_POS(iResolution.xy).y + C_NUM_POS(iResolution.xy).y) * 0.5);
        float cross_size = iResolution.y * UI_CROSS_SIZE_FRAC;
        vec2 p1 = cross_center + vec2(-cross_size, cross_size);
        vec2 p2 = cross_center + vec2(cross_size, -cross_size);
        vec2 p3 = cross_center + vec2(cross_size, cross_size);
        vec2 p4 = cross_center + vec2(-cross_size, -cross_size);
        float dist_to_cross =
            min(sdSegment(fragCoord, p1, p2), sdSegment(fragCoord, p3, p4));
        float cross_thickness = 1.0;
        float cross_aa = 1.0;
        vec3 cross_color = vec3(1.0, 0.2, 0.2);
        float cross_alpha =
            1.0 - smoothstep(cross_thickness - cross_aa,
                             cross_thickness + cross_aa, dist_to_cross);
        final_col = mix(final_col, cross_color, cross_alpha);
    }

    float ui_width = UI_WIDTH_PIXELS(iResolution.xy);
    float viz_margin = iResolution.y * UI_VIZBOX_MARGIN_FRAC;
    float viz_size = min(ui_width - viz_margin, iResolution.y * 0.4);
    vec4 vizBox = vec4(UI_CENTER_X(iResolution.xy),
                       viz_size * 0.5 + iResolution.y * UI_VIZBOX_Y_OFFSET_FRAC,
                       vec2(viz_size) * 0.5);
    if (insideBox(fragCoord, vizBox)) {
        vec2 local_uv =
            (fragCoord - (vizBox.xy - vizBox.zw)) / (2.0 * vizBox.zw);
        vec3 box_col = UI_BACKGROUND_COLOR;
        float border_width = 0.015;
        vec2 border_dist = min(local_uv, 1.0 - local_uv);
        float border_aa = 1.5 / vizBox.z;
        float border =
            smoothstep(border_width - border_aa, border_width + border_aa,
                       min(border_dist.x, border_dist.y));
        box_col = mix(vec3(1.0), box_col, border);

        vec2 original_slope = vec2(p, q);
        vec2 twisted_slope = M_anim * original_slope;

        float billiard_dist =
            twisted_slope.x * local_uv.x - twisted_slope.y * local_uv.y;

        float line_value = fract(billiard_dist);
        float line_width_in_uv_space = fwidth(billiard_dist)*2.0;
        float dist_to_line = min(line_value, 1.0 - line_value);
        float billiard_line = smoothstep(0.5 * line_width_in_uv_space, -0.5 * line_width_in_uv_space, dist_to_line);

        box_col = mix(box_col, UI_ACCENT_COLOR, billiard_line);
        final_col = box_col;
    }
    return final_col;
}

vec3 render(in vec2 fragCoord) {
    vec3 col;
    float ui_width = UI_WIDTH_PIXELS(iResolution.xy);
    if (fragCoord.x < ui_width) {
        vec2 pq = texelFetch(iChannel0, REG_PQ, 0).xy;
        vec2 ab = texelFetch(iChannel0, REG_MATRIX_AB, 0).xy;
        vec2 cd = texelFetch(iChannel0, REG_MATRIX_CD, 0).xy;
        col = render_ui(fragCoord, pq.x, pq.y, ab.x, ab.y, cd.x, cd.y);
        float border =
            smoothstep(0.0, 2.0 / iResolution.x, abs(fragCoord.x - ui_width));
        col = mix(col, UI_BORDER_COLOR, 1.0 - border);
    } else {
        vec2 pq = texelFetch(iChannel0, REG_PQ, 0).xy;
        vec2 camera_rot = texelFetch(iChannel0, REG_CAMERA, 0).xy;
        vec2 scene_coord = fragCoord - vec2(ui_width, 0.0);
        vec2 scene_res = vec2(iResolution.x - ui_width, iResolution.y);
        col = render_scene(scene_coord, scene_res, pq.x, pq.y, camera_rot);
    }
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 total_col = vec3(0.0);
#if AA > 1
    for (int m = 0; m < AA; m++) {
        for (int n = 0; n < AA; n++) {
            vec2 offset = (vec2(float(m), float(n)) + 0.5) / float(AA);
            vec2 sub_pixel_coord = floor(fragCoord) + offset;
            total_col += render(sub_pixel_coord);
        }
    }
    total_col /= float(AA * AA);
#else
    total_col = render(fragCoord);
#endif
    total_col +=
        fract(sin(fragCoord.x * vec3(13, 1, 11) + fragCoord.y * vec3(1, 7, 5)) *
              158.391832) /
        255.0;
    fragColor = vec4(total_col, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 fc = ivec2(fragCoord);
    fragColor = texelFetch(iChannel0, fc, 0);

    if (iFrame == 0) {
        if (fc == REG_PQ) fragColor = vec4(1.0, 1.0, 0.0, 0.0);
        if (fc == REG_MATRIX_AB) fragColor = vec4(1.0, -1.0, 0.0, 0.0);
        if (fc == REG_MATRIX_CD) fragColor = vec4(0.0, 1.0, 0.0, 0.0);
        if (fc == REG_MOUSE) fragColor = vec4(-1.0, -1.0, 0.0, 0.0);
        if (fc == REG_CAMERA) fragColor = vec4(0.0, 0.0, 0.0, 0.0);
        if (fc == REG_ACTIVE_MATRIX_AB) fragColor = vec4(1.0, -1.0, 0.0, 0.0);
        if (fc == REG_ACTIVE_MATRIX_CD) fragColor = vec4(0.0, 1.0, 0.0, 0.0);
        if (fc == REG_OLD_ACTIVE_MATRIX_AB) fragColor = vec4(1.0, -1.0, 0.0, 0.0);
        if (fc == REG_OLD_ACTIVE_MATRIX_CD) fragColor = vec4(0.0, 1.0, 0.0, 0.0);
        if (fc == REG_ANIM_TIMER) fragColor = vec4(2.0, 0.0, 0.0, 0.0);
        return;
    }

    vec2 pq = texelFetch(iChannel0, REG_PQ, 0).xy;
    vec2 display_ab = texelFetch(iChannel0, REG_MATRIX_AB, 0).xy;
    vec2 display_cd = texelFetch(iChannel0, REG_MATRIX_CD, 0).xy;
    vec2 active_ab = texelFetch(iChannel0, REG_ACTIVE_MATRIX_AB, 0).xy;
    vec2 active_cd = texelFetch(iChannel0, REG_ACTIVE_MATRIX_CD, 0).xy;
    vec2 old_active_ab = texelFetch(iChannel0, REG_OLD_ACTIVE_MATRIX_AB, 0).xy;
    vec2 old_active_cd = texelFetch(iChannel0, REG_OLD_ACTIVE_MATRIX_CD, 0).xy;

    vec4 anim_state = texelFetch(iChannel0, REG_ANIM_TIMER, 0);
    float anim_timer = anim_state.x;
    float show_cross = anim_state.y;

    vec4 prev_mouse = texelFetch(iChannel0, REG_MOUSE, 0);
    vec2 camera_rot = texelFetch(iChannel0, REG_CAMERA, 0).xy;

    bool mouse_clicked = iMouse.z > 0.0 && prev_mouse.z <= 0.0;

    if(anim_timer < 1.0) {
        anim_timer += 0.04;
    }

    if (iMouse.x < UI_WIDTH_PIXELS(iResolution.xy)) {
        if (mouse_clicked) {
            bool matrix_button_clicked = false;
            vec2 prospective_ab = display_ab;
            vec2 prospective_cd = display_cd;

            if      (insideBox(iMouse.xy, P_UP_BOX(iResolution.xy)))   { pq.x++; }
            else if (insideBox(iMouse.xy, P_DOWN_BOX(iResolution.xy))) { pq.x--; }
            else if (insideBox(iMouse.xy, Q_UP_BOX(iResolution.xy)))   { pq.y++; }
            else if (insideBox(iMouse.xy, Q_DOWN_BOX(iResolution.xy))) { pq.y--; }

            else if (insideBox(iMouse.xy, A_UP_BOX(iResolution.xy)))   { prospective_ab.x++; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, A_DOWN_BOX(iResolution.xy))) { prospective_ab.x--; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, B_UP_BOX(iResolution.xy)))   { prospective_ab.y++; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, B_DOWN_BOX(iResolution.xy))) { prospective_ab.y--; matrix_button_clicked = true; }

            else if (insideBox(iMouse.xy, C_UP_BOX(iResolution.xy)))   { prospective_cd.x++; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, C_DOWN_BOX(iResolution.xy))) { prospective_cd.x--; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, D_UP_BOX(iResolution.xy)))   { prospective_cd.y++; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, D_DOWN_BOX(iResolution.xy))) { prospective_cd.y--; matrix_button_clicked = true; }

            if (matrix_button_clicked) {
                display_ab = prospective_ab;
                display_cd = prospective_cd;

                float det = display_ab.x * display_cd.y - display_ab.y * display_cd.x;
                if (abs(det - 1.0) < 0.0001) {
                    old_active_ab = active_ab;
                    old_active_cd = active_cd;
                    active_ab = display_ab;
                    active_cd = display_cd;
                    anim_timer = 0.0;
                    show_cross = 0.0;
                } else {
                    anim_timer = 2.0;
                    show_cross = 1.0;
                }
            }
        }
    } 
    else if (iMouse.z > 0.0) {
        vec2 delta = iMouse.xy - prev_mouse.xy;
        camera_rot.x -= delta.x * 0.005;
        camera_rot.y += delta.y * 0.005;
        camera_rot.y = clamp(camera_rot.y, -PI / 2.0 + 0.05, PI / 2.0 - 0.05);
    }

    if (fc == REG_PQ) fragColor = vec4(pq, 0, 0);
    if (fc == REG_MATRIX_AB) fragColor = vec4(display_ab, 0, 0);
    if (fc == REG_MATRIX_CD) fragColor = vec4(display_cd, 0, 0);
    if (fc == REG_MOUSE) fragColor = iMouse;
    if (fc == REG_CAMERA) fragColor = vec4(camera_rot, 0, 0);
    if (fc == REG_ACTIVE_MATRIX_AB) fragColor = vec4(active_ab, 0, 0);
    if (fc == REG_ACTIVE_MATRIX_CD) fragColor = vec4(active_cd, 0, 0);
    if (fc == REG_OLD_ACTIVE_MATRIX_AB) fragColor = vec4(old_active_ab, 0, 0);
    if (fc == REG_OLD_ACTIVE_MATRIX_CD) fragColor = vec4(old_active_cd, 0, 0);
    if (fc == REG_ANIM_TIMER) fragColor = vec4(anim_timer, show_cross, 0.0, 0.0);
}

#define PI 3.14159265359

// --- UI Theming Constants ---
#define UI_WIDTH_FRAC 0.25
#define UI_BACKGROUND_COLOR vec3(0.1, 0.12, 0.15)
#define UI_TEXT_COLOR vec3(0.9)
#define UI_ELEMENT_COLOR vec3(0.9)
#define UI_BUTTON_COLOR vec3(0.4)
#define UI_BUTTON_HOVER_COLOR vec3(0.8, 0., 0.8)
#define UI_ACCENT_COLOR vec3(1.0, 0.7, 0.2)
#define UI_BORDER_COLOR vec3(0.05)

// --- UI Layout Base Constants (NOW AS PROPORTIONS) ---
#define DESIGN_RESOLUTION_Y 540.0
#define UI_SLOPE_TITLE_Y_FRAC (40.0 / DESIGN_RESOLUTION_Y)
#define UI_SLOPE_NUM_Y_FRAC (100.0 / DESIGN_RESOLUTION_Y)
#define UI_SLOPE_NUM_X_FRAC 0.2
#define UI_PQ_BTN_Y_SPACING_FRAC (30.0 / DESIGN_RESOLUTION_Y)
#define UI_PQ_BTN_RADIUS_FRAC (30.0 / DESIGN_RESOLUTION_Y)
#define UI_TWIST_TITLE_Y_FRAC (180.0 / DESIGN_RESOLUTION_Y)
#define UI_TWIST_TOP_ROW_Y_FRAC (240.0 / DESIGN_RESOLUTION_Y)
#define UI_TWIST_BOTTOM_ROW_Y_FRAC (320.0 / DESIGN_RESOLUTION_Y)
#define UI_TWIST_NUM_X_FRAC 0.2
#define UI_ABCD_BTN_Y_SPACING_FRAC (20.0 / DESIGN_RESOLUTION_Y)
#define UI_ABCD_BTN_RADIUS_FRAC (28.0 / DESIGN_RESOLUTION_Y)
#define UI_BTN_BBOX_SIZE_FRAC (15.0 / DESIGN_RESOLUTION_Y)
#define UI_TITLE_TEXT_SIZE_FRAC (20.0 / DESIGN_RESOLUTION_Y)
#define UI_PQ_NUM_SIZE_FRAC (30.0 / DESIGN_RESOLUTION_Y)
#define UI_ABCD_NUM_SIZE_FRAC (20.0 / DESIGN_RESOLUTION_Y)
#define UI_MATRIX_BRACKET_H_OFFSET_FRAC (30.0 / DESIGN_RESOLUTION_Y)
#define UI_MATRIX_BRACKET_V_OFFSET_FRAC (110.0 / DESIGN_RESOLUTION_Y)
#define UI_VIZBOX_Y_OFFSET_FRAC (20.0 / DESIGN_RESOLUTION_Y)
#define UI_VIZBOX_MARGIN_FRAC (100.0 / DESIGN_RESOLUTION_Y)
#define UI_CROSS_SIZE_FRAC (12.0 / DESIGN_RESOLUTION_Y)

// --- UI Layout Calculation Macros ---
#define UI_WIDTH_PIXELS(res) ((res).x * UI_WIDTH_FRAC)
#define UI_CENTER_X(res) (UI_WIDTH_PIXELS(res) * 0.5)
#define SLOPE_TITLE_POS(res)                                                   \
    vec2(UI_CENTER_X(res), (res).y * (1.0 - UI_SLOPE_TITLE_Y_FRAC))
#define P_NUM_POS(res)                                                         \
    vec2(UI_CENTER_X(res) - UI_WIDTH_PIXELS(res) * UI_SLOPE_NUM_X_FRAC,        \
         (res).y * (1.0 - UI_SLOPE_NUM_Y_FRAC))
#define Q_NUM_POS(res)                                                         \
    vec2(UI_CENTER_X(res) + UI_WIDTH_PIXELS(res) * UI_SLOPE_NUM_X_FRAC,        \
         (res).y * (1.0 - UI_SLOPE_NUM_Y_FRAC))
#define P_BTN_UP_POS(res)                                                      \
    (P_NUM_POS(res) + vec2(0.0, (res).y * UI_PQ_BTN_Y_SPACING_FRAC))
#define P_BTN_DOWN_POS(res)                                                    \
    (P_NUM_POS(res) - vec2(0.0, (res).y * UI_PQ_BTN_Y_SPACING_FRAC))
#define Q_BTN_UP_POS(res)                                                      \
    (Q_NUM_POS(res) + vec2(0.0, (res).y * UI_PQ_BTN_Y_SPACING_FRAC))
#define Q_BTN_DOWN_POS(res)                                                    \
    (Q_NUM_POS(res) - vec2(0.0, (res).y * UI_PQ_BTN_Y_SPACING_FRAC))
#define TWIST_TITLE_POS(res)                                                   \
    vec2(UI_CENTER_X(res), (res).y * (1.0 - UI_TWIST_TITLE_Y_FRAC))
#define A_NUM_POS(res)                                                         \
