// --- Buffer Register Definitions ---
#define REG_PQ ivec2(0, 0)
#define REG_MATRIX_AB ivec2(1, 0) // Stores the DISPLAY matrix values
#define REG_MATRIX_CD ivec2(2, 0) //
#define REG_MOUSE ivec2(3, 0)
#define REG_CAMERA ivec2(4, 0)
#define REG_ACTIVE_MATRIX_AB                                                   \
    ivec2(5, 0) // Stores the last VALID matrix for rendering
#define REG_ACTIVE_MATRIX_CD ivec2(6, 0) //
#define REG_OLD_ACTIVE_MATRIX_AB                                               \
    ivec2(7, 0) // Stores the previous VALID matrix for animation start
#define REG_OLD_ACTIVE_MATRIX_CD ivec2(8, 0) //
#define REG_ANIM_TIMER ivec2(9, 0)           // x: timer, y: show_cross_flag

// --- General Drawing & Math Functions ---
float sdTriangle(vec2 p) {
    p.y = -p.y;
    return max(abs(p.x) * 0.866025 + p.y * 0.5, -p.y);
}
float sdSegment(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}
bool insideBox(vec2 p, vec4 b) {
    vec2 q = abs(p - b.xy);
    return all(lessThan(q, b.zw));
}
float font_dist(sampler2D font_texture, vec2 tpos, float size, int char_code) {
    if (char_code < 32 || char_code > 127)
        return 1e5;
    float scl = 0.63 / size;
    vec2 uv = tpos * scl;
    ivec2 grid_pos = ivec2(char_code % 16, 15 - (char_code / 16));
    vec2 font_uv = (uv + vec2(grid_pos) + 0.5) * (1.0 / 16.0);
    float dist_from_tex = texture(font_texture, font_uv).a - 0.5;
    vec2 box = abs(uv) - 0.5;
    return max(dist_from_tex, max(box.x, box.y)) / scl;
}
float get_string_dist(sampler2D font_texture, vec2 fragCoord, vec2 p,
                      float size, const int chars[5], int len) {
    vec2 text_pos = fragCoord - p;
    float char_spacing = size * 0.8;
    float x_offset = text_pos.x + float(len - 1) * 0.5 * char_spacing;
    int char_idx = int(floor(x_offset / char_spacing + 0.5));
    if (char_idx < 0 || char_idx >= len)
        return 1e5;
    vec2 char_center_offset = vec2(float(char_idx) * char_spacing -
                                       float(len - 1) * 0.5 * char_spacing,
                                   0.0);
    return font_dist(font_texture, text_pos - char_center_offset, size,
                     chars[char_idx]);
}
float get_number_dist(sampler2D font_texture, vec2 fragCoord, vec2 p,
                      float size, float num) {
    vec2 text_pos = fragCoord - p;
    float char_spacing = size * 0.7;
    float num_abs = abs(num);
    bool is_neg = num < 0.0;
    int whole_part = int(floor(num_abs));
    int num_digits = (whole_part == 0)
                         ? 1
                         : int(floor(log2(float(whole_part)) / log2(10.0))) + 1;
    float total_width = float(num_digits) * char_spacing;
    if (is_neg)
        total_width += char_spacing;
    float x_offset = text_pos.x + total_width * 0.5;
    if (is_neg) {
        if (x_offset < char_spacing) {
            return font_dist(
                font_texture,
                text_pos - vec2(-total_width * 0.5 + char_spacing * 0.5, 0.0),
                size, 45);
        }
        x_offset -= char_spacing;
    }
    int digit_idx = int(floor(x_offset / char_spacing));
    if (digit_idx < 0 || digit_idx >= num_digits)
        return 1e5;
    float power_of_10 = pow(10.0, float(num_digits - 1 - digit_idx));
    int digit = int(mod(floor(float(whole_part) / power_of_10), 10.0));
    vec2 digit_center_offset =
        vec2((is_neg ? char_spacing : 0.0) - total_width * 0.5 +
                 (float(digit_idx) + 0.5) * char_spacing,
             0.0);
    return font_dist(font_texture, text_pos - digit_center_offset, size,
                     digit + 48);
}
vec3 draw_button(vec2 fragCoord, vec2 mouseCoord, vec2 center, float size,
                 float orientation) {
    vec2 p = fragCoord - center;
    mat2 rot = mat2(cos(orientation), -sin(orientation), sin(orientation),
                    cos(orientation));
    p = rot * p;
    float d = sdTriangle(p / size);
    vec3 col = UI_BUTTON_COLOR;
    if (length(center - mouseCoord) < size * 1.1) {
        col = UI_BUTTON_HOVER_COLOR;
    }
    float aa = fwidth(abs(d));
    return mix(vec3(0.0), col, 1.0 - smoothstep(0.15 - aa, 0.15 + aa, d));
}

/*
Mapping Class Group of the Torus and Sturmian words


Left-hand side of the UI:

**SLOPE**: 

A geodesic with slope p/q. This is a periodic orbit in the
square, and a closed geodesic on the torus:
it winds around the two fundamental circles of the torus, 
q times in one and p times in the other.

**TWIST**: Any matrix in SL(2,Z). It acts on the 
geodesic, sending slope p/q to the geodesic corresponding
to M * (p/q,1). On the right-hand 3D view, you can see
how this twists the torus and changes the geodesic.

**Sturmian word**: Each time the geodesic crosses a
vertical edge of the square, we write down A; each time
it crosses a horizontal edge, we write down B.
This produces an infinite sequence of A's and B's,
called a Sturmian word. When the slope is rational,
the Sturmian word has a finite repetend. This repetend
is displayed right below the TWIST matrix.

At the bottom of the UI, you see the geodesic inside the square.
*/


#define AA 3

const float TUBE_RADIUS = 0.035;
const float MAX_DIST = 100.0;
const float SURF_DIST = 0.001;

#define MAX_WORD_LEN 24

int gcd(int a, int b) {
    a = abs(a);
    b = abs(b);
    while (b != 0) {
        int temp = b;
        b = a % b;
        a = temp;
    }
    return (a == 0) ? 1 : a;
}
float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}
float sdGeodesicTube(vec3 pos, vec2 tor, float p_t, float q_t) {
    float d_torus = sdTorus(pos, tor);
    float u = atan(pos.z, pos.x);
    float v = atan(pos.y, length(pos.xz) - tor.x + 1e-9);
    float offset = 1.0 / sqrt(2.0) * PI;
    float val = p_t * (v / (2. * PI)) - q_t * (u / (2. * PI)) + offset;
    float d_line = fract(val);
    d_line = min(d_line, 1.0 - d_line);
    float n = length(vec2(p_t, q_t));
    if (n < 0.1)
        n = 1.;
    float d_geodesic = tor.y * d_line * 2.0 * PI / n;
    return length(vec2(d_torus, d_geodesic)) - TUBE_RADIUS;
}
vec2 map(vec3 pos, vec2 tor, float p_t, float q_t) {
    float dt = sdTorus(pos, tor);
    float dtu = sdGeodesicTube(pos, tor, p_t, q_t);
    if (dtu < dt)
        return vec2(dtu, 2.);
    return vec2(dt, 1.);
}
vec3 calcNormal(vec3 pos, vec2 tor, float p_t, float q_t) {
    vec2 e = vec2(.001, 0.);
    float d = map(pos, tor, p_t, q_t).x;
    return normalize(vec3(d - map(pos - e.xyy, tor, p_t, q_t).x,
                          d - map(pos - e.yxy, tor, p_t, q_t).x,
                          d - map(pos - e.yyx, tor, p_t, q_t).x));
}
vec2 raymarch(vec3 ro, vec3 rd, vec2 tor, float p_t, float q_t) {
    float t = 0., m = -1.;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + t * rd;
        vec2 res = map(p, tor, p_t, q_t);
        float d = res.x;
        if (!(d > -1000. && d < 1000.))
            break;
        m = res.y;
        if (d < SURF_DIST || t > MAX_DIST)
            break;
        t += d;
    }
    if (t > MAX_DIST)
        t = -1.;
    return vec2(t, m);
}

mat2 getAnimatedMatrix(out mat2 M_inv, out float show_cross) {
    vec2 active_ab = texelFetch(iChannel0, REG_ACTIVE_MATRIX_AB, 0).xy;
    vec2 active_cd = texelFetch(iChannel0, REG_ACTIVE_MATRIX_CD, 0).xy;
    vec2 old_active_ab = texelFetch(iChannel0, REG_OLD_ACTIVE_MATRIX_AB, 0).xy;
    vec2 old_active_cd = texelFetch(iChannel0, REG_OLD_ACTIVE_MATRIX_CD, 0).xy;
    vec4 anim_state = texelFetch(iChannel0, REG_ANIM_TIMER, 0);
    float anim_timer = anim_state.x;
    show_cross = anim_state.y;
    mat2 M_old = mat2(old_active_ab.x, old_active_cd.x, old_active_ab.y,
                      old_active_cd.y);
    mat2 M_new = mat2(active_ab.x, active_cd.x, active_ab.y, active_cd.y);
    float t = smoothstep(0.0, 1.0, anim_timer);
    mat2 M_anim = M_old * (1.0 - t) + M_new * t;
    float det = determinant(M_anim);
    if (abs(det) > 0.0001) {
        M_inv = inverse(M_anim);
    } else {
        M_inv = mat2(1.0);
    }
    return M_anim;
}

int generateSturmianWord(vec2 slope, out int word[MAX_WORD_LEN]) {
    ivec2 s_int = ivec2(round(abs(slope)));
    int common_divisor = gcd(s_int.x, s_int.y);
    s_int /= common_divisor;
    int p = s_int.x; // p is vertical
    int q = s_int.y; // q is horizontal
    if (p < 0 || q < 0)
        return 0;
    if (p == 0 && q == 0)
        return 0;
    if (q == 0) {
        word[0] = 65;
        return 1;
    }
    if (p == 0) {
        word[0] = 66;
        return 1;
    }
    int len = p + q;
    int loop_len = min(len, MAX_WORD_LEN);
    float x0 = 1.0 / sqrt(2.0);
    float y0 = 0.0;
    int ix = 0;
    int iy = 0;
    for (int i = 0; i < loop_len; ++i) {
        if (float(p) * (float(ix) + 1.0 - x0) <
            float(q) * (float(iy) + 1.0 - y0)) {
            word[i] = 65;
            ix++;
        } else {
            word[i] = 66;
            iy++;
        }
    }
    return loop_len;
}

vec3 render_scene(vec2 fragCoord, vec2 resolution, float p, float q,
                  vec2 camera_rot) {
    mat2 M_inv;
    float show_cross_unused;
    mat2 M_anim = getAnimatedMatrix(M_inv, show_cross_unused);
    ivec2 slope_int = ivec2(round(p), round(q));
    int common_divisor = gcd(slope_int.x, slope_int.y);
    vec2 reduced_slope = vec2(slope_int) / float(common_divisor);
    vec2 twisted_slope = M_anim * reduced_slope;
    float p_twisted = twisted_slope.x;
    float q_twisted = twisted_slope.y;
    float yaw = camera_rot.x;
    float pitch = camera_rot.y;
    if (iMouse.z <= 0.0) {
        yaw += iTime * 0.3;
    }
    float dist = 3.0;
    vec3 ro;
    ro.x = dist * cos(pitch) * sin(yaw);
    ro.y = dist * sin(pitch);
    ro.z = dist * cos(pitch) * cos(yaw);
    ro.y += 0.5;
    vec3 ta = vec3(0.0, -0.1, 0.0);
    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww, vec3(0.0, 1.0, 0.0)));
    vec3 vv = normalize(cross(uu, ww));
    vec2 p_scene = (-resolution + 2.0 * fragCoord) / resolution.y;
    vec3 rd = normalize(p_scene.x * uu + p_scene.y * vv + 1.5 * ww);
    vec2 torus = vec2(1.0, 0.5);
    vec2 hit = raymarch(ro, rd, torus, p_twisted, q_twisted);
    float t = hit.x;
    float materialID = hit.y;
    vec3 col = vec3(0.08) * (1.0 - 0.3 * length(p_scene)) + 0.02 * rd.y;
    if (t > 0.0) {
        vec3 pos = ro + t * rd;
        vec3 nor = calcNormal(pos, torus, p_twisted, q_twisted);
        vec3 lig = normalize(vec3(0.7, 0.6, 0.3));
        vec3 hal = normalize(-rd + lig);
        float dif = clamp(dot(nor, lig), 0.0, 1.0);
        float amb = clamp(0.7, 0.0, 1.0) + 0.3 * nor.y;
        if (materialID > 1.5) {
            col = UI_ACCENT_COLOR;
            col = col * (amb * 0.3 + dif * 0.7);
            float spe = pow(clamp(dot(nor, hal), 0.0, 1.0), 32.0);
            col += vec3(1.0) * spe * 0.5;
        } else {
            const float fr = 16.0;
            vec2 uv =
                vec2(atan(pos.z, pos.x), atan(pos.y, length(pos.xz) - torus.x));
            vec2 transformed_uv = M_inv * (uv / (2.0 * PI));
            vec2 scaled_uv = transformed_uv * fr;
            vec2 iuv = floor(scaled_uv);
            float checker_pattern = mod(iuv.x + iuv.y, 2.0);
            vec3 base_col = (checker_pattern > 0.5) ? vec3(1.0) : vec3(0.6);
            vec2 fuv = fract(scaled_uv);
            vec2 dist_to_edge = min(fuv, 1.0 - fuv);
            float dist_to_line = min(dist_to_edge.x, dist_to_edge.y);
            float line_width = 0.02;
            float aa = fwidth(dist_to_line) * 1.5;
            float line_intensity =
                1.0 -
                smoothstep(line_width - aa, line_width + aa, dist_to_line);
            vec3 line_color = vec3(0.1);
            col = mix(base_col, line_color, line_intensity);
            col *=
                vec3(0.15, 0.25, 0.35) * amb + 1.05 * vec3(1.0, 0.9, 0.7) * dif;
        }
    }
    return sqrt(col);
}

