// --- Buffer Register Definitions ---
#define REG_PQ ivec2(0, 0)
#define REG_MATRIX_AB ivec2(1, 0)
#define REG_MATRIX_CD ivec2(2, 0)
#define REG_MOUSE ivec2(3, 0)
#define REG_CAMERA ivec2(4, 0)
#define REG_ACTIVE_MATRIX_AB ivec2(5, 0)
#define REG_ACTIVE_MATRIX_CD ivec2(6, 0)
#define REG_OLD_ACTIVE_MATRIX_AB ivec2(7, 0)
#define REG_OLD_ACTIVE_MATRIX_CD ivec2(8, 0)
#define REG_ANIM_TIMER ivec2(9, 0)

// --- General Drawing & Math Functions ---
float sdTriangle(vec2 p) {
    p.y = -p.y;
    return max(abs(p.x) * 0.866025 + p.y * 0.5, -p.y);
}
float sdSegment(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}
bool insideBox(vec2 p, vec4 b) {
    vec2 q = abs(p - b.xy);
    return all(lessThan(q, b.zw));
}
float font_dist(sampler2D font_texture, vec2 tpos, float size, int char_code) {
    if (char_code < 32 || char_code > 127)
        return 1e5;
    float scl = 0.63 / size;
    vec2 uv = tpos * scl;
    ivec2 grid_pos = ivec2(char_code % 16, 15 - (char_code / 16));
    vec2 font_uv = (uv + vec2(grid_pos) + 0.5) * (1.0 / 16.0);
    float dist_from_tex = texture(font_texture, font_uv).a - 0.5;
    vec2 box = abs(uv) - 0.5;
    return max(dist_from_tex, max(box.x, box.y)) / scl;
}

#define MAX_UI_STRING_LEN 24
float get_string_dist(sampler2D font_texture, vec2 fragCoord, vec2 p,
                      float size, in int chars[MAX_UI_STRING_LEN], int len) {
    vec2 text_pos = fragCoord - p;
    float char_spacing = size * 0.8;
    float x_offset = text_pos.x + float(len - 1) * 0.5 * char_spacing;
    int char_idx = int(floor(x_offset / char_spacing + 0.5));
    if (char_idx < 0 || char_idx >= len)
        return 1e5;
    vec2 char_center_offset = vec2(float(char_idx) * char_spacing -
                                       float(len - 1) * 0.5 * char_spacing,
                                   0.0);
    return font_dist(font_texture, text_pos - char_center_offset, size,
                     chars[char_idx]);
}

float get_number_dist(sampler2D font_texture, vec2 fragCoord, vec2 p,
                      float size, float num) {
    vec2 text_pos = fragCoord - p;
    float char_spacing = size * 0.7;
    float num_abs = abs(num);
    bool is_neg = num < 0.0;
    int whole_part = int(floor(num_abs));
    int num_digits = (whole_part == 0)
                         ? 1
                         : int(floor(log2(float(whole_part)) / log2(10.0))) + 1;
    float total_width = float(num_digits) * char_spacing;
    if (is_neg)
        total_width += char_spacing;
    float x_offset = text_pos.x + total_width * 0.5;
    if (is_neg) {
        if (x_offset < char_spacing) {
            return font_dist(
                font_texture,
                text_pos - vec2(-total_width * 0.5 + char_spacing * 0.5, 0.0),
                size, 45);
        }
        x_offset -= char_spacing;
    }
    int digit_idx = int(floor(x_offset / char_spacing));
    if (digit_idx < 0 || digit_idx >= num_digits)
        return 1e5;
    float power_of_10 = pow(10.0, float(num_digits - 1 - digit_idx));
    int digit = int(mod(floor(float(whole_part) / power_of_10), 10.0));
    vec2 digit_center_offset =
        vec2((is_neg ? char_spacing : 0.0) - total_width * 0.5 +
                 (float(digit_idx) + 0.5) * char_spacing,
             0.0);
    return font_dist(font_texture, text_pos - digit_center_offset, size,
                     digit + 48);
}
vec3 draw_button(vec2 fragCoord, vec2 mouseCoord, vec2 center, float size,
                 float orientation) {
    vec2 p = fragCoord - center;
    mat2 rot = mat2(cos(orientation), -sin(orientation), sin(orientation),
                    cos(orientation));
    p = rot * p;
    float d = sdTriangle(p / size);
    vec3 col = UI_BUTTON_COLOR;
    if (length(center - mouseCoord) < size * 1.1) {
        col = UI_BUTTON_HOVER_COLOR;
    }
    float aa = fwidth(abs(d));
    return mix(vec3(0.0), col, 1.0 - smoothstep(0.15 - aa, 0.15 + aa, d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 fc = ivec2(fragCoord);
    fragColor = texelFetch(iChannel0, fc, 0);

    if (iFrame == 0) {
        if (fc == REG_PQ) fragColor = vec4(2.0, 5.0, 0.0, 0.0);
        if (fc == REG_MATRIX_AB) fragColor = vec4(1.0, 0.0, 0.0, 0.0);
        if (fc == REG_MATRIX_CD) fragColor = vec4(0.0, 1.0, 0.0, 0.0);
        if (fc == REG_MOUSE) fragColor = vec4(-1.0, -1.0, 0.0, 0.0);
        if (fc == REG_CAMERA) fragColor = vec4(0.0, 0.6, 0.0, 0.0);
        if (fc == REG_ACTIVE_MATRIX_AB) fragColor = vec4(1.0, 0.0, 0.0, 0.0);
        if (fc == REG_ACTIVE_MATRIX_CD) fragColor = vec4(0.0, 1.0, 0.0, 0.0);
        if (fc == REG_OLD_ACTIVE_MATRIX_AB) fragColor = vec4(1.0, 0.0, 0.0, 0.0);
        if (fc == REG_OLD_ACTIVE_MATRIX_CD) fragColor = vec4(0.0, 1.0, 0.0, 0.0);
        if (fc == REG_ANIM_TIMER) fragColor = vec4(2.0, 0.0, 0.0, 0.0);
        return;
    }

    vec2 pq = texelFetch(iChannel0, REG_PQ, 0).xy;
    vec2 display_ab = texelFetch(iChannel0, REG_MATRIX_AB, 0).xy;
    vec2 display_cd = texelFetch(iChannel0, REG_MATRIX_CD, 0).xy;
    vec2 active_ab = texelFetch(iChannel0, REG_ACTIVE_MATRIX_AB, 0).xy;
    vec2 active_cd = texelFetch(iChannel0, REG_ACTIVE_MATRIX_CD, 0).xy;
    vec2 old_active_ab = texelFetch(iChannel0, REG_OLD_ACTIVE_MATRIX_AB, 0).xy;
    vec2 old_active_cd = texelFetch(iChannel0, REG_OLD_ACTIVE_MATRIX_CD, 0).xy;

    vec4 anim_state = texelFetch(iChannel0, REG_ANIM_TIMER, 0);
    float anim_timer = anim_state.x;
    float show_cross = anim_state.y;

    vec4 prev_mouse = texelFetch(iChannel0, REG_MOUSE, 0);
    vec2 camera_rot = texelFetch(iChannel0, REG_CAMERA, 0).xy;

    bool mouse_clicked = iMouse.z > 0.0 && prev_mouse.z <= 0.0;

    if(anim_timer < 1.0) {
        anim_timer += 0.04;
    }

    if (iMouse.x < UI_WIDTH_PIXELS(iResolution.xy)) {
        if (mouse_clicked) {
            bool matrix_button_clicked = false;
            vec2 prospective_ab = display_ab;
            vec2 prospective_cd = display_cd;

            if      (insideBox(iMouse.xy, P_UP_BOX(iResolution.xy)))   { pq.x++; }
            else if (insideBox(iMouse.xy, P_DOWN_BOX(iResolution.xy))) { pq.x--; }
            else if (insideBox(iMouse.xy, Q_UP_BOX(iResolution.xy)))   { pq.y++; }
            else if (insideBox(iMouse.xy, Q_DOWN_BOX(iResolution.xy))) { pq.y--; }

            else if (insideBox(iMouse.xy, A_UP_BOX(iResolution.xy)))   { prospective_ab.x++; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, A_DOWN_BOX(iResolution.xy))) { prospective_ab.x--; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, B_UP_BOX(iResolution.xy)))   { prospective_ab.y++; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, B_DOWN_BOX(iResolution.xy))) { prospective_ab.y--; matrix_button_clicked = true; }

            else if (insideBox(iMouse.xy, C_UP_BOX(iResolution.xy)))   { prospective_cd.x++; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, C_DOWN_BOX(iResolution.xy))) { prospective_cd.x--; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, D_UP_BOX(iResolution.xy)))   { prospective_cd.y++; matrix_button_clicked = true; }
            else if (insideBox(iMouse.xy, D_DOWN_BOX(iResolution.xy))) { prospective_cd.y--; matrix_button_clicked = true; }

            if (matrix_button_clicked) {
                display_ab = prospective_ab;
                display_cd = prospective_cd;

                float det = display_ab.x * display_cd.y - display_ab.y * display_cd.x;
                if (abs(det - 1.0) < 0.0001) {
                    old_active_ab = active_ab;
                    old_active_cd = active_cd;
                    active_ab = display_ab;
                    active_cd = display_cd;
                    anim_timer = 0.0;
                    show_cross = 0.0;
                } else {
                    anim_timer = 2.0;
                    show_cross = 1.0;
                }
            }
        }
    } 
    else if (iMouse.z > 0.0) {
        vec2 delta = iMouse.xy - prev_mouse.xy;
        camera_rot.x -= delta.x * 0.005;
        camera_rot.y += delta.y * 0.005;
        camera_rot.y = clamp(camera_rot.y, -PI / 2.0 + 0.05, PI / 2.0 - 0.05);
    }

    if (fc == REG_PQ) fragColor = vec4(pq, 0, 0);
    if (fc == REG_MATRIX_AB) fragColor = vec4(display_ab, 0, 0);
    if (fc == REG_MATRIX_CD) fragColor = vec4(display_cd, 0, 0);
    if (fc == REG_MOUSE) fragColor = iMouse;
    if (fc == REG_CAMERA) fragColor = vec4(camera_rot, 0, 0);
    if (fc == REG_ACTIVE_MATRIX_AB) fragColor = vec4(active_ab, 0, 0);
    if (fc == REG_ACTIVE_MATRIX_CD) fragColor = vec4(active_cd, 0, 0);
    if (fc == REG_OLD_ACTIVE_MATRIX_AB) fragColor = vec4(old_active_ab, 0, 0);
    if (fc == REG_OLD_ACTIVE_MATRIX_CD) fragColor = vec4(old_active_cd, 0, 0);
    if (fc == REG_ANIM_TIMER) fragColor = vec4(anim_timer, show_cross, 0.0, 0.0);
}
